INSERT INTO TB_MS_PRODUCT_DLVR_PROD_NM_MULTI ( STORE_CD, SEQ, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM, REG_ID, REG_DT, MOD_ID, MOD_DT ) VALUES ( #{storeCd}, #{seq}, #{prodCd}, #{dlvrNameCd}, #{dlvrProdNm}, #{regId}, #{regDt}, #{modId}, #{modDt} )

/* USE : DlvrProdMultiMapper.copyStoreDlvrProdMultiNm */ INSERT INTO TB_MS_PRODUCT_DLVR_PROD_NM_MULTI ( SEQ, STORE_CD, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM, REG_DT, REG_ID, MOD_DT, MOD_ID, DLVR_MAPPING_FG )
SELECT SEQ, #{targetStoreCd} AS STORE_CD, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM, #{regDt}, #{regId}, #{modDt}, #{modId}, DLVR_MAPPING_FG FROM TB_HQ_PRODUCT_DLVR_PROD_NM_MULTI WHERE HQ_OFFICE_CD = #{originalStoreCd} AND DLVR_NAME_CD IN ( SELECT NMCODE_CD FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '112' AND NMCODE_CD != '1' )
SELECT SEQ, #{targetStoreCd} AS STORE_CD, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM, #{regDt}, #{regId}, #{modDt}, #{modId}, DLVR_MAPPING_FG FROM TB_MS_PRODUCT_DLVR_PROD_NM_MULTI WHERE STORE_CD = #{originalStoreCd} AND DLVR_NAME_CD IN ( SELECT NMCODE_CD FROM TB_CM_NMCODE WHERE NMCODE_GRP_CD = '112' AND NMCODE_CD != '1' )

mapper.select:
/* DlvrProdMapper.getDlvrProdMultiNmStoreRegistList */ SELECT tms.HQ_OFFICE_CD, tms.STORE_CD, tms.STORE_NM, tms.SYS_STAT_FG FROM TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.STORE_CD ASC

