INSERT INTO TB_HQ_PRODUCT_DLVR_PROD_NM_MULTI ( HQ_OFFICE_CD, SEQ, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM, REG_DT, REG_ID, MOD_DT, MOD_ID ) SELECT #{hqOfficeCd}, ( SELECT NVL(MAX(SEQ),0)+1 FROM TB_HQ_PRODUCT_DLVR_PROD_NM_MULTI WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd} AND DLVR_NAME_CD = #{dlvrNameCd} ), #{prodCd}, #{dlvrNameCd}, #{dlvrProdNm}, #{regDt}, #{regId}, #{modDt}, #{modId} FROM DUAL WHERE NOT EXISTS ( SELECT HQ_OFFICE_CD, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM FROM TB_HQ_PRODUCT_DLVR_PROD_NM_MULTI WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND DLVR_NAME_CD = #{dlvrNameCd} AND DLVR_PROD_NM = #{dlvrProdNm} )
INSERT INTO TB_MS_PRODUCT_DLVR_PROD_NM_MULTI ( STORE_CD, SEQ, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM, REG_DT, REG_ID, MOD_DT, MOD_ID ) SELECT #{storeCd}, ( SELECT NVL(MAX(SEQ),0)+1 FROM TB_MS_PRODUCT_DLVR_PROD_NM_MULTI WHERE STORE_CD = #{storeCd} AND PROD_CD = #{prodCd} AND DLVR_NAME_CD = #{dlvrNameCd} ), #{prodCd}, #{dlvrNameCd}, #{dlvrProdNm}, #{regDt}, #{regId}, #{modDt}, #{modId} FROM DUAL WHERE NOT EXISTS ( SELECT STORE_CD, PROD_CD, DLVR_NAME_CD, DLVR_PROD_NM FROM TB_MS_PRODUCT_DLVR_PROD_NM_MULTI WHERE STORE_CD = #{storeCd} AND DLVR_NAME_CD = #{dlvrNameCd} AND DLVR_PROD_NM = #{dlvrProdNm} )
