mapper.select:
/* USE : KioskDisplayResveMapper.getKioskDisplayResve */
SELECT a.*
FROM ( SELECT tmkdr.STORE_CD , tms.STORE_NM , tmkdr.PROD_CD , tmp.PROD_NM , TO_CHAR(TO_DATE(tmkdr.START_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS START_DATE , TO_CHAR(TO_DATE(tmkdr.START_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS ORG_START_DATE , tmkdr.START_TIME , tmkdr.START_TIME AS ORG_START_TIME , NVL(tmp.KIOSK_DISPLAY_YN, 'Y') AS NOW_KIOSK_DISPLAY_YN , tmkdr.KIOSK_DISPLAY_YN AS ORG_KIOSK_DISPLAY_YN , tmkdr.KIOSK_DISPLAY_YN , tmkdr.RESVE_FG , NVL2(( SELECT DISTINCT PROD_CD FROM TB_MS_KIOSK_KEY tmkk2 WHERE tmkk2.STORE_CD = tmp.STORE_CD AND tmkk2.PROD_CD = tmp.PROD_CD AND TU_CLS_TYPE IN (SELECT ENVST_VAL FROM TB_MS_POS_ENVST tmpe WHERE tmpe.STORE_CD = tmp.STORE_CD AND tmpe.ENVST_CD IN ('4068') AND STORE_CD || POS_NO IN (SELECT STORE_CD || POS_NO FROM TB_MS_POS_ENVST WHERE ENVST_CD = '4019' AND ENVST_VAL = '6' AND STORE_CD = tmp.STORE_CD))), 'Y', 'N') AS USE_YN_4068 , NVL2(( SELECT DISTINCT PROD_CD FROM TB_MS_KIOSK_KEY tmkk2 WHERE tmkk2.STORE_CD = tmp.STORE_CD AND tmkk2.PROD_CD = tmp.PROD_CD AND TU_CLS_TYPE IN (SELECT ENVST_VAL FROM TB_MS_POS_ENVST tmpe WHERE tmpe.STORE_CD = tmp.STORE_CD AND tmpe.ENVST_CD IN ('4069') AND STORE_CD || POS_NO IN (SELECT STORE_CD || POS_NO FROM TB_MS_POS_ENVST WHERE ENVST_CD = '4019' AND ENVST_VAL = '6' AND STORE_CD = tmp.STORE_CD))), 'Y', 'N') AS USE_YN_4069 FROM TB_MS_KIOSK_DISPLAY_RESVE tmkdr , TB_MS_PRODUCT tmp , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tmkdr.STORE_CD = tmp.STORE_CD AND tmkdr.PROD_CD = tmp.PROD_CD AND tmkdr.STORE_CD = tms.STORE_CD AND tmkdr.STORE_CD = tmsi.STORE_CD (+) AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tmkdr.STORE_CD = #{storeCds}
AND tmkdr.PROD_CD = #{prodCds}
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND tmkdr.START_DATE BETWEEN #{startDate} AND #{endDate}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tmp.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmkdr.KIOSK_DISPLAY_YN = #{kioskDisplayYn}
AND tmp.USE_YN = #{useYn}
) a WHERE 1=1
AND (USE_YN_4068 = 'Y' OR USE_YN_4069 = 'Y')
AND USE_YN_4068 = 'N' AND USE_YN_4069 = 'N'
ORDER BY STORE_CD, PROD_CD, START_DATE, START_TIME

/* USE : KioskDisplayResveMapper.getProdList */ SELECT * FROM ( SELECT tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tmp.STORE_CD , tms.STORE_NM , tmp.PROD_CD , tmp.PROD_NM , NVL(tmp.KIOSK_DISPLAY_YN, 'Y') AS ORG_KIOSK_DISPLAY_YN , NVL2(( SELECT DISTINCT PROD_CD FROM TB_MS_KIOSK_KEY tmkk2 WHERE tmkk2.STORE_CD = tmp.STORE_CD AND tmkk2.PROD_CD = tmp.PROD_CD AND TU_CLS_TYPE IN (SELECT ENVST_VAL FROM TB_MS_POS_ENVST tmpe WHERE tmpe.STORE_CD = tmp.STORE_CD AND tmpe.ENVST_CD IN ('4068') AND STORE_CD || POS_NO IN (SELECT STORE_CD || POS_NO FROM TB_MS_POS_ENVST WHERE ENVST_CD = '4019' AND ENVST_VAL = '6' AND STORE_CD = tmp.STORE_CD))), 'Y', 'N') AS USE_YN_4068 , NVL2(( SELECT DISTINCT PROD_CD FROM TB_MS_KIOSK_KEY tmkk2 WHERE tmkk2.STORE_CD = tmp.STORE_CD AND tmkk2.PROD_CD = tmp.PROD_CD AND TU_CLS_TYPE IN (SELECT ENVST_VAL FROM TB_MS_POS_ENVST tmpe WHERE tmpe.STORE_CD = tmp.STORE_CD AND tmpe.ENVST_CD IN ('4069') AND STORE_CD || POS_NO IN (SELECT STORE_CD || POS_NO FROM TB_MS_POS_ENVST WHERE ENVST_CD = '4019' AND ENVST_VAL = '6' AND STORE_CD = tmp.STORE_CD))), 'Y', 'N') AS USE_YN_4069 FROM TB_MS_PRODUCT tmp , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tmp.STORE_CD = tms.STORE_CD AND tms.STORE_CD = tmsi.STORE_CD (+) AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tms.STORE_CD = #{storeCds}
AND tmp.PROD_CD = #{prodCds}
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
) WHERE 1=1
AND (USE_YN_4068 = 'Y' OR USE_YN_4069 = 'Y')
AND USE_YN_4068 = 'N' AND USE_YN_4069 = 'N'
ORDER BY STORE_CD, PROD_CD

/* USE : KioskDisplayResveMapper.getKioskDisplayResveCnt */
    SELECT  COUNT(*)
    FROM    TB_MS_KIOSK_DISPLAY_RESVE
    WHERE   STORE_CD    = #{storeCd}
    AND     PROD_CD     = #{prodCd}
    AND     START_DATE  = #{startDate}
    AND     START_TIME  = #{startTime}
    AND     RESVE_FG   != '0'

mapper.insert:
/* USE : KioskDisplayResveMapper.saveKioskDisplayResve */
MERGE INTO TB_MS_KIOSK_DISPLAY_RESVE A
USING DUAL
ON
(
    A.STORE_CD      = #{storeCd}
AND A.PROD_CD       = #{prodCd}
AND A.START_DATE    = #{startDate}
AND A.START_TIME    = #{startTime}
)
WHEN MATCHED THEN
UPDATE
SET
    A.KIOSK_DISPLAY_YN  = #{kioskDisplayYn}
,   A.MOD_DT            = #{modDt}
,   A.MOD_ID            = #{modId}
WHEN NOT MATCHED THEN
INSERT  (
        STORE_CD
    ,   PROD_CD
    ,   START_DATE
    ,   START_TIME
    ,   KIOSK_DISPLAY_YN
    ,   RESVE_FG
    ,   REG_DT
    ,   REG_ID
    ,   MOD_DT
    ,   MOD_ID
) VALUES (
        #{storeCd}
    ,   #{prodCd}
    ,   #{startDate}
    ,   #{startTime}
    ,   #{kioskDisplayYn}
    ,   '0'
    ,   #{regDt}
    ,   #{regId}
    ,   #{modDt}
    ,   #{modId}
)

mapper.delete:
/* USE : KioskDisplayResveMapper.deleteKioskDisplayResve */
DELETE
FROM    TB_MS_KIOSK_DISPLAY_RESVE
WHERE   STORE_CD = #{storeCd}
AND     PROD_CD = #{prodCd}
AND     START_DATE = #{orgStartDate}
AND     START_TIME = #{orgStartTime}
AND     KIOSK_DISPLAY_YN = #{orgKioskDisplayYn}
AND     RESVE_FG = '0'
