mapper.select:
/* USE : KioskKeyMapResveMapper.getKioskKeyMapResveList */
SELECT tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tmtkgr.STORE_CD , tms.STORE_NM , tmtkgr.POS_NO , tmtkgr.ENVST_CD , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP , tmtkgr.START_DATE AS ORG_START_DATE , TO_CHAR(TO_DATE(tmtkgr.START_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS START_DATE , tmtkgr.TU_CLS_TYPE AS ORG_TU_CLS_TYPE , tmtkgr.TU_CLS_TYPE AS TU_CLS_TYPE , ( SELECT TO_CHAR(TO_DATE(MOD_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') FROM TB_MS_POS_ENVST WHERE STORE_CD = tmtkgr.STORE_CD AND POS_NO = tmtkgr.POS_NO AND ENVST_CD = tmtkgr.ENVST_CD) AS MOD_DT , ( SELECT ENVST_VAL FROM TB_MS_POS_ENVST WHERE STORE_CD = tmtkgr.STORE_CD AND POS_NO = tmtkgr.POS_NO AND ENVST_CD = tmtkgr.ENVST_CD) AS NOW_TU_CLS_TYPE , tmtkgr.RESVE_FG
FROM TB_MS_KIOSK_GROUP_RESVE tmtkgr , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmtkgr.STORE_CD = tms.STORE_CD AND tmtkgr.STORE_CD = tmsi.STORE_CD(+)
AND tmtkgr.ENVST_CD LIKE '%'||#{envstCd}||'%'
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tmtkgr.START_DATE BETWEEN #{startDate} AND #{endDate}
AND tmtkgr.TU_CLS_TYPE = #{tuClsType}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmtkgr.POS_NO = #{posNo}
ORDER BY tmtkgr.STORE_CD, tmtkgr.POS_NO, tmtkgr.ENVST_CD, tmtkgr.START_DATE

/* USE : KioskKeyMapResveMapper.getKioskKeyMapResveAddList */
SELECT	tms.BRANCH_CD
,		(SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM
,		tms.STORE_CD
,		tms.STORE_NM
,		tmpe.POS_NO
,		tms.HQ_BRAND_CD
,		tmsi.MOMS_TEAM
,		tmsi.MOMS_AC_SHOP
,		tmpe.ENVST_VAL AS ORG_TU_CLS_TYPE
,		tmpe.ENVST_CD AS ENVST_CD
,       TO_CHAR(TO_DATE(tmpe.MOD_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS MOD_DT
FROM 	TB_MS_STORE tms
,		TB_MS_STORE_INFO tmsi
,		TB_MS_POS_ENVST tmpe
,		TB_MS_POS_ENVST tmpe2
WHERE 	tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND 	tms.STORE_CD = tmsi.STORE_CD(+)
AND 	tms.STORE_CD = tmpe.STORE_CD(+)
AND 	#{envstCd} = tmpe.ENVST_CD(+)
AND 	tms.STORE_CD = tmpe2.STORE_CD(+)
AND 	tmpe.POS_NO = tmpe2.POS_NO
AND 	tmpe2.ENVST_CD = '4019'
AND 	tmpe2.ENVST_VAL = '6'

    AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'


    AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'


    AND tmpe.ENVST_VAL = #{tuClsType}


    AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



        -- 매장브랜드 전체일때
        AND tms.HQ_BRAND_CD IN

            #{item}




    AND tmsi.MOMS_TEAM = #{momsTeam}


    AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


    AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


    AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


    AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


    AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND tms.BRANCH_CD = #{branchCd}


    AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}


    AND tmpe.POS_NO = #{posNo}

ORDER
BY      tms.STORE_CD, tmpe.POS_NO

/* USE : KioskKeyMapResveMapper.getKioskDisplayResveCnt */
SELECT  COUNT(*)
FROM    TB_MS_KIOSK_GROUP_RESVE
WHERE   STORE_CD    = #{storeCd}
AND     POS_NO = #{posNo}
AND     START_DATE  = #{startDate}
AND     ENVST_CD = #{envstCd}
AND     RESVE_FG   != '0'

mapper.insert:
/* USE : KioskKeyMapResveMapper.saveKioskKeyMapResve */
MERGE INTO TB_MS_KIOSK_GROUP_RESVE A
USING DUAL
ON
(
    A.STORE_CD = #{storeCd}
AND A.POS_NO = #{posNo}
AND A.START_DATE = #{startDate}
AND A.ENVST_CD = #{envstCd}
)
WHEN MATCHED THEN
UPDATE
SET
    A.TU_CLS_TYPE = #{tuClsType},
    A.MOD_DT = #{modDt},
    A.MOD_ID = #{modId}
WHEN NOT MATCHED THEN
INSERT  (
        STORE_CD
    ,   POS_NO
    ,   START_DATE
    ,   ENVST_CD
    ,   TU_CLS_TYPE
    ,   RESVE_FG
    ,   REG_DT
    ,   REG_ID
    ,   MOD_DT
    ,   MOD_ID
) VALUES (
        #{storeCd}
    ,   #{posNo}
    ,   #{startDate}
    ,   #{envstCd}
    ,   #{tuClsType}
    ,   '0'
    ,   #{regDt}
    ,   #{regId}
    ,   #{modDt}
    ,   #{modId}
)

mapper.delete:
/* USE : KioskKeyMapResveMapper.deleteKioskKeyMapResve */
DELETE
FROM    TB_MS_KIOSK_GROUP_RESVE
WHERE   STORE_CD = #{storeCd}
AND     POS_NO = #{posNo}
AND     START_DATE = #{orgStartDate}
AND     ENVST_CD = #{envstCd}
AND     TU_CLS_TYPE = #{orgTuClsType}
AND     RESVE_FG = '0'
