mapper.select:
/* KioskOptionMapper.getKioskOptionList */
SELECT thp.PROD_CD, thp.PROD_NM, NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC, thpb.BAR_CD FROM TB_HQ_PRODUCT thp LEFT OUTER JOIN ( SELECT HQ_OFFICE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1=1 AND HQ_OFFICE_CD = #{membrOrgnCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = #{salePrcFg} GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp ON thp.HQ_OFFICE_CD = thpsp.HQ_OFFICE_CD AND thp.PROD_CD = thpsp.PROD_CD , ( SELECT HQ_OFFICE_CD, PROD_CD, BARCD_CD AS BAR_CD FROM TB_HQ_PRODUCT_BARCD WHERE 1=1 ) thpb WHERE 1=1 AND thpb.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpb.PROD_CD = thp.PROD_CD AND thp.HQ_OFFICE_CD = #{membrOrgnCd}
AND thp.REG_DT BETWEEN #{startDate} AND #{endDate}
AND thp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND thp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND thpb.BAR_CD LIKE '%'|| #{barCd} ||'%'
AND thp.USE_YN = #{useYn}
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE 1=1 AND HQ_OFFICE_CD = #{membrOrgnCd} START WITH PROD_CLASS_CD = #{prodClassCd} CONNECT BY P_PROD_CLASS_CD = PRIOR PROD_CLASS_CD )
ORDER BY thp.PROD_CD
SELECT tmp.PROD_CD, tmp.PROD_NM, NVL(tmpsp.SALE_UPRC, 0) AS SALE_UPRC, tmpb.BAR_CD FROM TB_MS_PRODUCT tmp LEFT OUTER JOIN ( SELECT STORE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE 1=1 AND STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = #{salePrcFg} GROUP BY STORE_CD, PROD_CD ) tmpsp ON tmp.STORE_CD = tmpsp.STORE_CD AND tmp.PROD_CD = tmpsp.PROD_CD , ( SELECT STORE_CD, PROD_CD, BARCD_CD AS BAR_CD FROM TB_MS_PRODUCT_BARCD WHERE 1=1 ) tmpb WHERE 1=1 AND tmpb.STORE_CD = tmp.STORE_CD AND tmpb.PROD_CD = tmp.PROD_CD AND tmp.STORE_CD = #{storeCd}
AND tmp.REG_DT BETWEEN #{startDate} AND #{endDate}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmpb.BAR_CD LIKE '%'|| #{barCd} ||'%'
AND tmp.USE_YN = #{useYn}
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE 1=1 AND STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} CONNECT BY P_PROD_CLASS_CD = PRIOR PROD_CLASS_CD )
ORDER BY tmp.PROD_CD

/* KioskOptionMapper.getKioskOptionDetailList */
SELECT thpo.PROD_CD, thpo.OPTION_PROD_CD, (SELECT PROD_NM FROM TB_HQ_PRODUCT WHERE HQ_OFFICE_CD = #{membrOrgnCd} AND PROD_CD = thpo.OPTION_PROD_CD) AS OPTION_PROD_NM, NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC, thpo.OPTION_FG FROM TB_HQ_PRODUCT_OPTION thpo LEFT OUTER JOIN ( SELECT HQ_OFFICE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE 1=1 AND HQ_OFFICE_CD = #{membrOrgnCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = #{salePrcFg} GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp ON thpo.HQ_OFFICE_CD = thpsp.HQ_OFFICE_CD AND thpo.PROD_CD = thpsp.PROD_CD WHERE 1=1 AND thpo.HQ_OFFICE_CD = #{membrOrgnCd} AND thpo.PROD_CD = #{prodCd} ORDER BY thpo.OPTION_FG, thpo.DISP_SEQ
SELECT tmpo.PROD_CD, tmpo.OPTION_PROD_CD, (SELECT PROD_NM FROM TB_MS_PRODUCT WHERE STORE_CD = #{storeCd} AND PROD_CD = tmpo.OPTION_PROD_CD) AS OPTION_PROD_NM, NVL(tmpsp.SALE_UPRC, 0) AS SALE_UPRC, tmpo.OPTION_FG FROM TB_MS_PRODUCT_OPTION tmpo LEFT OUTER JOIN ( SELECT STORE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE 1=1 AND STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = #{salePrcFg} GROUP BY STORE_CD, PROD_CD ) tmpsp ON tmpo.STORE_CD = tmpsp.STORE_CD AND tmpo.PROD_CD = tmpsp.PROD_CD WHERE 1=1 AND tmpo.STORE_CD = #{storeCd} AND tmpo.PROD_CD = #{prodCd} ORDER BY tmpo.OPTION_FG, tmpo.DISP_SEQ

mapper.delete:
/* KioskOptionMapper.getKioskOptionSaveDelete */



        DELETE
        FROM TB_HQ_PRODUCT_OPTION
        WHERE 1=1
        AND HQ_OFFICE_CD = #{membrOrgnCd}
        AND PROD_CD = #{prodCd}
        AND OPTION_PROD_CD = #{optionProdCd}



        DELETE
        FROM TB_MS_PRODUCT_OPTION
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}
        AND OPTION_PROD_CD = #{optionProdCd}

mapper.update:
/* KioskOptionMapper.getKioskOptionSaveUpdate */



        UPDATE
        TB_HQ_PRODUCT_OPTION
        SET
        DISP_SEQ = #{dispSeq},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND HQ_OFFICE_CD = #{membrOrgnCd}
        AND PROD_CD = #{prodCd}
        AND OPTION_PROD_CD = #{optionProdCd}



        UPDATE
        TB_MS_PRODUCT_OPTION
        SET
        DISP_SEQ = #{dispSeq},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}
        AND OPTION_PROD_CD = #{optionProdCd}

mapper.select:
/* KioskOptionMapper.getKioskOptionProdDispSeq */



        SELECT
        (NVL(MAX(thpo.DISP_SEQ), 0) + 1) AS DISP_SEQ
        FROM TB_HQ_PRODUCT_OPTION thpo
        WHERE 1=1
        AND thpo.HQ_OFFICE_CD = #{membrOrgnCd}
        AND thpo.PROD_CD = #{prodCd}



        SELECT
        (NVL(MAX(tmpo.DISP_SEQ), 0) + 1) AS DISP_SEQ
        FROM TB_MS_PRODUCT_OPTION tmpo
        WHERE 1=1
        AND tmpo.STORE_CD = #{storeCd}
        AND tmpo.PROD_CD = #{prodCd}

mapper.insert:
/* KioskOptionMapper.getKioskOptionProdSave */
INSERT INTO TB_HQ_PRODUCT_OPTION ( HQ_OFFICE_CD, PROD_CD, OPTION_PROD_CD, OPTION_FG, DISP_SEQ, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{membrOrgnCd}, #{prodCd}, #{optionProdCd}, #{optionFg}, #{dispSeq}, '', #{regDt}, #{regId}, #{modDt}, #{modId} )
INSERT INTO TB_MS_PRODUCT_OPTION ( STORE_CD, PROD_CD, OPTION_PROD_CD, OPTION_FG, DISP_SEQ, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{prodCd}, #{optionProdCd}, #{optionFg}, #{dispSeq}, '', #{regDt}, #{regId}, #{modDt}, #{modId} )
