mapper.select:
/* USE : ProdMapper.getProdDetail */
SELECT thp.HQ_OFFICE_CD, -- 본사코드 thp.PROD_CD, -- 상품코드 thp.PROD_NM, -- 상품명 thp.PROD_CLASS_CD, -- 상품분류코드 ( SELECT SF_GET_PROD_CLASSES('H', thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_CD_NM, thp.CORNR_CD, -- 코너코드 thp.ORGPLCE_CD, -- 원산지 구분 -- TODO 원산지명 조회 필요 thp.SDATTR_CLASS_CD, -- 사이드속성분류코드 thp.SDSEL_GRP_CD, -- 사이드선택그룹코드 thp.POINT_SAVE_YN, thp.PROD_DC_FG, thp.VAT_FG, -- 과세여부 thp.PROD_TIP_YN, thp.PROD_PACK_AMT, thp.PROD_DLVR_AMT, thp.PROD_TYPE_FG, -- 상품유형구분 thp.SALE_PROD_YN, -- 판매상품여부 thp.STOCK_PROD_YN, -- 재고관리여부 thp.SIDE_PROD_YN, thp.SET_PROD_FG, -- 세트상품구분 NVL(thpsp.SALE_UPRC, 0) AS SALE_URC, -- 최초판매단가 NVL(thp.COST_UPRC, 0) AS COST_UPRC, -- 원가단가 NVL(thp.LAST_COST_UPRC, 0) AS LAST_COST_UPRC, -- 최종원가단가 NVL(thp.SPLY_UPRC, 0) AS SPLY_UPRC, -- 판매단가 thp.SPLY_UPRC_USE_YN, thp.PO_PROD_FG, -- 주문상품구분 thp.PO_UNIT_FG, -- 주문단위 thp.PO_UNIT_QTY, thp.PO_UNIT_ALLOW_FG, thp.PO_MIN_QTY, -- 최소주문 thp.SAFE_STOCK_QTY, -- 안전재고 thp.STOCK_UNIT_FG, thp.USE_YN, -- 사용여부 thp.REMARK, -- 비고 thp.REG_DT FROM TB_HQ_PRODUCT thp LEFT OUTER JOIN ( SELECT PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' -- 본사, 매장 구분이 필요한지 확인. 무시면 조건 제거 GROUP BY PROD_CD ) thpsp ON thp.PROD_CD = thpsp.PROD_CD WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = #{prodCd}
SELECT tmp.STORE_CD, -- 매장코드 tmp.PROD_CD, -- 상품코드 tmp.PROD_NM, -- 상품명 tmp.PROD_CLASS_CD, -- 상품분류코드 ( SELECT SF_GET_PROD_CLASSES('S', tmp.STORE_CD, tmp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_CD_NM, tmp.CORNR_CD, -- 코너코드 tmp.ORGPLCE_CD, -- 원산지 구분 -- TODO 원산지명 조회 필요 tmp.SDATTR_CLASS_CD, tmp.SDSEL_GRP_CD, tmp.POINT_SAVE_YN, tmp.PROD_DC_FG, tmp.VAT_FG, -- 과세여부 tmp.PROD_TIP_YN, tmp.PROD_PACK_AMT, tmp.PROD_DLVR_AMT, tmp.PROD_TYPE_FG, -- 상품유형 tmp.SALE_PROD_YN, -- 판매상품여부 tmp.STOCK_PROD_YN, -- 재고관리여부 tmp.SIDE_PROD_YN, tmp.SET_PROD_FG, -- 세트상품구분 NVL(tmpsp.SALE_UPRC, 0) AS SALE_URC, -- 최초판매단가 NVL(tmp.COST_UPRC, 0) AS COST_UPRC, -- 원가단가 NVL(tmp.LAST_COST_UPRC, 0) AS LAST_COST_UPRC, -- 최종원가단가 NVL(tmp.SPLY_UPRC, 0) AS SPLY_UPRC, -- 판매단가 tmp.SPLY_UPRC_USE_YN, tmp.PO_PROD_FG, -- 주문상품구분 tmp.PO_UNIT_FG, -- 주문단위 tmp.PO_UNIT_QTY, tmp.PO_UNIT_ALLOW_FG, tmp.PO_MIN_QTY, -- 최소주문 tmp.SAFE_STOCK_QTY, -- 안전재고 tmp.STOCK_UNIT_FG, tmp.USE_YN, -- 사용여부 tmp.REMARK, -- 비고 tmp.REG_DT FROM TB_MS_PRODUCT tmp LEFT OUTER JOIN ( SELECT PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '2' -- 본사, 매장 구분이 필요한지 확인. 무시면 조건 제거 GROUP BY PROD_CD ) tmpsp ON tmp.PROD_CD = tmpsp.PROD_CD WHERE tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD = #{prodCd}

/* USE : ProdMapper.getLinkedProdList */
SELECT thpup.HQ_OFFICE_CD, thpup.PROD_CD, thpup.UNIT_PROD_CD, thpup.SET_PROD_FG, thpup.UNIT_PROD_QTY, thpup.DISP_SEQ FROM TB_HQ_PRODUCT_UNITST_PROD thpup WHERE thpup.HQ_OFFICE_CD = #{hqOfficeCd} AND thpup.PROD_CD = #{prodCd} ORDER BY thpup.DISP_SEQ
SELECT tmpup.STORE_CD, tmpup.PROD_CD, tmpup.UNIT_PROD_CD, tmpup.SET_PROD_FG, tmpup.UNIT_PROD_QTY, tmpup.DISP_SEQ FROM TB_MS_PRODUCT_UNITST_PROD tmpup WHERE tmpup.STORE_CD = #{storeCd} AND tmpup.PROD_CD = #{prodCd} ORDER BY tmpup.DISP_SEQ

mapper.update:
/* ProdMapper.saveProductInfo */
MERGE INTO TB_HQ_PRODUCT thp USING DUAL ON ( thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = #{prodCd} ) WHEN MATCHED THEN UPDATE SET PROD_NM = #{prodNm}, PROD_CLASS_CD = #{prodClassCd}, CORNR_CD = #{cornrCd}, ORGPLCE_CD = #{orgplceCd}, SDATTR_CLASS_CD = #{sdattrClassCd}, SDSEL_GRP_CD = #{sdselGrpCd}, POINT_SAVE_YN = #{pointSaveYn, jdbcType=VARCHAR}, PROD_DC_FG = #{prodDcFg}, VAT_FG = #{vatFg}, PROD_TIP_YN = #{prodTipYn, jdbcType=VARCHAR}, PROD_PACK_AMT = #{prodPackAmt, jdbcType=NUMERIC}, PROD_DLVR_AMT = #{prodDlvrAmt, jdbcType=NUMERIC}, PROD_TYPE_FG = #{prodTypeFg}, SALE_PROD_YN = #{saleProdYn}, STOCK_PROD_YN = #{stockProdYn}, SIDE_PROD_YN = #{sideProdYn, jdbcType=VARCHAR}, SET_PROD_FG = #{setProdFg}, COST_UPRC = #{costUprc, jdbcType=NUMERIC}, LAST_COST_UPRC = #{lastCostUprc, jdbcType=NUMERIC}, SPLY_UPRC = #{splyUprc, jdbcType=NUMERIC}, SPLY_UPRC_USE_YN = #{splyUprcUseYn, jdbcType=VARCHAR}, PO_PROD_FG = #{poProdFg}, PO_UNIT_FG = #{poUnitFg}, PO_UNIT_QTY = #{poUnitQty, jdbcType=NUMERIC}, PO_UNIT_ALLOW_FG = #{poUnitAllowFg}, PO_MIN_QTY = #{poMinQty, jdbcType=NUMERIC}, SAFE_STOCK_QTY = #{safeStockQty, jdbcType=NUMERIC}, STOCK_UNIT_FG = #{stockUnitFg}, USE_YN = #{useYn}, REMARK = #{remark}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( HQ_OFFICE_CD, PROD_CD, PROD_NM, PROD_CLASS_CD, CORNR_CD, ORGPLCE_CD, SDATTR_CLASS_CD, SDSEL_GRP_CD, POINT_SAVE_YN, PROD_DC_FG, VAT_FG, PROD_TIP_YN, PROD_PACK_AMT, PROD_DLVR_AMT, PROD_TYPE_FG, SALE_PROD_YN, STOCK_PROD_YN, SIDE_PROD_YN, SET_PROD_FG, COST_UPRC, LAST_COST_UPRC, SPLY_UPRC, SPLY_UPRC_USE_YN, PO_PROD_FG, PO_UNIT_FG, PO_UNIT_QTY, PO_UNIT_ALLOW_FG, PO_MIN_QTY, SAFE_STOCK_QTY, STOCK_UNIT_FG, USE_YN, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, ( SELECT LPAD(NVL(MAX(TO_NUMBER(PROD_CD)),0) + 1, 13, '0') FROM TB_HQ_PRODUCT WHERE HQ_OFFICE_CD = #{hqOfficeCd} ), #{prodNm}, #{prodClassCd}, '00', #{orgplceCd}, #{sdattrClassCd}, #{sdselGrpCd}, #{pointSaveYn, jdbcType=VARCHAR}, #{prodDcFg}, #{vatFg}, #{prodTipYn, jdbcType=VARCHAR}, #{prodPackAmt, jdbcType=NUMERIC}, #{prodDlvrAmt, jdbcType=NUMERIC}, #{prodTypeFg}, #{saleProdYn}, #{stockProdYn}, #{sideProdYn, jdbcType=VARCHAR}, #{setProdFg}, #{costUprc, jdbcType=NUMERIC}, #{lastCostUprc, jdbcType=NUMERIC}, #{splyUprc, jdbcType=NUMERIC}, #{splyUprcUseYn, jdbcType=VARCHAR}, #{poProdFg}, #{poUnitFg}, #{poUnitQty, jdbcType=NUMERIC}, #{poUnitAllowFg}, #{poMinQty, jdbcType=NUMERIC}, #{safeStockQty, jdbcType=NUMERIC}, #{stockUnitFg}, #{useYn}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId} )
MERGE INTO TB_MS_PRODUCT tmp USING DUAL ON ( tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD = #{prodCd} ) WHEN MATCHED THEN UPDATE SET PROD_NM = #{prodNm}, PROD_CLASS_CD = #{prodClassCd}, CORNR_CD = #{cornrCd}, ORGPLCE_CD = #{orgplceCd}, SDATTR_CLASS_CD = #{sdattrClassCd}, SDSEL_GRP_CD = #{sdselGrpCd}, POINT_SAVE_YN = #{pointSaveYn}, PROD_DC_FG = #{prodDcFg}, VAT_FG = #{vatFg}, PROD_TIP_YN = #{prodTipYn}, PROD_PACK_AMT = #{prodPackAmt, jdbcType=NUMERIC}, PROD_DLVR_AMT = #{prodDlvrAmt, jdbcType=NUMERIC}, PROD_TYPE_FG = #{prodTypeFg}, SALE_PROD_YN = #{saleProdYn}, STOCK_PROD_YN = #{stockProdYn}, SIDE_PROD_YN = #{sideProdYn}, SET_PROD_FG = #{setProdFg}, COST_UPRC = #{costUprc}, LAST_COST_UPRC = #{lastCostUprc}, SPLY_UPRC = #{splyUprc}, SPLY_UPRC_USE_YN = #{splyUprcUseYn}, PO_PROD_FG = #{poProdFg}, PO_UNIT_FG = #{poUnitFg}, PO_UNIT_QTY = #{poUnitQty}, PO_UNIT_ALLOW_FG = #{poUnitAllowFg}, PO_MIN_QTY = #{poMinQty}, SAFE_STOCK_QTY = #{safeStockQty}, STOCK_UNIT_FG = #{stockUnitFg}, USE_YN = #{useYn}, REMARK = #{remark}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( STORE_CD, PROD_CD, PROD_NM, PROD_CLASS_CD, CORNR_CD, ORGPLCE_CD, SDATTR_CLASS_CD, SDSEL_GRP_CD, POINT_SAVE_YN, PROD_DC_FG, VAT_FG, PROD_TIP_YN, PROD_PACK_AMT, PROD_DLVR_AMT, PROD_TYPE_FG, SALE_PROD_YN, STOCK_PROD_YN, SIDE_PROD_YN, SET_PROD_FG, COST_UPRC, LAST_COST_UPRC, SPLY_UPRC, SPLY_UPRC_USE_YN, PO_PROD_FG, PO_UNIT_FG, PO_UNIT_QTY, PO_UNIT_ALLOW_FG, PO_MIN_QTY, SAFE_STOCK_QTY, STOCK_UNIT_FG, USE_YN, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, ( SELECT LPAD(NVL(MAX(TO_NUMBER(PROD_CD)),0) + 1, 13, '0') FROM TB_MS_PRODUCT WHERE STORE_CD = #{storeCd} ), #{prodNm}, #{prodClassCd}, '00', #{orgplceCd}, #{sdattrClassCd}, #{sdselGrpCd}, 'N', #{prodDcFg}, #{vatFg}, #{prodTipYn}, #{prodPackAmt, jdbcType=NUMERIC}, #{prodDlvrAmt, jdbcType=NUMERIC}, #{prodTypeFg}, #{saleProdYn}, #{stockProdYn}, 'N', #{setProdFg}, #{costUprc}, #{lastCostUprc}, #{splyUprc}, #{splyUprcUseYn}, #{poProdFg}, #{poUnitFg}, #{poUnitQty}, #{poUnitAllowFg}, #{poMinQty}, #{safeStockQty}, #{stockUnitFg}, #{useYn}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId} )
