mapper.select:
/* USE : ProdMapper.getProdDetail */
SELECT thp.HQ_OFFICE_CD, -- 본사코드 thp.PROD_CD, -- 상품코드 thp.PROD_NM, -- 상품명 thp.PROD_CLASS_CD, -- 상품분류코드 ( SELECT SF_GET_PROD_CLASSES('H', thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_NM, NVL(thp.COST_UPRC, 0) AS COST_UPRC, -- 원가단가 NVL(thp.LAST_COST_UPRC, 0) AS LAST_COST_UPRC, -- 최종원가단가 NVL(thp.SPLY_UPRC, 0) AS SPLY_UPRC, -- 판매단가 NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC, -- 최초판매단가 thp.ORGPLCE_CD, -- 원산지 구분 -- TODO 원산지명 조회 필요 sale_prod_yn.NMCODE_NM AS SALE_PROD_YN, -- 판매상품여부 thp.PO_PROD_FG, -- 주문상품구분 --TODO 주문상품구분명 조회 필요 thp.PO_UNIT_FG, -- 주문단위 --TODO 주문단위명 조회 필요 thp.PO_MIN_QTY, -- 최소주문 vat_fg.NMCODE_NM AS VAT_FG, -- 과세여부 stock_prod_yn.NMCODE_NM AS STOCK_PROD_YN, -- 재고관리여부 use_yn.NMCODE_NM AS USE_YN, -- 사용여부 thp.SAFE_STOCK_QTY, -- 안전재고 set_prod_fg.NMCODE_NM AS SET_PROD_FG, -- 세트상품구분 thp.REMARK, -- 비고 thp.REG_DT FROM TB_HQ_PRODUCT thp LEFT OUTER JOIN ( SELECT PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' -- 본사, 매장 구분이 필요한지 확인. 무시면 조건 제거 GROUP BY PROD_CD ) thpsp ON thp.PROD_CD = thpsp.PROD_CD LEFT OUTER JOIN TB_CM_NMCODE sale_prod_yn ON sale_prod_yn.NMCODE_GRP_CD = '067' AND thp.SALE_PROD_YN = sale_prod_yn.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE vat_fg ON vat_fg.NMCODE_GRP_CD = '039' AND thp.VAT_FG = vat_fg.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE stock_prod_yn ON stock_prod_yn.NMCODE_GRP_CD = '067' AND thp.STOCK_PROD_YN = stock_prod_yn.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE use_yn ON use_yn.NMCODE_GRP_CD = '067' AND thp.USE_YN = use_yn.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE set_prod_fg ON set_prod_fg.NMCODE_GRP_CD = '009' AND thp.SET_PROD_FG = set_prod_fg.NMCODE_CD WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = #{prodCd}
SELECT tmp.STORE_CD, -- 매장코드 tmp.PROD_CD, -- 상품코드 tmp.PROD_NM, -- 상품명 tmp.PROD_CLASS_CD, -- 상품분류코드 ( SELECT SF_GET_PROD_CLASSES('S', tmp.STORE_CD, tmp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_NM, NVL(tmp.COST_UPRC, 0) AS COST_UPRC, -- 원가단가 NVL(tmp.LAST_COST_UPRC, 0) AS LAST_COST_UPRC, -- 최종원가단가 NVL(tmp.SPLY_UPRC, 0) AS SPLY_UPRC, -- 판매단가 NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC, -- 최초판매단가 tmp.ORGPLCE_CD, -- 원산지 구분 -- TODO 원산지명 조회 필요 sale_prod_yn.NMCODE_NM AS SALE_PROD_YN, -- 판매상품여부 tmp.PO_PROD_FG, -- 주문상품구분 --TODO 주문상품구분명 조회 필요 tmp.PO_UNIT_FG, -- 주문단위 --TODO 주문단위명 조회 필요 tmp.PO_MIN_QTY, -- 최소주문 vat_fg.NMCODE_NM AS VAT_FG, -- 과세여부 stock_prod_yn.NMCODE_NM AS STOCK_PROD_YN, -- 재고관리여부 use_yn.NMCODE_NM AS USE_YN, -- 사용여부 tmp.SAFE_STOCK_QTY, -- 안전재고 set_prod_fg.NMCODE_NM AS SET_PROD_FG, -- 세트상품구분 tmp.REMARK, -- 비고 tmp.REG_DT FROM TB_MS_PRODUCT tmp LEFT OUTER JOIN ( SELECT PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '2' -- 본사, 매장 구분이 필요한지 확인. 무시면 조건 제거 GROUP BY PROD_CD ) thpsp ON tmp.PROD_CD = thpsp.PROD_CD LEFT OUTER JOIN TB_CM_NMCODE sale_prod_yn ON sale_prod_yn.NMCODE_GRP_CD = '067' AND tmp.SALE_PROD_YN = sale_prod_yn.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE vat_fg ON vat_fg.NMCODE_GRP_CD = '039' AND tmp.VAT_FG = vat_fg.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE stock_prod_yn ON stock_prod_yn.NMCODE_GRP_CD = '067' AND tmp.STOCK_PROD_YN = stock_prod_yn.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE use_yn ON use_yn.NMCODE_GRP_CD = '067' AND tmp.USE_YN = use_yn.NMCODE_CD LEFT OUTER JOIN TB_CM_NMCODE set_prod_fg ON set_prod_fg.NMCODE_GRP_CD = '009' AND tmp.SET_PROD_FG = set_prod_fg.NMCODE_CD WHERE tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD = #{prodCd}

/* USE : ProdMapper.getUnitstProdList */
SELECT thpup.HQ_OFFICE_CD, thpup.PROD_CD, thpup.UNIT_PROD_CD, thpup.SET_PROD_FG, thpup.UNIT_PROD_QTY, thpup.DISP_SEQ FROM TB_HQ_PRODUCT_UNITST_PROD thpup WHERE thpup.HQ_OFFICE_CD = #{hqOfficeCd} AND thpup.PROD_CD = #{prodCd} ORDER BY thpup.DISP_SEQ
SELECT tmpup.STORE_CD, tmpup.PROD_CD, tmpup.UNIT_PROD_CD, tmpup.SET_PROD_FG, tmpup.UNIT_PROD_QTY, tmpup.DISP_SEQ FROM TB_MS_PRODUCT_UNITST_PROD tmpup WHERE tmpup.STORE_CD = #{storeCd} AND tmpup.PROD_CD = #{prodCd} ORDER BY tmpup.DISP_SEQ
