mapper.select:
/* USE : ProdMapper.getProdNmList */



        SELECT  thp.PROD_NM
        FROM    TB_HQ_PRODUCT thp
        WHERE   thp.HQ_OFFICE_CD = #{hqOfficeCd}



        SELECT  tmp.PROD_NM
        FROM    TB_MS_PRODUCT tmp
        WHERE   tmp.STORE_CD = #{storeCd}

/* USE : ProdMapper.getProdDetail */
SELECT thp.HQ_OFFICE_CD, -- 본사코드 thp.PROD_CD, -- 상품코드 thp.PROD_NM, -- 상품명 thp.HQ_BRAND_CD, -- 브랜드명 thb.HQ_BRAND_NM AS HQ_BRAND_NM, thp.PROD_CLASS_CD, -- 상품분류코드 ( SELECT SF_GET_PROD_CLASSES('H', thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_CD_NM, ( SELECT BARCD_CD FROM TB_HQ_PRODUCT_BARCD WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd}) AS BAR_CD, thp.CORNR_CD, -- 코너코드 thp.ORGPLCE_CD, -- 원산지 구분 -- TODO 원산지명 조회 필요 thp.SDATTR_CLASS_CD, -- 사이드속성분류코드 thp.SDSEL_GRP_CD, -- 사이드선택그룹코드 thpsg.SDSEL_GRP_NM, thp.POINT_SAVE_YN, thp.PROD_DC_FG, thp.VAT_FG, -- 과세여부 thp.PROD_TIP_YN, thp.PROD_PACK_AMT, thp.PROD_DLVR_AMT, thp.PROD_TYPE_FG, -- 상품유형구분 thp.SALE_PROD_YN, -- 판매상품여부 thp.STOCK_PROD_YN, -- 재고관리여부 thp.SIDE_PROD_YN, thp.SET_PROD_FG, -- 세트상품구분 NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC, -- 최초판매단가 thpsp.STIN_SALE_UPRC AS STIN_SALE_UPRC, -- 내점가(NULL 허용가능) thpsp.DLVR_SALE_UPRC AS DLVR_SALE_UPRC, -- 배달가(NULL 허용가능) thpsp.PACK_SALE_UPRC AS PACK_SALE_UPRC, -- 포장가(NULL 허용가능) NVL(thp.COST_UPRC, 0) AS COST_UPRC, -- 원가단가 NVL(thp.LAST_COST_UPRC, 0) AS LAST_COST_UPRC, -- 최종원가단가 NVL(thp.SPLY_UPRC, 0) AS SPLY_UPRC, -- 판매단가 thp.SPLY_UPRC_USE_YN, thp.PO_PROD_FG, -- 주문상품구분 thp.PO_UNIT_FG, -- 주문단위 thp.PO_UNIT_QTY, thp.PO_UNIT_ALLOW_FG, thp.PO_MIN_QTY, -- 최소주문 thp.SAFE_STOCK_QTY, -- 안전재고 0 AS DEFAULT_STOCK, -- todo 초기재고 thp.STOCK_UNIT_FG, thp.USE_YN, -- 사용여부 NVL(thp.REMARK, '') AS REMARK, -- 비고 thp.REG_DT, thpi.IMG_URL, thvp.VENDR_CD, thvp.VENDR_NM, thp.PRC_CTRL_FG, thp.MAP_PROD_CD, NVL(thp.DEPOSIT_CUP_FG, '') AS DEPOSIT_CUP_FG, thp.POINT_USE_YN, thp.DC_YN, thpi.PROD_INFO, NVL(thp.SALE_TIME_FG, 'N') AS SALE_TIME_FG, thpi.NU_TOT_WT, thpi.NU_KCAL, thpi.NU_PROTEIN, thpi.NU_SODIUM, thpi.NU_SUGARS, thpi.NU_SAT_FAT, thpi.NU_CAFFEINE, thpi.MOMS_KIOSK_EDGE, (SELECT OPTION_GRP_NM FROM TB_HQ_PRODUCT_OPTION_GROUP WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND OPTION_GRP_CD = thp.OPTION_GRP_CD) AS OPTION_GRP_NM, thp.OPTION_GRP_CD, thp.RELEASE_DATE, thp.DISCON_DATE, thp.DISCON_YN, thpi.SALE_TYPE_YN_SIN, thpi.SALE_TYPE_YN_DLV, thpi.SALE_TYPE_YN_PKG, thpi.SALE_CHN_YN_POS, thpi.SALE_CHN_YN_KSK, thpi.SALE_CHN_YN_CMP, thpi.SALE_CHN_YN_BAE, thpi.SALE_CHN_YN_BAO, thpi.SALE_CHN_YN_YGY, thpi.SALE_CHN_YN_YGE, thpi.SALE_CHN_YN_CPN, thpi.SALE_CHN_YN_TNG, thpi.SALE_CHN_YN_DDN FROM TB_HQ_PRODUCT thp LEFT OUTER JOIN TB_HQ_BRAND thb ON thb.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thb.HQ_BRAND_CD = thp.HQ_BRAND_CD LEFT OUTER JOIN ( SELECT HQ_OFFICE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC, MIN(STIN_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS STIN_SALE_UPRC, MIN(DLVR_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS DLVR_SALE_UPRC, MIN(PACK_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS PACK_SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp ON thp.HQ_OFFICE_CD = thpsp.HQ_OFFICE_CD AND thp.PROD_CD = thpsp.PROD_CD LEFT OUTER JOIN ( SELECT HQ_OFFICE_CD, PROD_CD, IMG_URL || '/' || IMG_FILE_NM AS IMG_URL FROM TB_HQ_PRODUCT_IMAGE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND IMG_FG = '001' ) thpi ON thp.HQ_OFFICE_CD = thpi.HQ_OFFICE_CD AND thp.PROD_CD = thpi.PROD_CD LEFT OUTER JOIN ( SELECT thvp.HQ_OFFICE_CD, thvp.PROD_CD, LISTAGG(thvp.VENDR_CD, ',') WITHIN GROUP (ORDER BY thvp.VENDR_CD) AS VENDR_CD, LISTAGG(thv.VENDR_NM, ',') WITHIN GROUP (ORDER BY thvp.VENDR_CD) AS VENDR_NM FROM TB_HQ_VENDOR_PROD thvp, TB_HQ_VENDOR thv WHERE 1=1 AND thvp.HQ_OFFICE_CD = #{hqOfficeCd} AND thvp.PROD_CD = #{prodCd} AND thvp.USE_YN = 'Y' AND thv.HQ_OFFICE_CD = thvp.HQ_OFFICE_CD AND thv.VENDR_CD = thvp.VENDR_CD GROUP BY thvp.HQ_OFFICE_CD, thvp.PROD_CD ) thvp ON thp.HQ_OFFICE_CD = thvp.HQ_OFFICE_CD AND thp.PROD_CD = thvp.PROD_CD LEFT OUTER JOIN TB_HQ_PRODUCT_SDSEL_GROUP thpsg ON thp.HQ_OFFICE_CD = thpsg.HQ_OFFICE_CD AND thp.SDSEL_GRP_CD = thpsg.SDSEL_GRP_CD AND thpsg.USE_YN = 'Y' LEFT OUTER JOIN TB_HQ_PRODUCT_INFO thpi ON thp.HQ_OFFICE_CD = thpi.HQ_OFFICE_CD AND thp.PROD_CD = thpi.PROD_CD WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = #{prodCd}
SELECT tmp.STORE_CD, -- 매장코드 tmp.PROD_CD, -- 상품코드 tmp.PROD_NM, -- 상품명 tmp.HQ_BRAND_CD, -- 브랜드명 tmb.MS_BRAND_NM AS HQ_BRAND_NM, tmp.PROD_CLASS_CD, -- 상품분류코드 ( SELECT SF_GET_PROD_CLASSES('S', tmp.STORE_CD, tmp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_CD_NM, ( SELECT BARCD_CD FROM TB_MS_PRODUCT_BARCD WHERE STORE_CD = #{storeCd} AND PROD_CD = #{prodCd}) AS BAR_CD, tmp.CORNR_CD, -- 코너코드 tmp.ORGPLCE_CD, -- 원산지 구분 -- TODO 원산지명 조회 필요 tmp.SDATTR_CLASS_CD, tmp.SDSEL_GRP_CD, tmpsg.SDSEL_GRP_NM, tmp.POINT_SAVE_YN, tmp.PROD_DC_FG, tmp.VAT_FG, -- 과세여부 tmp.PROD_TIP_YN, tmp.PROD_PACK_AMT, tmp.PROD_DLVR_AMT, tmp.PROD_TYPE_FG, -- 상품유형 tmp.SALE_PROD_YN, -- 판매상품여부 tmp.STOCK_PROD_YN, -- 재고관리여부 tmp.SIDE_PROD_YN, tmp.SET_PROD_FG, -- 세트상품구분 NVL(tmpsp.SALE_UPRC, 0) AS SALE_UPRC, -- 최초판매단가 tmpsp.STIN_SALE_UPRC AS STIN_SALE_UPRC, -- 내점가(NULL 허용가능) tmpsp.DLVR_SALE_UPRC AS DLVR_SALE_UPRC, -- 배달가(NULL 허용가능) tmpsp.PACK_SALE_UPRC AS PACK_SALE_UPRC, -- 포장가(NULL 허용가능) NVL(tmp.COST_UPRC, 0) AS COST_UPRC, -- 원가단가 NVL(tmp.LAST_COST_UPRC, 0) AS LAST_COST_UPRC, -- 최종원가단가 NVL(tmp.SPLY_UPRC, 0) AS SPLY_UPRC, -- 판매단가 tmp.SPLY_UPRC_USE_YN, tmp.PO_PROD_FG, -- 주문상품구분 tmp.PO_UNIT_FG, -- 주문단위 tmp.PO_UNIT_QTY, tmp.PO_UNIT_ALLOW_FG, tmp.PO_MIN_QTY, -- 최소주문 tmp.SAFE_STOCK_QTY, -- 안전재고 0 AS DEFAULT_STOCK, -- todo 초기재고 tmp.STOCK_UNIT_FG, tmp.USE_YN, -- 사용여부 NVL(tmp.REMARK, '') AS REMARK, -- 비고 tmp.REG_DT, tmpi.IMG_URL, tmvp.VENDR_CD, tmvp.VENDR_NM, tmp.PRC_CTRL_FG, tmp.MAP_PROD_CD, NVL(tmp.DEPOSIT_CUP_FG, '') AS DEPOSIT_CUP_FG, tmp.POINT_USE_YN, tmp.DC_YN, tmpi.PROD_INFO, NVL(tmp.SALE_TIME_FG, 'N') AS SALE_TIME_FG, tmpi.NU_TOT_WT, tmpi.NU_KCAL, tmpi.NU_PROTEIN, tmpi.NU_SODIUM, tmpi.NU_SUGARS, tmpi.NU_SAT_FAT, tmpi.NU_CAFFEINE, tmpi.MOMS_KIOSK_EDGE, (SELECT OPTION_GRP_NM FROM TB_MS_PRODUCT_OPTION_GROUP WHERE STORE_CD = #{storeCd} AND OPTION_GRP_CD = tmp.OPTION_GRP_CD) AS OPTION_GRP_NM, tmp.OPTION_GRP_CD, tmp.RELEASE_DATE, tmp.DISCON_DATE, tmp.DISCON_YN, tmpi.SALE_TYPE_YN_SIN, tmpi.SALE_TYPE_YN_DLV, tmpi.SALE_TYPE_YN_PKG, tmpi.SALE_CHN_YN_POS, tmpi.SALE_CHN_YN_KSK, tmpi.SALE_CHN_YN_CMP, tmpi.SALE_CHN_YN_BAE, tmpi.SALE_CHN_YN_BAO, tmpi.SALE_CHN_YN_YGY, tmpi.SALE_CHN_YN_YGE, tmpi.SALE_CHN_YN_CPN, tmpi.SALE_CHN_YN_TNG, tmpi.SALE_CHN_YN_DDN FROM TB_MS_PRODUCT tmp LEFT OUTER JOIN TB_MS_BRAND tmb ON tmb.STORE_CD = tmp.STORE_CD AND tmb.MS_BRAND_CD = tmp.HQ_BRAND_CD LEFT OUTER JOIN ( SELECT STORE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC, MIN(STIN_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS STIN_SALE_UPRC, MIN(DLVR_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS DLVR_SALE_UPRC, MIN(PACK_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS PACK_SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY STORE_CD, PROD_CD ) tmpsp ON tmp.STORE_CD = tmpsp.STORE_CD AND tmp.PROD_CD = tmpsp.PROD_CD LEFT OUTER JOIN ( SELECT STORE_CD, PROD_CD, IMG_URL || '/' || IMG_FILE_NM AS IMG_URL FROM TB_MS_PRODUCT_IMAGE WHERE STORE_CD = #{storeCd} AND IMG_FG = '001' ) tmpi ON tmp.STORE_CD = tmpi.STORE_CD AND tmp.PROD_CD = tmpi.PROD_CD LEFT OUTER JOIN ( SELECT tmvp.STORE_CD, tmvp.PROD_CD, LISTAGG(tmvp.VENDR_CD, ',') WITHIN GROUP (ORDER BY tmvp.VENDR_CD) AS VENDR_CD, LISTAGG(tmv.VENDR_NM, ',') WITHIN GROUP (ORDER BY tmvp.VENDR_CD) AS VENDR_NM FROM TB_MS_VENDOR_PROD tmvp, TB_MS_VENDOR tmv WHERE 1=1 AND tmvp.STORE_CD = #{storeCd} AND tmvp.PROD_CD = #{prodCd} AND tmvp.USE_YN = 'Y' AND tmv.STORE_CD = tmvp.STORE_CD AND tmv.VENDR_CD = tmvp.VENDR_CD GROUP BY tmvp.STORE_CD, tmvp.PROD_CD ) tmvp ON tmp.STORE_CD = tmvp.STORE_CD AND tmp.PROD_CD = tmvp.PROD_CD LEFT OUTER JOIN TB_MS_PRODUCT_SDSEL_GROUP tmpsg ON tmp.STORE_CD = tmpsg.STORE_CD AND tmp.SDSEL_GRP_CD = tmpsg.SDSEL_GRP_CD AND tmpsg.USE_YN = 'Y' LEFT OUTER JOIN TB_MS_PRODUCT_INFO tmpi ON tmp.STORE_CD = tmpi.STORE_CD AND tmp.PROD_CD = tmpi.PROD_CD WHERE tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD = #{prodCd}

/* USE : ProdMapper.getLinkedProdList */
SELECT thpup.HQ_OFFICE_CD, thpup.PROD_CD, thpup.UNIT_PROD_CD, thpup.SET_PROD_FG, thpup.UNIT_PROD_QTY, thpup.DISP_SEQ FROM TB_HQ_PRODUCT_UNITST_PROD thpup WHERE thpup.HQ_OFFICE_CD = #{hqOfficeCd} AND thpup.PROD_CD = #{prodCd} ORDER BY thpup.DISP_SEQ
SELECT tmpup.STORE_CD, tmpup.PROD_CD, tmpup.UNIT_PROD_CD, tmpup.SET_PROD_FG, tmpup.UNIT_PROD_QTY, tmpup.DISP_SEQ FROM TB_MS_PRODUCT_UNITST_PROD tmpup WHERE tmpup.STORE_CD = #{storeCd} AND tmpup.PROD_CD = #{prodCd} ORDER BY tmpup.DISP_SEQ

/* ProdMapper.getPrefixProdCd */
SELECT #{prefix} || LPAD(NVL(MAX(TO_NUMBER(REGEXP_REPLACE(SUBSTR(PROD_CD, LENGTH(#{prefix}) + 1), '[^0-9]'))),0) + 1, 13 - LENGTH(#{prefix}), '0') AS PROD_CD
  FROM TB_MS_PRODUCT
 WHERE STORE_CD = #{storeCd}
   AND PROD_CD LIKE #{prefix} || '%'

mapper.update:
/* ProdMapper.saveProductInfo */
MERGE INTO TB_HQ_PRODUCT thp USING DUAL ON ( thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = #{prodCd} ) WHEN MATCHED THEN UPDATE SET PROD_NM = TRIM(REPLACE(REPLACE(REPLACE(#{prodNm}, CHR(10), NULL), CHR(13), NULL), CHR(9), NULL)), HQ_BRAND_CD = NVL(#{hqBrandCd}, '0000000'), PROD_CLASS_CD = #{prodClassCd}, CORNR_CD = #{cornrCd}, ORGPLCE_CD = #{orgplceCd}, SDATTR_CLASS_CD = #{sdattrClassCd}, SDSEL_GRP_CD = #{sdselGrpCd}, POINT_SAVE_YN = #{pointSaveYn, jdbcType=VARCHAR}, PROD_DC_FG = #{prodDcFg}, VAT_FG = #{vatFg}, PROD_TIP_YN = #{prodTipYn, jdbcType=VARCHAR}, PROD_PACK_AMT = #{prodPackAmt, jdbcType=NUMERIC}, PROD_DLVR_AMT = #{prodDlvrAmt, jdbcType=NUMERIC}, PROD_TYPE_FG = #{prodTypeFg}, SALE_PROD_YN = #{saleProdYn}, STOCK_PROD_YN = #{stockProdYn}, SIDE_PROD_YN = #{sideProdYn, jdbcType=VARCHAR}, SET_PROD_FG = #{setProdFg}, COST_UPRC = #{costUprc, jdbcType=NUMERIC}, LAST_COST_UPRC = #{lastCostUprc, jdbcType=NUMERIC}, SPLY_UPRC = #{splyUprc, jdbcType=NUMERIC}, SPLY_UPRC_USE_YN = #{splyUprcUseYn, jdbcType=VARCHAR}, PO_PROD_FG = #{poProdFg}, PO_UNIT_FG = #{poUnitFg}, PO_UNIT_QTY = #{poUnitQty, jdbcType=NUMERIC}, PO_UNIT_ALLOW_FG = #{poUnitAllowFg}, PO_MIN_QTY = #{poMinQty, jdbcType=NUMERIC}, SAFE_STOCK_QTY = #{safeStockQty, jdbcType=NUMERIC}, STOCK_UNIT_FG = #{stockUnitFg}, USE_YN = #{useYn}, REMARK = #{remark}, MOD_DT = #{modDt}, MOD_ID = #{modId}, PRC_CTRL_FG = #{prcCtrlFg}, MAP_PROD_CD = #{mapProdCd}, DEPOSIT_CUP_FG = #{depositCupFg}, POINT_USE_YN = #{pointUseYn}, DC_YN = #{dcYn}, SALE_TIME_FG = #{saleTimeFg}, OPTION_GRP_CD = #{optionGrpCd}, RELEASE_DATE = #{releaseDate}, DISCON_DATE = #{disconDate}, DISCON_YN = #{disconYn} WHEN NOT MATCHED THEN INSERT ( HQ_OFFICE_CD, PROD_CD, PROD_NM, HQ_BRAND_CD, PROD_CLASS_CD, CORNR_CD, ORGPLCE_CD, SDATTR_CLASS_CD, SDSEL_GRP_CD, POINT_SAVE_YN, PROD_DC_FG, VAT_FG, PROD_TIP_YN, PROD_PACK_AMT, PROD_DLVR_AMT, PROD_TYPE_FG, SALE_PROD_YN, STOCK_PROD_YN, SIDE_PROD_YN, SET_PROD_FG, COST_UPRC, LAST_COST_UPRC, SPLY_UPRC, SPLY_UPRC_USE_YN, PO_PROD_FG, PO_UNIT_FG, PO_UNIT_QTY, PO_UNIT_ALLOW_FG, PO_MIN_QTY, SAFE_STOCK_QTY, STOCK_UNIT_FG, USE_YN, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID, PRC_CTRL_FG, MAP_PROD_CD, DEPOSIT_CUP_FG, POINT_USE_YN, DC_YN, SALE_TIME_FG, OPTION_GRP_CD, RELEASE_DATE, DISCON_DATE, DISCON_YN ) VALUES ( #{hqOfficeCd}, TRIM(REPLACE(REPLACE(REPLACE(#{prodCd}, CHR(10), NULL), CHR(13), NULL), CHR(9), NULL)), TRIM(REPLACE(REPLACE(REPLACE(#{prodNm}, CHR(10), NULL), CHR(13), NULL), CHR(9), NULL)), NVL(#{hqBrandCd}, '0000000'), #{prodClassCd}, '00', #{orgplceCd}, #{sdattrClassCd}, #{sdselGrpCd}, #{pointSaveYn, jdbcType=VARCHAR}, #{prodDcFg}, #{vatFg}, #{prodTipYn, jdbcType=VARCHAR}, #{prodPackAmt, jdbcType=NUMERIC}, #{prodDlvrAmt, jdbcType=NUMERIC}, #{prodTypeFg}, #{saleProdYn}, #{stockProdYn}, #{sideProdYn, jdbcType=VARCHAR}, #{setProdFg}, #{costUprc, jdbcType=NUMERIC}, #{lastCostUprc, jdbcType=NUMERIC}, #{splyUprc, jdbcType=NUMERIC}, #{splyUprcUseYn, jdbcType=VARCHAR}, #{poProdFg}, #{poUnitFg}, #{poUnitQty, jdbcType=NUMERIC}, #{poUnitAllowFg}, #{poMinQty, jdbcType=NUMERIC}, #{safeStockQty, jdbcType=NUMERIC}, #{stockUnitFg}, #{useYn}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId}, #{prcCtrlFg}, #{mapProdCd}, #{depositCupFg}, #{pointUseYn}, #{dcYn}, #{saleTimeFg}, #{optionGrpCd}, #{releaseDate}, #{disconDate}, #{disconYn} )
MERGE INTO TB_MS_PRODUCT tmp USING DUAL ON ( tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD = #{prodCd} ) WHEN MATCHED THEN UPDATE SET PROD_NM = TRIM(REPLACE(REPLACE(REPLACE(#{prodNm}, CHR(10), NULL), CHR(13), NULL), CHR(9), NULL)), HQ_BRAND_CD = NVL(#{hqBrandCd}, '0000000'), PROD_CLASS_CD = #{prodClassCd}, CORNR_CD = #{cornrCd}, ORGPLCE_CD = #{orgplceCd}, SDATTR_CLASS_CD = #{sdattrClassCd}, SDSEL_GRP_CD = #{sdselGrpCd}, POINT_SAVE_YN = #{pointSaveYn}, PROD_DC_FG = #{prodDcFg}, VAT_FG = #{vatFg}, PROD_TIP_YN = #{prodTipYn}, PROD_PACK_AMT = #{prodPackAmt, jdbcType=NUMERIC}, PROD_DLVR_AMT = #{prodDlvrAmt, jdbcType=NUMERIC}, PROD_TYPE_FG = #{prodTypeFg}, SALE_PROD_YN = #{saleProdYn}, STOCK_PROD_YN = #{stockProdYn}, SIDE_PROD_YN = #{sideProdYn}, SET_PROD_FG = #{setProdFg}, PRC_CTRL_FG = #{prcCtrlFg}, COST_UPRC = #{costUprc}, LAST_COST_UPRC = #{lastCostUprc}, SPLY_UPRC = #{splyUprc}, SPLY_UPRC_USE_YN = #{splyUprcUseYn}, PO_PROD_FG = #{poProdFg}, PO_UNIT_FG = #{poUnitFg}, PO_UNIT_QTY = #{poUnitQty}, PO_UNIT_ALLOW_FG = #{poUnitAllowFg}, PO_MIN_QTY = #{poMinQty}, SAFE_STOCK_QTY = #{safeStockQty}, STOCK_UNIT_FG = #{stockUnitFg}, USE_YN = #{useYn}, REMARK = #{remark}, MOD_DT = #{modDt}, MOD_ID = #{modId}, MAP_PROD_CD = #{mapProdCd}, DEPOSIT_CUP_FG = #{depositCupFg}, POINT_USE_YN = #{pointUseYn}, DC_YN = #{dcYn}, SALE_TIME_FG = #{saleTimeFg}, OPTION_GRP_CD = #{optionGrpCd}, RELEASE_DATE = #{releaseDate}, DISCON_DATE = #{disconDate}, DISCON_YN = #{disconYn} WHEN NOT MATCHED THEN INSERT ( STORE_CD, PROD_CD, PROD_NM, HQ_BRAND_CD, PROD_CLASS_CD, CORNR_CD, ORGPLCE_CD, SDATTR_CLASS_CD, SDSEL_GRP_CD, POINT_SAVE_YN, PROD_DC_FG, VAT_FG, PROD_TIP_YN, PROD_PACK_AMT, PROD_DLVR_AMT, PROD_TYPE_FG, SALE_PROD_YN, STOCK_PROD_YN, SIDE_PROD_YN, SET_PROD_FG, PRC_CTRL_FG, COST_UPRC, LAST_COST_UPRC, SPLY_UPRC, SPLY_UPRC_USE_YN, PO_PROD_FG, PO_UNIT_FG, PO_UNIT_QTY, PO_UNIT_ALLOW_FG, PO_MIN_QTY, SAFE_STOCK_QTY, STOCK_UNIT_FG, USE_YN, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID, REG_FG, MAP_PROD_CD, DEPOSIT_CUP_FG, POINT_USE_YN, DC_YN, SALE_TIME_FG, OPTION_GRP_CD, RELEASE_DATE, DISCON_DATE, DISCON_YN ) VALUES ( #{storeCd}, TRIM(REPLACE(REPLACE(REPLACE(#{prodCd}, CHR(10), NULL), CHR(13), NULL), CHR(9), NULL)), TRIM(REPLACE(REPLACE(REPLACE(#{prodNm}, CHR(10), NULL), CHR(13), NULL), CHR(9), NULL)), NVL(#{hqBrandCd}, '0000000'), #{prodClassCd}, '00', #{orgplceCd}, #{sdattrClassCd}, #{sdselGrpCd}, #{pointSaveYn}, #{prodDcFg}, #{vatFg}, #{prodTipYn}, #{prodPackAmt, jdbcType=NUMERIC}, #{prodDlvrAmt, jdbcType=NUMERIC}, #{prodTypeFg}, #{saleProdYn}, #{stockProdYn}, #{sideProdYn}, #{setProdFg}, #{prcCtrlFg}, #{costUprc}, #{lastCostUprc}, #{splyUprc}, #{splyUprcUseYn}, #{poProdFg}, #{poUnitFg}, #{poUnitQty}, #{poUnitAllowFg}, #{poMinQty}, #{safeStockQty}, #{stockUnitFg}, #{useYn}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId}, 'S', #{mapProdCd}, #{depositCupFg}, #{pointUseYn}, #{dcYn}, #{saleTimeFg}, #{optionGrpCd}, #{releaseDate}, #{disconDate}, #{disconYn} )

/* ProdMapper.saveProdInfo */
