mapper.select:
/* USE : ProdMapper.getRegistProdCount */ SELECT COUNT(PROD_CD) AS CNT FROM ( SELECT STORE_CD, PROD_CD, SALE_PRC_FG, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC, MIN(STIN_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS STIN_SALE_UPRC, MIN(DLVR_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS DLVR_SALE_UPRC, MIN(PACK_SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS PACK_SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE 1 = 1
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY STORE_CD, PROD_CD, SALE_PRC_FG ) WHERE STORE_CD = #{storeCd} AND PROD_CD = #{prodCd} AND (SALE_UPRC = NVL(#{saleUprc}, 0) OR STIN_SALE_UPRC = NVL(#{stinSaleUprc}, 0) OR DLVR_SALE_UPRC = NVL(#{dlvrSaleUprc}, 0) OR PACK_SALE_UPRC = NVL(#{packSaleUprc}, 0))

/* USE : ProdMapper.getProdCdCnt */





        SELECT
        COUNT(1) AS CNT
        FROM (
        SELECT PROD_CD FROM TB_HQ_PRODUCT WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd}
        UNION ALL
        (
        SELECT
        tmp.PROD_CD
        FROM TB_MS_PRODUCT tmp,
        TB_MS_STORE tms
        WHERE 1=1
        AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
        AND tmp.STORE_CD = tms.STORE_CD
        AND tmp.PROD_CD = #{prodCd}
        )
        )



        SELECT
        COUNT(1) AS CNT
        FROM (
        SELECT PROD_CD FROM TB_HQ_PRODUCT WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd}
        UNION ALL
        SELECT PROD_CD FROM TB_MS_PRODUCT WHERE STORE_CD = #{storeCd} AND PROD_CD = #{prodCd}
        )



        SELECT COUNT(1) AS CNT
        FROM TB_MS_PRODUCT
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}

/* USE : ProdMapper.chkBarCd */
SELECT '[' || CD || ']' || NM || '에 [' || PROD_CD || ']' || PROD_NM || ' 상품의 바코드와 중복되어 저장 할 수 없습니다.' FROM ( SELECT thpb.HQ_OFFICE_CD AS CD, tho.HQ_OFFICE_NM AS NM, thpb.PROD_CD, thp.PROD_NM FROM TB_HQ_PRODUCT_BARCD thpb, TB_HQ_PRODUCT thp, TB_HQ_OFFICE tho WHERE thpb.HQ_OFFICE_CD = #{hqOfficeCd} AND tho.HQ_OFFICE_CD = thpb.HQ_OFFICE_CD AND thp.HQ_OFFICE_CD = thpb.HQ_OFFICE_CD AND thp.PROD_CD = thpb.PROD_CD AND thpb.BARCD_CD = #{barCd}
AND thpb.PROD_CD != #{prodCd}
UNION ALL SELECT tms.STORE_CD AS CD, tms.STORE_NM AS NM, tmpb.PROD_CD, tmp.PROD_NM FROM TB_MS_PRODUCT_BARCD tmpb, TB_MS_PRODUCT tmp, TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmpb.STORE_CD = tms.STORE_CD AND tmp.STORE_CD = tms.STORE_CD AND tmp.PROD_CD = tmpb.PROD_CD AND tmpb.BARCD_CD =#{barCd}
AND tmpb.PROD_CD != #{prodCd}
)
SELECT '[' || CD || ']' || NM || '에 [' || PROD_CD || ']' || PROD_NM || ' 상품의 바코드와 중복되어 저장 할 수 없습니다.' FROM ( SELECT thpb.HQ_OFFICE_CD AS CD, tho.HQ_OFFICE_NM AS NM, thpb.PROD_CD, thp.PROD_NM FROM TB_HQ_PRODUCT_BARCD thpb, TB_HQ_PRODUCT thp, TB_HQ_OFFICE tho WHERE thpb.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.HQ_OFFICE_CD = thpb.HQ_OFFICE_CD AND tho.HQ_OFFICE_CD = thpb.HQ_OFFICE_CD AND thp.PROD_CD = thpb.PROD_CD AND thpb.BARCD_CD = #{barCd}
AND thpb.PROD_CD != #{prodCd}
UNION ALL SELECT tms.STORE_CD AS CD, tms.STORE_NM AS NM, tmpb.PROD_CD, tmp.PROD_NM FROM TB_MS_PRODUCT_BARCD tmpb, TB_MS_PRODUCT tmp, TB_MS_STORE tms WHERE tmpb.STORE_CD = #{storeCd} AND tmp.STORE_CD = tmpb.STORE_CD AND tms.STORE_CD = tmpb.STORE_CD AND tmp.PROD_CD = tmpb.PROD_CD AND tmpb.BARCD_CD = #{barCd}
AND tmpb.PROD_CD != #{prodCd}
)
SELECT '[' || tmpb.STORE_CD || ']' || tms.STORE_NM || '에 [' || tmpb.PROD_CD || ']' || tmp.PROD_NM || ' 상품의 바코드와 중복되어 저장 할 수 없습니다.' FROM TB_MS_PRODUCT_BARCD tmpb, TB_MS_PRODUCT tmp, TB_MS_STORE tms WHERE tmpb.STORE_CD = #{storeCd} AND tmp.STORE_CD = tmpb.STORE_CD AND tms.STORE_CD = tmpb.STORE_CD AND tmp.PROD_CD = tmpb.PROD_CD AND tmpb.BARCD_CD = #{barCd}
AND tmpb.PROD_CD != #{prodCd}

/* USE : prodMapper.chkBarCdHq */
SELECT  A.CNT AS HQ_CNT,
B.CNT AS MS_CNT
FROM (
SELECT  COUNT(*) AS CNT
FROM    TB_HQ_PRODUCT_BARCD thpb,   TB_HQ_PRODUCT thp,  TB_HQ_OFFICE tho
WHERE   thpb.HQ_OFFICE_CD = #{hqOfficeCd}
AND     tho.HQ_OFFICE_CD = thpb.HQ_OFFICE_CD
AND     thp.HQ_OFFICE_CD = thpb.HQ_OFFICE_CD
AND     thp.PROD_CD = thpb.PROD_CD
AND     thpb.BARCD_CD = REPLACE(REPLACE(REPLACE(#{barCd}, CHR(10), NULL), CHR(13), NULL), ' ', '')

    AND thpb.PROD_CD != #{prodCd}

) A,
(
SELECT  COUNT(*) AS CNT
FROM    TB_MS_PRODUCT_BARCD tmpb,   TB_MS_PRODUCT tmp,  TB_MS_STORE tms
WHERE   tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND     tmpb.STORE_CD = tms.STORE_CD
AND     tmp.STORE_CD = tms.STORE_CD
AND     tmp.PROD_CD = tmpb.PROD_CD
AND     tmpb.BARCD_CD = REPLACE(REPLACE(REPLACE(#{barCd}, CHR(10), NULL), CHR(13), NULL), ' ', '')

    AND tmpb.PROD_CD != #{prodCd}

) B

/* USE : ProdMapper.getProdBarCdCnt */
 SELECT COUNT(1) AS CNT
   FROM TB_HQ_PRODUCT_BARCD
  WHERE HQ_OFFICE_CD = #{hqOfficeCd}
    AND PROD_CD = #{prodCd}

mapper.insert:
/* USE : ProdMapper.saveProdBarcd */



        MERGE INTO TB_HQ_PRODUCT_BARCD
        USING DUAL ON (
        HQ_OFFICE_CD = #{hqOfficeCd}
        AND PROD_CD = #{prodCd}
        )
        WHEN MATCHED THEN
        UPDATE
        SET BARCD_CD = #{barCd}
        , BARCD_NM = #{prodNm}
        , MOD_DT = #{modDt}
        , MOD_ID = #{modId}
        WHEN NOT MATCHED THEN
        INSERT (
        HQ_OFFICE_CD
        , PROD_CD
        , BARCD_CD
        , BARCD_NM
        , UNIT_QTY
        , REG_DT
        , REG_ID
        , MOD_DT
        , MOD_ID )
        VALUES (
        #{hqOfficeCd}
        , #{prodCd}
        , #{barCd}
        , #{prodNm}
        , '1'
        , #{regDt}
        , #{regId}
        , #{modDt}
        , #{modId} )



        MERGE INTO TB_MS_PRODUCT_BARCD
        USING DUAL ON (
        STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}
        )
        WHEN MATCHED THEN
        UPDATE
        SET BARCD_CD = #{barCd}
        , BARCD_NM = #{prodNm}
        , MOD_DT = #{modDt}
        , MOD_ID = #{modId}
        WHEN NOT MATCHED THEN
        INSERT (
        STORE_CD
        , PROD_CD
        , BARCD_CD
        , BARCD_NM
        , UNIT_QTY
        , REG_DT
        , REG_ID
        , MOD_DT
        , MOD_ID )
        VALUES (
        #{storeCd}
        , #{prodCd}
        , #{barCd}
        , #{prodNm}
        , '1'
        , #{regDt}
        , #{regId}
        , #{modDt}
        , #{modId} )

mapper.delete:
/* USE : ProdMapper.deleteProdBarcd */



        MERGE INTO TB_HQ_PRODUCT_BARCD
        USING DUAL ON (
        HQ_OFFICE_CD = #{hqOfficeCd}
        AND PROD_CD = #{prodCd}
        )
        WHEN MATCHED THEN
        UPDATE
        SET MOD_DT = #{modDt}
        , MOD_ID = #{modId}
        DELETE
        WHERE HQ_OFFICE_CD = #{hqOfficeCd}
        AND PROD_CD = #{prodCd}



        MERGE INTO TB_MS_PRODUCT_BARCD
        USING DUAL ON (
        STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}
        )
        WHEN MATCHED THEN
        UPDATE
        SET MOD_DT = #{modDt}
        , MOD_ID = #{modId}
        DELETE
        WHERE STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}

/* USE : ProdMapper.deleteProdBarcdStoreHq */
MERGE INTO TB_MS_PRODUCT_BARCD A
    USING   (
        SELECT  B.STORE_CD      AS  STORE_CD
             ,  #{barCd}    AS  BARCD_CD
        FROM  TB_MS_STORE B
        WHERE   B.HQ_OFFICE_CD  =   #{hqOfficeCd}
    ) B
    ON  (
            A.STORE_CD      =   B.STORE_CD
            AND A.BARCD_CD   =   B.BARCD_CD
        )
    WHEN MATCHED THEN
        UPDATE
            SET MOD_DT = #{modDt}
            ,   MOD_ID = #{modId}
        DELETE
        WHERE   STORE_CD    = B.STORE_CD
          AND   BARCD_CD     = B.BARCD_CD

/* USE : ProdMapper.deleteProdBarcdStore */
        MERGE INTO TB_MS_PRODUCT_BARCD A
            USING   (
                SELECT  B.STORE_CD      AS  STORE_CD
                     ,  #{prodCd}    AS  PROD_CD
                  FROM  TB_MS_STORE B
                WHERE   B.HQ_OFFICE_CD  =   #{hqOfficeCd}
            ) B
            ON  (
                    A.STORE_CD      =   B.STORE_CD
                    AND A.PROD_CD   =   B.PROD_CD
                )
        WHEN MATCHED THEN
        UPDATE
        SET MOD_DT = #{modDt}
        ,   MOD_ID = #{modId}
        DELETE
        WHERE   STORE_CD    = B.STORE_CD
        AND     PROD_CD     = B.PROD_CD

