/* ProdMapper.getProdImageFileSaveCheck */




            SELECT COUNT(*)
            FROM TB_HQ_PRODUCT_IMAGE thpi
            WHERE 1=1
            AND thpi.HQ_OFFICE_CD = #{hqOfficeCd}
            AND thpi.PROD_CD = #{prodCd}
            AND thpi.IMG_FG = '001'



            SELECT COUNT(*)
            FROM TB_MS_PRODUCT_IMAGE tmpi
            WHERE 1=1
            AND tmpi.STORE_CD = #{storeCd}
            AND tmpi.PROD_CD = #{prodCd}
            AND tmpi.IMG_FG = '001'

mapper.insert:
/* ProdMapper.getProdImageFileSaveInsert */
INSERT INTO TB_HQ_PRODUCT_IMAGE ( HQ_OFFICE_CD, PROD_CD, IMG_FG, IMG_URL, IMG_FILE_NM, IMG_CHG_DT, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{prodCd}, '001', --001: 기본이미지, 002: KIOSK이미지, 003: DID이미지 #{filePath}, #{fileNm} ||'.'|| #{fileExt}, #{modDt}, '', #{regDt}, #{regId}, #{modDt}, #{modId} )
INSERT INTO TB_MS_PRODUCT_IMAGE ( STORE_CD, PROD_CD, IMG_FG, IMG_URL, IMG_FILE_NM, IMG_CHG_DT, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{prodCd}, '001', --001: 기본이미지, 002: KIOSK이미지, 003: DID이미지 #{filePath}, #{fileNm} ||'.'|| #{fileExt}, #{modDt}, '', #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* ProdMapper.getProdImageFileSaveUpdate */
UPDATE TB_HQ_PRODUCT_IMAGE SET IMG_URL = #{filePath}, IMG_FILE_NM = #{fileNm} ||'.'|| #{fileExt}, IMG_CHG_DT = #{modDt}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd} AND IMG_FG = '001'
UPDATE TB_MS_PRODUCT_IMAGE SET IMG_URL = #{filePath}, IMG_FILE_NM = #{fileNm} ||'.'|| #{fileExt}, IMG_CHG_DT = #{modDt}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE 1=1 AND STORE_CD = #{storeCd} AND PROD_CD = #{prodCd} AND IMG_FG = '001'

mapper.delete:
/* ProdMapper.getProdImageFileSaveDelete */




            DELETE
            TB_HQ_PRODUCT_IMAGE
            WHERE 1=1
            AND HQ_OFFICE_CD = #{hqOfficeCd}
            AND PROD_CD = #{prodCd}
            AND IMG_FG = '001'



            DELETE
            TB_MS_PRODUCT_IMAGE
            WHERE 1=1
            AND STORE_CD = #{storeCd}
            AND PROD_CD = #{prodCd}
            AND IMG_FG = '001'

mapper.select:
/* ProdMapper.getProdImageFileSaveImgFileNm */
SELECT SUBSTR(thpi.IMG_URL, INSTR(thpi.IMG_URL, '\prod_img')) || thpi.IMG_FILE_NM AS PATH_FULL -- SUBSTR(thpi.IMG_URL, INSTR(thpi.IMG_URL, '\A0001')) || thpi.IMG_FILE_NM AS PATH_FULL --로컬 테스트시 FROM TB_HQ_PRODUCT_IMAGE thpi WHERE 1=1 AND thpi.HQ_OFFICE_CD = #{hqOfficeCd} AND thpi.PROD_CD = #{prodCd} AND thpi.IMG_FG = '001'
SELECT SUBSTR(tmpi.IMG_URL, INSTR(tmpi.IMG_URL, '\prod_img')) || tmpi.IMG_FILE_NM AS PATH_FULL -- SUBSTR(tmpi.IMG_URL, INSTR(tmpi.IMG_URL, '\S000002')) || tmpi.IMG_FILE_NM AS PATH_FULL --로컬 테스트시 FROM TB_MS_PRODUCT_IMAGE tmpi WHERE 1=1 AND tmpi.STORE_CD = #{storeCd} AND tmpi.PROD_CD = #{prodCd} AND tmpi.IMG_FG = '001'

