mapper.insert:
/* ProdMapper.getVendorProdSaveInsert */
MERGE INTO TB_HQ_VENDOR_PROD thvp USING DUAL ON (thvp.HQ_OFFICE_CD = #{hqOfficeCd} AND thvp.VENDR_CD = #{vendrCd} AND thvp.PROD_CD = #{prodCd}) WHEN MATCHED THEN UPDATE SET thvp.SPLY_UPRC = #{splyUprc}, thvp.USE_YN = 'Y', thvp.MOD_DT = #{modDt}, thvp.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( HQ_OFFICE_CD, HQ_BRAND_CD, VENDR_CD, PROD_CD, SPLY_UPRC, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, '0000000', --HQ_BRAND_CD #{vendrCd}, #{prodCd}, #{splyUprc}, 'Y', #{regDt}, #{regId}, #{modDt}, #{modId} )
MERGE INTO TB_MS_VENDOR_PROD tmvp USING DUAL ON (tmvp.STORE_CD = #{storeCd} AND tmvp.VENDR_CD = #{vendrCd} AND tmvp.PROD_CD = #{prodCd}) WHEN MATCHED THEN UPDATE SET tmvp.SPLY_UPRC = #{splyUprc}, tmvp.USE_YN = 'Y', tmvp.MOD_DT = #{modDt}, tmvp.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( STORE_CD, VENDR_CD, PROD_CD, SPLY_UPRC, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{vendrCd}, #{prodCd}, #{splyUprc}, 'Y', #{regDt}, #{regId}, #{modDt}, #{modId} )

