/* ProdMapper.getKitchenprintList */ SELECT tms.STORE_CD, tms.STORE_NM, tmp.PRTER_NO, tmp.PRTER_NM FROM TB_MS_STORE tms, TB_MS_PRINTER tmp WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tms.STORE_CD = #{storeCd}
AND tmp.STORE_CD = tms.STORE_CD AND tmp.STORE_CD = tms.STORE_CD ORDER BY tms.STORE_CD, tmp.PRTER_NO

/* ProdMapper.getBrandList */
SELECT HQ_BRAND_CD AS VALUE, HQ_BRAND_NM AS NAME FROM TB_HQ_BRAND WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND USE_YN = 'Y' AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY HQ_BRAND_CD
SELECT MS_BRAND_CD AS VALUE, MS_BRAND_NM AS NAME FROM TB_MS_BRAND WHERE STORE_CD = #{storeCd} AND USE_YN = 'Y' ORDER BY MS_BRAND_CD

/* ProdMapper.getBrandList2 */
SELECT A.VALUE, A.NAME FROM ( SELECT thp.HQ_BRAND_CD AS VALUE, thb.HQ_BRAND_NM AS NAME FROM TB_HQ_PRODUCT thp, TB_HQ_BRAND thb WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD = #{prodCd} AND thb.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thb.HQ_BRAND_CD (+)= thp.HQ_BRAND_CD UNION SELECT HQ_BRAND_CD AS VALUE, HQ_BRAND_NM AS NAME FROM TB_HQ_BRAND WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND USE_YN = 'Y' ) A ORDER BY A.VALUE
SELECT A.VALUE, A.NAME FROM ( SELECT tmp.HQ_BRAND_CD AS VALUE, tmb.MS_BRAND_NM AS NAME FROM TB_MS_PRODUCT tmp, TB_MS_BRAND tmb WHERE tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD = #{prodCd} AND tmb.STORE_CD (+)= tmp.STORE_CD AND tmb.MS_BRAND_CD (+)= tmp.HQ_BRAND_CD UNION SELECT MS_BRAND_CD AS VALUE, MS_BRAND_NM AS NAME FROM TB_MS_BRAND WHERE STORE_CD = #{storeCd} AND USE_YN = 'Y' ) A ORDER BY A.VALUE

/* ProdMapper.getSetConfigProdList */
SELECT thpup.UNIT_PROD_CD, thpup.UNIT_PROD_QTY, thpup.DISP_SEQ, thp.PROD_NM, thp.PO_UNIT_FG, thp.COST_UPRC FROM TB_HQ_PRODUCT_UNITST_PROD thpup, TB_HQ_PRODUCT thp WHERE thpup.HQ_OFFICE_CD = #{hqOfficeCd} AND thpup.PROD_CD = #{prodCd} AND thp.HQ_OFFICE_CD = thpup.HQ_OFFICE_CD AND thp.PROD_CD = thpup.UNIT_PROD_CD ORDER BY thpup.DISP_SEQ
SELECT tmpup.UNIT_PROD_CD, tmpup.UNIT_PROD_QTY, tmpup.DISP_SEQ, tmp.PROD_NM, tmp.PO_UNIT_FG, tmp.COST_UPRC FROM TB_MS_PRODUCT_UNITST_PROD tmpup, TB_MS_PRODUCT tmp WHERE tmpup.STORE_CD = #{storeCd} AND tmpup.PROD_CD = #{prodCd} AND tmp.STORE_CD = tmpup.STORE_CD AND tmp.PROD_CD = tmpup.UNIT_PROD_CD ORDER BY tmpup.DISP_SEQ

/* ProdMapper.getSetConfigProdDispSeq */



        SELECT NVL(MAX(DISP_SEQ), 0) + 1
          FROM TB_HQ_PRODUCT_UNITST_PROD
         WHERE HQ_OFFICE_CD = #{hqOfficeCd}
           AND PROD_CD = #{prodCd}



        SELECT NVL(MAX(DISP_SEQ), 0) + 1
          FROM TB_MS_PRODUCT_UNITST_PROD
         WHERE STORE_CD = #{storeCd}
          AND PROD_CD = #{prodCd}

