/* USE : ProdMapper.deleteProdInfoProdInfo */



      DELETE TB_HQ_PRODUCT_INFO
      WHERE HQ_OFFICE_CD = #{hqOfficeCd}
      AND PROD_CD IN (
                        SELECT PROD_CD
                        FROM TB_TMP_DEL_PRODUCT
