/* USE : ProdMapper.deleteProdInfoAllStore */
DELETE TB_MS_PRODUCT
 WHERE STORE_CD IN (
                      SELECT STORE_CD
                      FROM TB_MS_STORE
                      WHERE HQ_OFFICE_CD = #{hqOfficeCd}
                   )
   AND PROD_CD IN (
                      SELECT PROD_CD
                       FROM TB_TMP_DEL_PRODUCT
                      WHERE SESSION_ID = #{sessionId}
                        AND HQ_OFFICE_CD = #{hqOfficeCd}
                        AND DEL_TYPE_FG = #{delTypeFg}
                  )

mapper.update:
/* ProdMapper.updateProdUseYn */



        UPDATE TB_HQ_PRODUCT
           SET USE_YN = 'N',
               MOD_DT = #{modDt},
               MOD_ID = #{modId}
         WHERE HQ_OFFICE_CD = #{hqOfficeCd}
           AND PROD_CD IN (
                            SELECT PROD_CD
                              FROM TB_TMP_DEL_PRODUCT
                             WHERE SESSION_ID = #{sessionId}
                               AND HQ_OFFICE_CD = #{hqOfficeCd}
                               AND DEL_TYPE_FG = #{delTypeFg}
                           )



        UPDATE TB_MS_PRODUCT
           SET USE_YN = 'N',
               MOD_DT = #{modDt},
               MOD_ID = #{modId}
         WHERE STORE_CD = #{storeCd}
           AND PROD_CD IN (
                            SELECT PROD_CD
                              FROM TB_TMP_DEL_PRODUCT
                             WHERE SESSION_ID = #{sessionId}
                               AND HQ_OFFICE_CD = #{hqOfficeCd}
                               AND STORE_CD = #{storeCd}
                               AND DEL_TYPE_FG = #{delTypeFg}
                           )

/* ProdMapper.updateStoreProdUseYn */
UPDATE TB_MS_PRODUCT
SET USE_YN = 'N',
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE STORE_CD IN (
                    SELECT STORE_CD
                    FROM TB_MS_STORE
                    WHERE HQ_OFFICE_CD = #{hqOfficeCd}
                  )
AND PROD_CD IN (
                  SELECT PROD_CD
                  FROM TB_TMP_DEL_PRODUCT
                  WHERE SESSION_ID = #{sessionId}
                  AND HQ_OFFICE_CD = #{hqOfficeCd}
                  AND DEL_TYPE_FG = #{delTypeFg}
                )

mapper.delete:
/* ProdMapper.deleteAllTmpDelProduct */
DELETE
TB_TMP_DEL_PRODUCT
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{hqOfficeCd}

    AND STORE_CD = #{storeCd}

mapper.select:
/* ProdMapper.getAllProdList */
SELECT thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, thp.PROD_CD, thp.PROD_NM FROM TB_HQ_PRODUCT thp WHERE 1=1 AND thp.HQ_OFFICE_CD = #{hqOfficeCd} AND thp.PROD_CD NOT IN ('0000000000000', '0A0000DLVFEE', '0ADLVFEE0000') AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY thp.PROD_CD
SELECT tmp.STORE_CD, tmp.PROD_CLASS_CD, tmp.PROD_CD, tmp.PROD_NM, tmp.REG_FG FROM TB_MS_PRODUCT tmp WHERE 1=1 AND tmp.STORE_CD = #{storeCd} AND tmp.PROD_CD NOT IN ('0000000000000', '0A0000DLVFEE', '0ADLVFEE0000') AND tmp.REG_FG ='S' AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (tmp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY tmp.PROD_CD

mapper.insert:
/* USE : ProdMapper.insertProdSaleTime */



      INSERT INTO TB_HQ_PRODUCT_SALE_TIME
      (   HQ_OFFICE_CD
        , PROD_CD
        , TIME_SEQ
        , INCLUDE_FG
        , S_SALE_TIME
        , E_SALE_TIME
        , REG_DT
        , REG_ID
        , MOD_DT
        , MOD_ID
      )
      VALUES
      (   #{hqOfficeCd}
        , #{prodCd}
        , #{timeSeq}
        , #{includeFg}
        , #{sSaleTime}
        , #{eSaleTime}
        , #{regDt}
        , #{regId}
        , #{modDt}
        , #{modId}
      )



      INSERT INTO TB_MS_PRODUCT_SALE_TIME
      (   STORE_CD
        , PROD_CD
        , TIME_SEQ
        , INCLUDE_FG
        , S_SALE_TIME
        , E_SALE_TIME
        , REG_DT
        , REG_ID
        , MOD_DT
        , MOD_ID
      )
      VALUES
      (   #{storeCd}
        , #{prodCd}
        , #{timeSeq}
        , #{includeFg}
        , #{sSaleTime}
        , #{eSaleTime}
        , #{regDt}
        , #{regId}
        , #{modDt}
        , #{modId}
      )

mapper.delete:
/* USE : ProdMapper.deleteProdSaleTime */



        DELETE
        FROM  TB_HQ_PRODUCT_SALE_TIME
        WHERE HQ_OFFICE_CD = #{hqOfficeCd}
        AND   PROD_CD = #{prodCd}



        DELETE
        FROM TB_MS_PRODUCT_SALE_TIME
        WHERE STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}

mapper.select:
/* USE : ProdMapper.getProdSaleTime */
SELECT HQ_OFFICE_CD , PROD_CD , TIME_SEQ , INCLUDE_FG , S_SALE_TIME , E_SALE_TIME FROM TB_HQ_PRODUCT_SALE_TIME WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd}
SELECT STORE_CD , PROD_CD , TIME_SEQ , INCLUDE_FG , S_SALE_TIME , E_SALE_TIME FROM TB_MS_PRODUCT_SALE_TIME WHERE STORE_CD = #{storeCd} AND PROD_CD = #{prodCd}

mapper.delete:
/* USE : ProdMapper.deleteStoreProdSaleTime */
DELETE
  FROM TB_MS_PRODUCT_SALE_TIME
 WHERE STORE_CD IN (SELECT STORE_CD FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd})
   AND PROD_CD = #{prodCd}

mapper.select:
/* USE : ProdMapper.getProdSaleTimeFg */
SELECT NVL(MAX(SALE_TIME_FG), 'N') AS SALE_TIME_FG
  FROM TB_HQ_PRODUCT
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND PROD_CD = #{prodCd}

mapper.delete:
/* USE : ProdMapper.deleteProdStoreProdSaleTime */
DELETE
FROM TB_MS_PRODUCT_SALE_TIME
WHERE STORE_CD = #{storeCd}
AND PROD_CD = #{prodCd}

mapper.select:
/* ProdMapper.getProdInfoRowCount */



        SELECT
        COUNT(PROD_CD) AS ROW_COUNT
        FROM TB_HQ_PRODUCT_INFO
        WHERE 1=1
        AND HQ_OFFICE_CD = #{hqOfficeCd}
        AND PROD_CD = #{prodCd}



        SELECT
        COUNT(PROD_CD) AS ROW_COUNT
        FROM TB_MS_PRODUCT_INFO
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}

/* USE : ProdMapper.selectStoreList */
SELECT tms.HQ_OFFICE_CD, tms.STORE_CD, tms.STORE_NM FROM TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND ( 'N' = (SELECT NVL(MAX(ENVST_VAL), 'N') FROM TB_HQ_ENVST WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ENVST_CD = '0001') OR tms.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_EMPLOYEE_STORE_V08 WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND EMP_NO = #{empNo}) )
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
ORDER BY tms.STORE_CD
