        SET
        SALE_PROD_YN = #{saleProdYn},
        POINT_SAVE_YN = #{pointSaveYn},
        PRC_CTRL_FG = #{prcCtrlFg},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
