mapper.select:
/* ProdBatchChangeMapper.getProdBatchChangeList */
SELECT thp.PROD_CD, thp.PROD_NM, FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, thp.PROD_CLASS_CD, thpb.BARCD_CD AS BAR_CD, thp.SALE_PROD_YN, thp.SALE_PROD_YN AS OLD_SALE_PROD_YN, thp.POINT_SAVE_YN, thp.PRC_CTRL_FG, thp.MAP_PROD_CD, thp.HQ_BRAND_CD, thp.VAT_FG, thp.HQ_BRAND_CD AS OLD_HQ_BRAND_CD, thp.POINT_SAVE_YN AS OLD_POINT_SAVE_YN, thp.PRC_CTRL_FG AS OLD_PRC_CTRL_FG, thp.MAP_PROD_CD AS OLD_MAP_PROD_CD, FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS OLD_PATH_NM, thp.VAT_FG AS OLD_VAT_FG
FROM TB_HQ_PRODUCT thp, TB_HQ_PRODUCT_CLASS thpc, TB_HQ_PRODUCT_BARCD thpb WHERE 1=1 AND thp.HQ_OFFICE_CD = #{hqOfficeCd}
AND thp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND thp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY P_PROD_CLASS_CD = PRIOR PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND thpb.BARCD_CD LIKE '%'|| #{barCd} ||'%'
AND thp.SALE_PROD_YN = #{saleProdYn}
AND thp.POINT_SAVE_YN = #{pointSaveYn}
AND thp.PRC_CTRL_FG = #{prcCtrlFg}
AND thp.MAP_PROD_CD LIKE '%'|| #{mapProdCd} ||'%'
AND thp.HQ_BRAND_CD = #{hqBrandCd}
AND thp.VAT_FG = #{vatFg}
AND thpc.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpc.PROD_CLASS_CD = thp.PROD_CLASS_CD AND thpb.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thpb.PROD_CD (+)= thp.PROD_CD ORDER BY thp.PROD_CD
SELECT tmp.PROD_CD, tmp.PROD_NM, FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, tmp.PROD_CLASS_CD, tmpb.BARCD_CD AS BAR_CD, tmp.SALE_PROD_YN, tmp.SALE_PROD_YN AS OLD_SALE_PROD_YN, tmp.POINT_SAVE_YN, tmp.PRC_CTRL_FG, tmp.REG_FG, tmp.MAP_PROD_CD, tmp.POINT_SAVE_YN AS OLD_POINT_SAVE_YN, tmp.PRC_CTRL_FG AS OLD_PRC_CTRL_FG, tmp.MAP_PROD_CD AS OLD_MAP_PROD_CD, tmp.HQ_BRAND_CD, tmp.HQ_BRAND_CD AS OLD_HQ_BRAND_CD, tmp.VAT_FG, tmp.VAT_FG AS OLD_VAT_FG
FROM TB_MS_PRODUCT tmp, TB_MS_PRODUCT_CLASS tmpc, TB_MS_PRODUCT_BARCD tmpb WHERE 1=1 AND tmp.STORE_CD = #{storeCd}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE 1=1 AND STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY P_PROD_CLASS_CD = PRIOR PROD_CLASS_CD AND STORE_CD = #{storeCd} )
AND tmpb.BARCD_CD LIKE '%'|| #{barCd} ||'%'
AND tmp.SALE_PROD_YN = #{saleProdYn}
AND tmp.POINT_SAVE_YN = #{pointSaveYn}
AND tmp.PRC_CTRL_FG = #{prcCtrlFg}
AND tmp.REG_FG = #{regFg}
AND tmp.MAP_PROD_CD LIKE '%'|| #{mapProdCd} ||'%'
AND tmp.HQ_BRAND_CD = #{hqBrandCd}
AND tmp.VAT_FG = #{vatFg}
AND tmpc.STORE_CD = tmp.STORE_CD AND tmpc.PROD_CLASS_CD = tmp.PROD_CLASS_CD AND tmpb.STORE_CD (+)= tmp.STORE_CD AND tmpb.PROD_CD (+)= tmp.PROD_CD ORDER BY tmp.PROD_CD

mapper.update:
/* ProdBatchChangeMapper.getProdBatchChangeSaveUpdate */
UPDATE TB_HQ_PRODUCT SET SALE_PROD_YN = #{saleProdYn}, POINT_SAVE_YN = #{pointSaveYn}, PRC_CTRL_FG = #{prcCtrlFg}, MAP_PROD_CD = TRIM(REPLACE(REPLACE(#{mapProdCd}, CHR(10), NULL), CHR(13), NULL)), VAT_FG = #{vatFg}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd}
UPDATE TB_MS_PRODUCT SET SALE_PROD_YN = #{saleProdYn}, POINT_SAVE_YN = #{pointSaveYn}, PRC_CTRL_FG = #{prcCtrlFg}, MAP_PROD_CD = TRIM(REPLACE(REPLACE(#{mapProdCd}, CHR(10), NULL), CHR(13), NULL)), VAT_FG = #{vatFg}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE 1=1 AND STORE_CD = #{storeCd} AND PROD_CD = #{prodCd}

/* ProdBatchChangeMapper.getProdBatchChange2SaveUpdate */



        UPDATE
        TB_HQ_PRODUCT
        SET
        HQ_BRAND_CD = #{hqBrandCd},
        PROD_CLASS_CD = #{prodClassCd},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND HQ_OFFICE_CD = #{hqOfficeCd}
        AND PROD_CD = #{prodCd}



        UPDATE
        TB_MS_PRODUCT
        SET
        HQ_BRAND_CD = #{hqBrandCd},
        PROD_CLASS_CD = #{prodClassCd},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND PROD_CD = #{prodCd}

/* ProdBatchChangeMapper.getProdBatchChange2SaveStoreUpdate */
    UPDATE
    TB_MS_PRODUCT
    SET
    HQ_BRAND_CD = #{hqBrandCd},
    PROD_CLASS_CD = #{prodClassCd},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
    WHERE 1=1
    AND STORE_CD IN (
                        SELECT STORE_CD
                        FROM TB_MS_STORE tms
                        WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
                    )
    AND PROD_CD = #{prodCd}

