mapper.select:
/* USE : ProdImgMapper.getProdImg */
SELECT HQ_OFFICE_CD, PROD_CD, IMG_FG, IMG_URL, IMG_FILE_NM, IMG_CHG_DT, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID FROM TB_HQ_PRODUCT_IMAGE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND PROD_CD = #{prodCd}
SELECT STORE_CD, PROD_CD, IMG_FG, IMG_URL, IMG_FILE_NM, IMG_CHG_DT, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID FROM TB_MS_PRODUCT_IMAGE WHERE STORE_CD = #{storeCd} AND PROD_CD = #{prodCd}

/* ProdImgMapper.getProdImgChk */



        SELECT IMG_FILE_NM
          FROM TB_HQ_PRODUCT_IMAGE
         WHERE HQ_OFFICE_CD = #{hqOfficeCd}
           AND PROD_CD = #{prodCd}
           AND IMG_FG = #{imgFg}



        SELECT IMG_FILE_NM
          FROM TB_MS_PRODUCT_IMAGE
         WHERE STORE_CD = #{storeCd}
           AND PROD_CD = #{prodCd}
           AND IMG_FG = #{imgFg}

mapper.insert:
/* ProdImgMapper.saveProdImg */
INSERT INTO TB_HQ_PRODUCT_IMAGE ( HQ_OFFICE_CD, PROD_CD, IMG_FG, IMG_URL, IMG_FILE_NM, IMG_CHG_DT, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{prodCd}, #{imgFg}, #{imgUrl}, #{imgFileNm}, #{imgChgDt}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId} )
INSERT INTO TB_MS_PRODUCT_IMAGE( STORE_CD, PROD_CD, IMG_FG, IMG_URL, IMG_FILE_NM, IMG_CHG_DT, REMARK, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, #{prodCd}, #{imgFg}, #{imgUrl}, #{imgFileNm}, #{imgChgDt}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* ProdImgMapper.updateProgImg */



            UPDATE TB_HQ_PRODUCT_IMAGE
               SET IMG_URL = #{imgUrl},
                    IMG_FILE_NM = #{imgFileNm},
                    IMG_CHG_DT = #{imgChgDt},
                    MOD_DT = #{modDt},
                    MOD_ID = #{modId}
             WHERE HQ_OFFICE_CD = #{hqOfficeCd}
               AND PROD_CD = #{prodCd}
               AND IMG_FG = #{imgFg}



            UPDATE TB_MS_PRODUCT_IMAGE
               SET IMG_URL = #{imgUrl},
                    IMG_FILE_NM = #{imgFileNm},
                    IMG_CHG_DT = #{imgChgDt},
                    MOD_DT = #{modDt},
                    MOD_ID = #{modId}
             WHERE STORE_CD = #{storeCd}
               AND PROD_CD = #{prodCd}
               AND IMG_FG = #{imgFg}

mapper.delete:
/* ProdImgMapper.delProdImg */



            DELETE TB_HQ_PRODUCT_IMAGE
             WHERE HQ_OFFICE_CD = #{hqOfficeCd}
               AND PROD_CD = #{prodCd}
               AND IMG_FG = #{imgFg}



            DELETE TB_MS_PRODUCT_IMAGE
             WHERE STORE_CD = #{storeCd}
               AND PROD_CD = #{prodCd}
               AND IMG_FG = #{imgFg}
