mapper.select:
/* USE : ProdKitchenprintLinkMapper.getProdList */ SELECT thp.PROD_CD, thp.PROD_NM, NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC -- 판매단가 FROM TB_HQ_PRODUCT thp LEFT OUTER JOIN ( SELECT HQ_OFFICE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp ON thp.HQ_OFFICE_CD = thpsp.HQ_OFFICE_CD AND thp.PROD_CD = thpsp.PROD_CD WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd}
AND thp.REG_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND thp.PROD_CD LIKE '%'||#{prodCd}||'%' --상품코드
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%' --상품명
AND EXISTS ( SELECT 1 FROM TB_HQ_PRODUCT_BARCD WHERE HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND PROD_CD = thp.PROD_CD AND BARCD_CD = #{barCd} ) --바코드
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND thp.USE_YN = #{useYn} --사용여부
AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) ORDER BY thp.PROD_CD ASC

/* USE : ProdKitchenprintLinkMapper.getLinkedList */
SELECT tms.STORE_CD,
       tms.STORE_NM,
       tmpp.PRTER_NO,
       TMP.PRTER_NM
FROM TB_MS_PRINTER_PROD tmpp, TB_MS_STORE tms, TB_MS_PRINTER tmp
WHERE tmpp.STORE_CD = tms.STORE_CD
  AND tmpp.STORE_CD = tmp.STORE_CD
  AND tmpp.PRTER_no = tmp.PRTER_no
  AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
  AND PROD_CD = #{prodCd}

  AND tms.STORE_CD IN

      #{item}



  AND tms.SYS_STAT_FG = #{sysStatFg}

ORDER BY tms.STORE_CD, tmpp.PRTER_NO

mapper.delete:
DELETE TB_MS_PRINTER_PROD tmpp
  WHERE STORE_CD LIKE #{storeCd}
    AND PRTER_NO = #{prterNo}
    AND PROD_CD = #{prodCd}

mapper.select:
/* USE : ProdKitchenprintLinkMapper.getUnlinkList */ SELECT tms.STORE_CD, tms.STORE_NM, tmp2.PRTER_NO, tmp2.PRTER_NM FROM TB_MS_STORE tms, TB_MS_PRODUCT tmp, TB_MS_PRINTER tmp2 WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmp.STORE_CD = tms.STORE_CD AND tmp.PROD_CD = #{prodCd} AND tmp.USE_YN = 'Y' AND tmp2.STORE_CD = tms.STORE_CD AND (tmp2.STORE_CD, tmp2.PRTER_NO) NOT IN ( SELECT STORE_CD , PRTER_NO FROM TB_MS_PRINTER_PROD tmpp WHERE tmpp.STORE_CD = tms.STORE_CD AND tmpp.PROD_CD = #{prodCd} )
AND tms.STORE_CD IN
#{item}
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.STORE_CD, tmp2.PRTER_NO

/* USE : ProdKitchenprintLinkMapper.getPrinterGroupList */ SELECT PRINTER_GROUP_CD, PRINTER_GROUP_NM, '추가' AS ADD_PROD, '추가' AS ADD_PRINTER FROM TB_HQ_PRINTER_GROUP thpg WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND PRINTER_GROUP_NM LIKE '%'||#{printerGroupNm}||'%' --그룹명
ORDER BY PRINTER_GROUP_CD

/* USE : ProdKitchenprintLinkMapper.getPrinterGroupCode */
SELECT LPAD(NVL(MAX(PRINTER_GROUP_CD), '0') + 1, 3, '0')
FROM TB_HQ_PRINTER_GROUP
WHERE HQ_OFFICE_CD = #{hqOfficeCd}

mapper.insert:
/* USE : ProdKitchenprintLinkMapper.savePrinterGroup */ MERGE INTO TB_HQ_PRINTER_GROUP A USING DUAL ON ( A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.PRINTER_GROUP_CD = #{printerGroupCd})
WHEN MATCHED THEN UPDATE SET A.PRINTER_GROUP_NM = #{printerGroupNm}, A.MOD_DT = #{modDt}, A.MOD_ID = #{modId}
WHEN NOT MATCHED THEN INSERT ( A.HQ_OFFICE_CD, A.PRINTER_GROUP_CD, A.PRINTER_GROUP_NM, A.REMARK, A.REG_DT, A.REG_ID, A.MOD_DT, A.MOD_ID ) VALUES ( #{hqOfficeCd}, #{printerGroupCd}, #{printerGroupNm}, #{remark}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.select:
/* USE : ProdKitchenprintLinkMapper.getProdMapping */
SELECT 	thpgp.PROD_CD,
        thp.PROD_NM
FROM 	TB_HQ_PRINTER_GROUP_PROD thpgp,
        TB_HQ_PRODUCT thp
WHERE 	thpgp.HQ_OFFICE_CD = #{hqOfficeCd}
AND     thpgp.PRINTER_GROUP_CD = #{printerGroupCd}
AND		thpgp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD
AND		thpgp.PROD_CD = thp.PROD_CD
ORDER BY thpgp.PROD_CD

mapper.delete:
/* USE : ProdKitchenprintLinkMapper.deleteProdMapping */
DELETE  TB_HQ_PRINTER_GROUP_PROD
WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
AND     PRINTER_GROUP_CD = #{printerGroupCd}
AND     PROD_CD = #{prodCd}

/* USE : ProdKitchenprintLinkMapper.deletePrinterMapping */
DELETE  TB_HQ_PRINTER_GROUP_STORE_PRINT
WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
AND     PRINTER_GROUP_CD = #{printerGroupCd}
AND     STORE_CD = #{storeCd}
AND     PRTER_NO = #{prterNo}
