mapper.select:
/* USE : ProdOptionMapper.getProdOptionGroup */
SELECT OPTION_GRP_CD , OPTION_GRP_NM , USE_YN , (SELECT COUNT(*) FROM TB_HQ_PRODUCT_OPTION_VAL thpov WHERE thpov.HQ_OFFICE_CD = thpog.HQ_OFFICE_CD AND thpov.OPTION_GRP_CD = thpog.OPTION_GRP_CD) AS CNT FROM TB_HQ_PRODUCT_OPTION_GROUP thpog WHERE HQ_OFFICE_CD = #{hqOfficeCd} ORDER BY OPTION_GRP_CD
SELECT OPTION_GRP_CD , OPTION_GRP_NM , USE_YN , (SELECT COUNT(*) FROM TB_MS_PRODUCT_OPTION_VAL tmpov WHERE tmpov.STORE_CD = tmpog.STORE_CD AND tmpov.OPTION_GRP_CD = tmpog.OPTION_GRP_CD) AS CNT FROM TB_MS_PRODUCT_OPTION_GROUP tmpog WHERE STORE_CD = #{storeCd} ORDER BY OPTION_GRP_CD

/* USE : ProdOptionMapper.getProdOptionGroupCode */



        SELECT  LPAD(NVL(MAX(OPTION_GRP_CD), '0') + 1, 4, '0')
        FROM    TB_HQ_PRODUCT_OPTION_GROUP
        WHERE   HQ_OFFICE_CD = #{hqOfficeCd}



        SELECT  LPAD(NVL(MAX(OPTION_GRP_CD), '7999') + 1, 4, '0')
        FROM    TB_MS_PRODUCT_OPTION_GROUP
        WHERE   STORE_CD = #{storeCd}
        AND     OPTION_GRP_CD > 7999

mapper.insert:
/* USE : ProdOptionMapper.saveProdOptionGroup */
MERGE INTO TB_HQ_PRODUCT_OPTION_GROUP A USING DUAL ON ( A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.OPTION_GRP_CD = #{optionGrpCd}) WHEN MATCHED THEN UPDATE SET A.OPTION_GRP_NM = #{optionGrpNm}, A.USE_YN = #{useYn}, A.MOD_DT = #{modDt}, A.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( A.HQ_OFFICE_CD, A.OPTION_GRP_CD, A.OPTION_GRP_NM, A.USE_YN, A.REG_DT, A.REG_ID, A.MOD_DT, A.MOD_ID ) VALUES ( #{hqOfficeCd}, #{optionGrpCd}, #{optionGrpNm}, #{useYn}, #{regDt}, #{regId}, #{modDt}, #{modId} )
MERGE INTO TB_MS_PRODUCT_OPTION_GROUP A USING DUAL ON ( A.STORE_CD = #{storeCd} AND A.OPTION_GRP_CD = #{optionGrpCd}) WHEN MATCHED THEN UPDATE SET A.OPTION_GRP_NM = #{optionGrpNm}, A.USE_YN = #{useYn}, A.MOD_DT = #{modDt}, A.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( A.STORE_CD, A.OPTION_GRP_CD, A.OPTION_GRP_NM, A.USE_YN, A.REG_FG, A.REG_DT, A.REG_ID, A.MOD_DT, A.MOD_ID ) VALUES ( #{storeCd}, #{optionGrpCd}, #{optionGrpNm}, #{useYn}, #{regFg}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.delete:
/* USE : ProdOptionMapper.deleteProdOptionGroup */



        DELETE  TB_HQ_PRODUCT_OPTION_GROUP thpog
        WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
        AND     OPTION_GRP_CD = #{optionGrpCd}



        DELETE  TB_MS_PRODUCT_OPTION_GROUP tmpog
        WHERE   STORE_CD = #{storeCd}
        AND     OPTION_GRP_CD = #{optionGrpCd}

mapper.select:
/* USE : ProdOptionMapper.getProdOptionVal */
SELECT thpov.OPTION_GRP_CD , thpov.OPTION_VAL_CD , thpov.OPTION_VAL_NM , thpov.OPT_PROD_CD , thp.PROD_NM FROM TB_HQ_PRODUCT_OPTION_VAL thpov , TB_HQ_PRODUCT thp WHERE thpov.HQ_OFFICE_CD = #{hqOfficeCd} AND thpov.OPTION_GRP_CD = #{optionGrpCd} AND thp.HQ_OFFICE_CD (+)= thpov.HQ_OFFICE_CD AND thp.PROD_CD (+)= thpov.OPT_PROD_CD ORDER BY thpov.OPTION_VAL_CD
SELECT tmpov.OPTION_GRP_CD , tmpov.OPTION_VAL_CD , tmpov.OPTION_VAL_NM , tmpov.OPT_PROD_CD , tmp.PROD_NM FROM TB_MS_PRODUCT_OPTION_VAL tmpov , TB_MS_PRODUCT tmp WHERE tmpov.STORE_CD = #{storeCd} AND tmpov.OPTION_GRP_CD = #{optionGrpCd} AND tmp.STORE_CD (+)= tmpov.STORE_CD AND tmp.PROD_CD (+)= tmpov.OPT_PROD_CD ORDER BY tmpov.OPTION_VAL_CD

/* USE : ProdOptionMapper.getProdOptionValCode */



        SELECT  LPAD(NVL(MAX(OPTION_VAL_CD), '0') + 1, 2, '0')
        FROM    TB_HQ_PRODUCT_OPTION_VAL
        WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
        AND     OPTION_GRP_CD = #{optionGrpCd}



        SELECT  LPAD(NVL(MAX(OPTION_VAL_CD), '0') + 1, 2, '0')
        FROM    TB_MS_PRODUCT_OPTION_VAL
        WHERE   STORE_CD = #{storeCd}
        AND     OPTION_GRP_CD = #{optionGrpCd}

mapper.insert:
/* USE : ProdOptionMapper.saveProdOptionVal */
MERGE INTO TB_HQ_PRODUCT_OPTION_VAL A USING DUAL ON ( A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.OPTION_GRP_CD = #{optionGrpCd} AND A.OPTION_VAL_CD = #{optionValCd}) WHEN MATCHED THEN UPDATE SET A.OPTION_VAL_NM = #{optionValNm}, A.MOD_DT = #{modDt}, A.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( A.HQ_OFFICE_CD, A.OPTION_GRP_CD, A.OPTION_VAL_CD, A.OPTION_VAL_NM, A.REG_DT, A.REG_ID, A.MOD_DT, A.MOD_ID, A.OPT_PROD_CD ) VALUES ( #{hqOfficeCd}, #{optionGrpCd}, #{optionValCd}, #{optionValNm}, #{regDt}, #{regId}, #{modDt}, #{modId}, #{optProdCd} )
MERGE INTO TB_MS_PRODUCT_OPTION_VAL A USING DUAL ON ( A.STORE_CD = #{storeCd} AND A.OPTION_GRP_CD = #{optionGrpCd} AND A.OPTION_VAL_CD = #{optionValCd}) WHEN MATCHED THEN UPDATE SET A.OPTION_VAL_NM = #{optionValNm}, A.MOD_DT = #{modDt}, A.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( A.STORE_CD, A.OPTION_GRP_CD, A.OPTION_VAL_CD, A.OPTION_VAL_NM, A.REG_FG, A.REG_DT, A.REG_ID, A.MOD_DT, A.MOD_ID, A.OPT_PROD_CD ) VALUES ( #{storeCd}, #{optionGrpCd}, #{optionValCd}, #{optionValNm}, #{regFg}, #{regDt}, #{regId}, #{modDt}, #{modId}, #{optProdCd} )

mapper.delete:
/* USE : ProdOptionMapper.deleteProdOptionVal */



        DELETE  TB_HQ_PRODUCT_OPTION_VAL
        WHERE   HQ_OFFICE_CD = #{hqOfficeCd}
        AND     OPTION_GRP_CD = #{optionGrpCd}
        AND     OPTION_VAL_CD = #{optionValCd}



        DELETE  TB_MS_PRODUCT_OPTION_VAL
        WHERE   STORE_CD = #{storeCd}
        AND     OPTION_GRP_CD = #{optionGrpCd}
        AND     OPTION_VAL_CD = #{optionValCd}
