SELECT thpsc.SDSEL_GRP_CD, thpsp.SDSEL_CLASS_CD, thpsp.PROD_CD, thp.PROD_NM, thpsp.ADD_PROD_UPRC, thpsp.ADD_PROD_QTY, thpsp.DISP_SEQ, NVL(thpsp.FIX_PROD_FG, '0') AS FIX_PROD_FG FROM TB_HQ_PRODUCT_SDSEL_PROD thpsp LEFT OUTER JOIN TB_HQ_PRODUCT thp ON thpsp.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpsp.PROD_CD = thp.PROD_CD AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (thp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) INNER JOIN TB_HQ_PRODUCT_SDSEL_CLASS thpsc ON thpsc.HQ_OFFICE_CD = thpsp.HQ_OFFICE_CD AND thpsc.SDSEL_CLASS_CD = thpsp.SDSEL_CLASS_CD WHERE thpsp.HQ_OFFICE_CD = #{hqOfficeCd} AND thpsp.SDSEL_CLASS_CD = #{sdselClassCd} AND thpsp.USE_YN = 'Y' ORDER BY thpsp.DISP_SEQ
SELECT tmpsc.SDSEL_GRP_CD, tmpsp.SDSEL_CLASS_CD, tmpsp.PROD_CD, tmp.PROD_NM, tmpsp.ADD_PROD_UPRC, tmpsp.ADD_PROD_QTY, tmpsp.DISP_SEQ, NVL(tmpsp.FIX_PROD_FG, '0') AS FIX_PROD_FG FROM TB_MS_PRODUCT_SDSEL_PROD tmpsp LEFT OUTER JOIN TB_MS_PRODUCT tmp ON tmpsp.STORE_CD = tmp.STORE_CD AND tmpsp.PROD_CD = tmp.PROD_CD AND ( ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) != 0) AND (tmp.HQ_BRAND_CD IN (SELECT HQ_BRAND_CD FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId})) ) OR ( ((SELECT COUNT(HQ_BRAND_CD) FROM TB_WB_USER_BRAND WHERE USER_ID = #{userId}) = 0) AND 1=1 ) ) INNER JOIN TB_MS_PRODUCT_SDSEL_CLASS tmpsc ON tmpsc.STORE_CD = tmpsp.STORE_CD AND tmpsc.SDSEL_CLASS_CD = tmpsp.SDSEL_CLASS_CD WHERE tmpsp.STORE_CD = #{storeCd} AND tmpsp.SDSEL_CLASS_CD = #{sdselClassCd} AND tmpsp.USE_YN = 'Y' ORDER BY tmpsp.DISP_SEQ

mapper.insert:
/* USE : SideMenuMapper.insertMenuProdList */
MERGE INTO TB_HQ_PRODUCT_SDSEL_PROD thpsp USING DUAL ON ( thpsp.HQ_OFFICE_CD = #{hqOfficeCd} AND thpsp.SDSEL_CLASS_CD = #{sdselClassCd} AND thpsp.PROD_CD = #{prodCd} ) WHEN MATCHED THEN UPDATE SET thpsp.USE_YN = 'Y', thpsp.MOD_DT = #{modDt}, thpsp.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( HQ_OFFICE_CD, SDSEL_CLASS_CD, PROD_CD, ADD_PROD_UPRC, ADD_PROD_QTY, DISP_SEQ, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID, FIX_PROD_FG ) VALUES ( #{hqOfficeCd}, #{sdselClassCd}, #{prodCd}, #{addProdUprc}, #{addProdQty}, (SELECT NVL(MAX(DISP_SEQ), 0) +1 FROM TB_HQ_PRODUCT_SDSEL_PROD WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND SDSEL_CLASS_CD = #{sdselClassCd} AND USE_YN='Y'), -- 임시수정 'Y', #{regDt}, #{regId}, #{modDt}, #{modId}, #{fixProdFg} )
MERGE INTO TB_MS_PRODUCT_SDSEL_PROD tmpsp USING DUAL ON ( tmpsp.STORE_CD = #{storeCd} AND tmpsp.SDSEL_CLASS_CD = #{sdselClassCd} AND tmpsp.PROD_CD = #{prodCd} ) WHEN MATCHED THEN UPDATE SET tmpsp.USE_YN = 'Y', tmpsp.MOD_DT = #{modDt}, tmpsp.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( STORE_CD, SDSEL_CLASS_CD, PROD_CD, ADD_PROD_UPRC, ADD_PROD_QTY, DISP_SEQ, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID, FIX_PROD_FG ) VALUES ( #{storeCd}, #{sdselClassCd}, #{prodCd}, #{addProdUprc}, #{addProdQty}, (SELECT NVL(MAX(DISP_SEQ), 0) +1 FROM TB_MS_PRODUCT_SDSEL_PROD WHERE STORE_CD = #{storeCd} AND SDSEL_CLASS_CD = #{sdselClassCd} AND USE_YN='Y'), -- 임시수정 'Y', #{regDt}, #{regId}, #{modDt}, #{modId}, #{fixProdFg} )

