/* SideMenuMapper.getSideMenuManageProdList */
SELECT thp.PROD_CD, thp.PROD_NM, FN_GET_PROD_CLASS_CD_NM_STR(thp.HQ_OFFICE_CD, thp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, thp.PROD_CLASS_CD, thp.HQ_BRAND_CD, thb.HQ_BRAND_NM, thp.SALE_PROD_YN, thp.ORGPLCE_CD, thp.STOCK_PROD_YN, thp.VAT_FG, thp.SIDE_PROD_YN, thp.SDATTR_CLASS_CD, thpsc.SDATTR_CLASS_NM, thp.SDSEL_GRP_CD, thpsg.SDSEL_GRP_NM, thp.USE_YN, thp.PO_PROD_FG, thp.PROD_TIP_YN
FROM TB_HQ_PRODUCT thp, TB_HQ_PRODUCT_CLASS thpc, TB_HQ_BRAND thb, TB_HQ_PRODUCT_SDATTR_CLASS thpsc, TB_HQ_PRODUCT_SDSEL_GROUP thpsg WHERE 1=1 AND thp.HQ_OFFICE_CD = #{hqOfficeCd}
AND thp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND thp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND thp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_HQ_PRODUCT_CLASS WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND HQ_OFFICE_CD = #{hqOfficeCd} CONNECT BY P_PROD_CLASS_CD = PRIOR PROD_CLASS_CD AND HQ_OFFICE_CD = #{hqOfficeCd} )
AND thp.HQ_BRAND_CD = #{hqBrandCd}
AND thp.SIDE_PROD_YN = #{sideProdYn}
AND thp.SDATTR_CLASS_CD IS NULL
AND thp.SDSEL_GRP_CD IS NULL
AND thp.SDATTR_CLASS_CD = #{sdattrClassCd}
AND thp.SDSEL_GRP_CD = #{sdselGrpCd}
AND thpc.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thpc.PROD_CLASS_CD = thp.PROD_CLASS_CD AND thb.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thb.HQ_BRAND_CD (+)= thp.HQ_BRAND_CD AND thpsc.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thpsc.SDATTR_CLASS_CD (+)= thp.SDATTR_CLASS_CD AND thpsg.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thpsg.SDSEL_GRP_CD (+)= thp.SDSEL_GRP_CD ORDER BY thp.PROD_CD
SELECT tmp.PROD_CD, tmp.PROD_NM, FN_GET_PROD_CLASS_CD_NM_STR(tmp.STORE_CD, tmp.PROD_CLASS_CD, 'NM_STR') AS PATH_NM, tmp.PROD_CLASS_CD, tmp.HQ_BRAND_CD, tmb.MS_BRAND_NM, tmp.SALE_PROD_YN, tmp.ORGPLCE_CD, tmp.STOCK_PROD_YN, tmp.VAT_FG, tmp.SIDE_PROD_YN, tmp.SDATTR_CLASS_CD, tmpsc.SDATTR_CLASS_NM, tmp.SDSEL_GRP_CD, tmpsg.SDSEL_GRP_NM, tmp.USE_YN, tmp.PO_PROD_FG, tmp.PROD_TIP_YN, tmp.REG_FG
FROM TB_MS_PRODUCT tmp, TB_MS_PRODUCT_CLASS tmpc, TB_MS_BRAND tmb, TB_MS_PRODUCT_SDATTR_CLASS tmpsc, TB_MS_PRODUCT_SDSEL_GROUP tmpsg WHERE 1=1 AND tmp.STORE_CD = #{storeCd}
AND tmp.PROD_CD LIKE '%'|| #{prodCd} ||'%'
AND tmp.PROD_NM LIKE '%'|| #{prodNm} ||'%'
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE 1=1 AND STORE_CD = #{storeCd} START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = #{storeCd} CONNECT BY P_PROD_CLASS_CD = PRIOR PROD_CLASS_CD AND STORE_CD = #{storeCd} )
AND tmp.HQ_BRAND_CD = #{hqBrandCd}
AND tmp.SIDE_PROD_YN = #{sideProdYn}
AND tmp.SDATTR_CLASS_CD IS NULL
AND tmp.SDSEL_GRP_CD IS NULL
AND tmp.SDATTR_CLASS_CD = #{sdattrClassCd}
AND tmp.SDSEL_GRP_CD = #{sdselGrpCd}
AND tmpc.STORE_CD = tmp.STORE_CD AND tmpc.PROD_CLASS_CD = tmp.PROD_CLASS_CD AND tmb.STORE_CD (+)= tmp.STORE_CD AND tmb.MS_BRAND_CD (+)= tmp.HQ_BRAND_CD AND tmpsc.STORE_CD (+)= tmp.STORE_CD AND tmpsc.SDATTR_CLASS_CD (+)= tmp.SDATTR_CLASS_CD AND tmpsg.STORE_CD (+)= tmp.STORE_CD AND tmpsg.SDSEL_GRP_CD (+)= tmp.SDSEL_GRP_CD ORDER BY tmp.PROD_CD

mapper.update:
/* SideMenuMapper.saveSideMenuManageProdBatch */



        UPDATE TB_HQ_PRODUCT
           SET SIDE_PROD_YN = #{sideProdYn},
                SDATTR_CLASS_CD = #{sdattrClassCd},
                SDSEL_GRP_CD = #{sdselGrpCd},
                MOD_DT = #{modDt},
                MOD_ID = #{modId}
         WHERE 1=1
           AND HQ_OFFICE_CD = #{hqOfficeCd}
           AND PROD_CD = #{prodCd}



        UPDATE TB_MS_PRODUCT
           SET SIDE_PROD_YN = #{sideProdYn},
                SDATTR_CLASS_CD = #{sdattrClassCd},
                SDSEL_GRP_CD = #{sdselGrpCd},
                MOD_DT = #{modDt},
                MOD_ID = #{modId}
         WHERE 1=1
           AND STORE_CD = #{storeCd}
           AND PROD_CD = #{prodCd}

/* SideMenuMapper.saveSideMenuManageProdBatchStoreUpdate */
    UPDATE TB_MS_PRODUCT
       SET SIDE_PROD_YN = #{sideProdYn},
            SDATTR_CLASS_CD = #{sdattrClassCd},
            SDSEL_GRP_CD = #{sdselGrpCd},
            MOD_DT = #{modDt},
            MOD_ID = #{modId}
     WHERE 1=1
       AND STORE_CD IN (
                          SELECT STORE_CD
                            FROM TB_MS_STORE tms
                           WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
                    )
       AND PROD_CD = #{prodCd}

mapper.select:
/* USE : SideMenuMapper.getSideMenuAttrClassCombo */
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT thpsc.SDATTR_CLASS_CD AS VALUE, thpsc.SDATTR_CLASS_NM AS NAME FROM TB_HQ_PRODUCT_SDATTR_CLASS thpsc WHERE thpsc.HQ_OFFICE_CD = #{hqOfficeCd} AND thpsc.USE_YN = 'Y' ORDER BY VALUE NULLS FIRST, VALUE ASC
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT tmpsc.SDATTR_CLASS_CD AS VALUE, tmpsc.SDATTR_CLASS_NM AS NAME FROM TB_MS_PRODUCT_SDATTR_CLASS tmpsc WHERE tmpsc.STORE_CD = #{storeCd} AND tmpsc.USE_YN = 'Y' ORDER BY VALUE NULLS FIRST, VALUE ASC

/* USE : SideMenuMapper.getSideMenuSdselGrpCdCombo */
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT thpsg.SDSEL_GRP_CD AS VALUE, thpsg.SDSEL_GRP_NM AS NAME FROM TB_HQ_PRODUCT_SDSEL_GROUP thpsg WHERE thpsg.HQ_OFFICE_CD = #{hqOfficeCd} AND thpsg.USE_YN = 'Y' ORDER BY VALUE NULLS FIRST, VALUE ASC
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT tmpsg.SDSEL_GRP_CD AS VALUE, tmpsg.SDSEL_GRP_NM AS NAME FROM TB_MS_PRODUCT_SDSEL_GROUP tmpsg WHERE tmpsg.STORE_CD = #{storeCd} AND tmpsg.USE_YN = 'Y' ORDER BY VALUE NULLS FIRST, VALUE ASC
