AND A.SDSEL_GRP_CD LIKE '%'||#{sdselGrpCd}||'%'
AND A.SDSEL_GRP_NM LIKE '%'||#{sdselGrpNm}||'%'
AND B.SDSEL_CLASS_CD LIKE '%'||#{sdselClassCd}||'%'
AND B.SDSEL_CLASS_NM LIKE '%'||#{sdselClassNm}||'%'
AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' AND NVL(B.REG_STORE_FG, '0') IN ('1', '2') AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.SDSEL_GRP_CD = A.SDSEL_GRP_CD AND C.STORE_CD (+)= #{storeCd} AND C.HQ_OFFICE_CD (+)= B.HQ_OFFICE_CD AND C.SDSEL_CLASS_CD (+)= B.SDSEL_CLASS_CD ORDER BY B.DISP_SEQ ) WHERE 1=1
