AND REG_YN = #{regYn}

/* SideMenuStoreMapper.getSideMenuClassRegStoreList */
SELECT A.SDSEL_GRP_CD, A.SDSEL_GRP_NM, B.SDSEL_CLASS_CD, B.SDSEL_CLASS_NM, NVL(B.REQUIRE_YN, 'N') AS REQUIRE_YN, NVL(B.REG_STORE_FG, '0') AS REG_STORE_FG, NVL(B.REG_STORE_FG, '0') AS OLD_REG_STORE_FG
FROM TB_HQ_PRODUCT_SDSEL_GROUP A, TB_HQ_PRODUCT_SDSEL_CLASS B WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.SDSEL_GRP_CD LIKE '%'||#{sdselGrpCd}||'%'
AND A.SDSEL_GRP_NM LIKE '%'||#{sdselGrpNm}||'%'
AND B.SDSEL_CLASS_CD LIKE '%'||#{sdselClassCd}||'%'
AND B.SDSEL_CLASS_NM LIKE '%'||#{sdselClassNm}||'%'
AND NVL(B.REG_STORE_FG, '0') = #{regStoreFg}
AND A.USE_YN = 'Y' AND B.USE_YN = 'Y' AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.SDSEL_GRP_CD = A.SDSEL_GRP_CD ORDER BY B.DISP_SEQ

