mapper.delete:
/* SimpleProdMapper.getSimpleProdCheckDeleteAll */
DELETE
TB_TMP_PRODUCT
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{membrOrgnCd}


        AND STORE_CD = #{storeCd}

mapper.select:
/* SimpleProdMapper.getProdCd */
SELECT
NVL(MAX(PROD_CD),0) + 1 AS PROD_CD
FROM TB_TMP_PRODUCT
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{membrOrgnCd}


        AND STORE_CD = #{storeCd}

/* SimpleProdMapper.getProdNmCnt */ SELECT COUNT(1) AS CNT FROM ( SELECT PROD_NM FROM TB_HQ_PRODUCT WHERE 1=1 AND HQ_OFFICE_CD = #{membrOrgnCd} UNION ALL SELECT PROD_NM FROM TB_MS_PRODUCT WHERE 1=1
AND STORE_CD = #{storeCd}
) WHERE 1=1 AND PROD_NM = #{prodNm}

mapper.delete:
/* SimpleProdMapper.getSimpleProdCheckDelete */
DELETE
TB_TMP_PRODUCT
WHERE 1=1
AND SESSION_ID = #{sessionId}
AND HQ_OFFICE_CD = #{membrOrgnCd}


        AND STORE_CD = #{storeCd}


AND PROD_CD = #{prodCd}
