mapper.select:
/* TorderQtyDispMapper.getTorderQtyDispList */ SELECT * FROM ( SELECT tmp.STORE_CD, tms.STORE_NM, tmp.PROD_CD, tmp.PROD_NM, NVL(tmpi.ORD_QTY_DISP_FG, '0') AS ORD_QTY_DISP_FG, NVL(tmp.SOLD_OUT_YN, 'N') AS SOLD_OUT_YN, tms.HQ_OFFICE_CD, tmp.HQ_BRAND_CD, tmb.MS_BRAND_NM AS HQ_BRAND_NM, tmp.PROD_CLASS_CD, ( SELECT SF_GET_PROD_CLASSES('S', tmp.STORE_CD, tmp.PROD_CLASS_CD) FROM DUAL ) AS PROD_CLASS_NM, NVL(tmp.COST_UPRC, 0) COST_UPRC, NVL(tmp.SPLY_UPRC, 0) SPLY_UPRC, NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC, -- 판매단가 tmp.ORGPLCE_CD, -- TODO 원산지명 조회 필요 tmp.SIDE_PROD_YN, tmp.SDATTR_CLASS_CD, tmp.SDSEL_GRP_CD, tmp.PO_UNIT_FG, tmp.REG_DT, tmp.USE_YN, tmp.REG_FG, NVL2((SELECT DISTINCT PROD_CD FROM TB_MS_KIOSK_KEY tmkk2 WHERE tmkk2.STORE_CD = tmp.STORE_CD AND tmkk2.PROD_CD = tmp.PROD_CD AND TU_CLS_TYPE IN (SELECT ENVST_VAL FROM TB_MS_POS_ENVST tmpe WHERE tmpe.STORE_CD = tmp.STORE_CD AND tmpe.ENVST_CD IN ('4068') AND STORE_CD || POS_NO IN (SELECT STORE_CD || POS_NO FROM TB_MS_POS_ENVST WHERE ENVST_CD = '4019' AND ENVST_VAL = '6' AND STORE_CD = tmp.STORE_CD))), 'Y', 'N') AS USE_YN_4068, NVL2((SELECT DISTINCT PROD_CD FROM TB_MS_KIOSK_KEY tmkk2 WHERE tmkk2.STORE_CD = tmp.STORE_CD AND tmkk2.PROD_CD = tmp.PROD_CD AND TU_CLS_TYPE IN (SELECT ENVST_VAL FROM TB_MS_POS_ENVST tmpe WHERE tmpe.STORE_CD = tmp.STORE_CD AND tmpe.ENVST_CD IN ('4069') AND STORE_CD || POS_NO IN (SELECT STORE_CD || POS_NO FROM TB_MS_POS_ENVST WHERE ENVST_CD = '4019' AND ENVST_VAL = '6' AND STORE_CD = tmp.STORE_CD))), 'Y', 'N') AS USE_YN_4069, DECODE(NVL(tmp.CHANNEL_SOLD_OUT_YN, 'N'), 'Y', '품절', 'N', '정상') AS CHANNEL_SOLD_OUT_YN FROM TB_MS_PRODUCT tmp LEFT OUTER JOIN ( SELECT STORE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE 1=1
AND STORE_CD IN (${storeCdQuery})
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
AND SALE_PRC_FG = '1' GROUP BY STORE_CD, PROD_CD ) thpsp ON tmp.STORE_CD = thpsp.STORE_CD AND tmp.PROD_CD = thpsp.PROD_CD INNER JOIN TB_MS_STORE tms ON tms.STORE_CD = tmp.STORE_CD LEFT OUTER JOIN TB_MS_BRAND tmb ON tmb.STORE_CD = tmp.STORE_CD AND tmb.MS_BRAND_CD = tmp.HQ_BRAND_CD LEFT OUTER JOIN TB_MS_STORE_INFO tmsi ON tmsi.STORE_CD = tmp.STORE_CD LEFT OUTER JOIN TB_MS_PRODUCT_INFO tmpi ON tmpi.STORE_CD = tmp.STORE_CD AND tmpi.PROD_CD = tmp.PROD_CD WHERE 1=1 AND tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tmp.REG_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959'
AND tmp.PROD_CD LIKE '%'||#{prodCd}||'%' --상품코드
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%' --상품명
AND tmpb.BARCD_CD LIKE '%'||#{barCd}||'%' --바코드
AND tmp.PROD_CLASS_CD IN ( SELECT PROD_CLASS_CD FROM TB_MS_PRODUCT_CLASS WHERE STORE_CD = tmp.STORE_CD START WITH PROD_CLASS_CD = #{prodClassCd} AND STORE_CD = tmp.STORE_CD CONNECT BY PRIOR PROD_CLASS_CD = P_PROD_CLASS_CD AND STORE_CD = tmp.STORE_CD )
AND tmp.USE_YN = #{useYn} --사용여부
-- 수량변경표시여부 AND (tmpi.ORD_QTY_DISP_FG = #{ordQtyDispFg} OR tmpi.ORD_QTY_DISP_FG IS NULL)
-- 수량변경표시여부 AND tmpi.ORD_QTY_DISP_FG = #{ordQtyDispFg}
AND tmp.STORE_CD IN (${storeCdQuery})
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND
tmp.PROD_CD = #{item}
AND tmp.HQ_BRAND_CD = #{prodHqBrandCd} -- 상품브랜드 선택일때
-- 상품브랜드 전체일때 AND tmp.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
AND tmsi.MOMS_STORE_FG_02 = #{momsStoreFg02}
AND tmsi.MOMS_STORE_FG_03 = #{momsStoreFg03}
AND tmsi.MOMS_STORE_FG_04 = #{momsStoreFg04}
AND tmsi.MOMS_STORE_FG_05 = #{momsStoreFg05}
) WHERE 1=1
AND (USE_YN_4068 = 'Y' OR USE_YN_4069 = 'Y')
AND USE_YN_4068 = 'N' AND USE_YN_4069 = 'N'
ORDER BY STORE_CD, PROD_CD ASC
