mapper.select:
/* USE : TouchKeyMapper.getTouchKeyPageStyleCd */
SELECT tkc.STYLE_CD FROM ( SELECT ROWNUM AS rnum, thtkc.STYLE_CD FROM TB_HQ_TOUCH_KEY_CLASS thtkc WHERE thtkc.HQ_OFFICE_CD = #{hqOfficeCd} AND thtkc.PAGE_NO = #{pageNo} ORDER BY TUKEY_CLASS_CD DESC ) tkc WHERE tkc.rnum = 1
SELECT tkc.STYLE_CD FROM ( SELECT ROWNUM AS rnum, tmtkc.STYLE_CD FROM TB_MS_TOUCH_KEY_CLASS tmtkc WHERE tmtkc.STORE_CD = #{storeCd} AND tmtkc.PAGE_NO = #{pageNo} ORDER BY tmtkc.TUKEY_CLASS_CD DESC ) tkc WHERE tkc.rnum = 1

/* USE : TouchkeyMapper.getTouchKeyStyleList */ SELECT tctksd.STYLE_CD, tctksd.BUTTON_FG, tctksd.BUTTON_TAG_FG, tctksd.BUTTON_ON_COLOR, tctksd.BUTTON_OFF_COLOR, tctksd.FONT_ON_COLOR, tctksd.FONT_OFF_COLOR, tctksd.FONT_SIZE FROM TB_CM_TOUCH_KEY_STYLE_DTL tctksd
tctksd.STYLE_CD = #{styleCd}
ORDER BY tctksd.STYLE_CD, tctksd.BUTTON_FG

/* USE : TouchkeyMapper.getTouchKeyStyleForCompare */
SELECT
    FONT_SIZE,
    FONT_OFF_COLOR,
    BUTTON_OFF_COLOR
FROM
    TB_CM_TOUCH_KEY_STYLE_DTL
WHERE
    STYLE_CD = #{styleCd}
    AND BUTTON_FG = #{buttonFg}
    AND BUTTON_TAG_FG = #{buttonTagFg}

/* USE : TouchkeyMapper.getTouchKeyXml */
SELECT XML FROM TB_WB_HQ_CONFG_XML twhcx WHERE twhcx.HQ_OFFICE_CD = #{hqOfficeCd} AND twhcx.CONFG_FG = #{confgFg} AND twhcx.USE_YN = 'Y'
SELECT XML FROM TB_WB_STORE_CONFG_XML twscx WHERE twscx.STORE_CD = #{storeCd} AND twscx.CONFG_FG = #{confgFg} AND twscx.USE_YN = 'Y'

/* USE : TouchKeyMapper.getTouchKeyProdInfoList */
SELECT thtk.PROD_CD, thp.PROD_NM, TO_CHAR(NVL(thpsp.SALE_UPRC, 0), 'FM9,999,999') AS SALE_UPRC FROM TB_HQ_TOUCH_KEY thtk LEFT OUTER JOIN TB_HQ_PRODUCT thp ON thtk.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thtk.PROD_CD = thp.PROD_CD LEFT OUTER JOIN ( SELECT HQ_OFFICE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp ON thtk.HQ_OFFICE_CD = thpsp.HQ_OFFICE_CD AND thtk.PROD_CD = thpsp.PROD_CD WHERE thtk.HQ_OFFICE_CD = #{hqOfficeCd} GROUP BY thtk.PROD_CD, thp.PROD_NM, thpsp.SALE_UPRC ORDER BY thtk.PROD_CD
SELECT tmtk.PROD_CD, tmp.PROD_NM, NVL(tmpsp.SALE_UPRC, 0) AS SALE_UPRC FROM TB_MS_TOUCH_KEY tmtk LEFT OUTER JOIN TB_MS_PRODUCT tmp ON tmtk.STORE_CD = tmp.STORE_CD AND tmtk.PROD_CD = tmp.PROD_CD LEFT OUTER JOIN ( SELECT STORE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
GROUP BY STORE_CD, PROD_CD ) tmpsp ON tmtk.STORE_CD = tmpsp.STORE_CD AND tmtk.PROD_CD = tmpsp.PROD_CD WHERE tmtk.STORE_CD = #{storeCd} GROUP BY tmtk.PROD_CD, tmp.PROD_NM, tmpsp.SALE_UPRC ORDER BY tmtk.PROD_CD

mapper.insert:
/* USE : TouchkeyMapper.insertTouchKeyConfgXml */
INSERT INTO TB_WB_HQ_CONFG_XML twhcx ( twhcx.HQ_OFFICE_CD, twhcx.CONFG_FG, twhcx.XML, twhcx.USE_YN, twhcx.REG_DT, twhcx.REG_ID ) VALUES ( #{hqOfficeCd}, #{confgFg}, #{xml:CLOB}, #{useYn}, #{regDt}, #{regId} )
INSERT INTO TB_WB_STORE_CONFG_XML twscx ( twscx.STORE_CD, twscx.CONFG_FG, twscx.XML, twscx.USE_YN, twscx.REG_DT, twscx.REG_ID ) VALUES ( #{storeCd}, #{confgFg}, #{xml:CLOB}, #{useYn}, #{regDt}, #{regId} )

mapper.update:
/* USE : TouchkeyMapper.updateTouchKeyConfgXml */



        UPDATE
            TB_WB_HQ_CONFG_XML twhcx
        SET
            twhcx.XML = #{xml:CLOB},
            twhcx.USE_YN = #{useYn},
            twhcx.MOD_DT = #{regDt},
            twhcx.MOD_ID = #{regId}
        WHERE
            twhcx.HQ_OFFICE_CD = #{hqOfficeCd}
            AND twhcx.CONFG_FG = #{confgFg}



        UPDATE
            TB_WB_STORE_CONFG_XML twscx
        SET
            twscx.XML = #{xml:CLOB},
            twscx.USE_YN = #{useYn},
            twscx.MOD_DT = #{regDt},
            twscx.MOD_ID = #{regId}
        WHERE
            twscx.STORE_CD = #{storeCd}
            AND twscx.CONFG_FG = #{confgFg}

mapper.insert:
/* USE : TouchkeyMapper.insertTouchKeyClass */
INTO TB_HQ_TOUCH_KEY_CLASS ( HQ_OFFICE_CD, TUKEY_GRP_CD, TUKEY_CLASS_CD, TUKEY_CLASS_NM, PAGE_NO, PAGE_ROWS, X, Y, WIDTH, HEIGHT, STYLE_CD, FONT_SIZE, FONT_COLOR, FILL_COLOR, IMG_NM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{item.hqOfficeCd}, #{item.tukeyGrpCd}, #{item.tukeyClassCd}, #{item.tukeyClassNm}, #{item.pageNo}, #{item.pageRows}, #{item.x}, #{item.y}, #{item.width}, #{item.height}, #{item.styleCd}, #{item.fontSize, jdbcType=NUMERIC}, #{item.fontColor, jdbcType=VARCHAR}, #{item.fillColor, jdbcType=VARCHAR}, #{item.imgNm, jdbcType=VARCHAR}, #{item.regDt}, #{item.regId}, #{item.regDt}, #{item.regId} )
INTO TB_MS_TOUCH_KEY_CLASS ( STORE_CD, TUKEY_GRP_CD, TUKEY_CLASS_CD, TUKEY_CLASS_NM, PAGE_NO, PAGE_ROWS, X, Y, WIDTH, HEIGHT, STYLE_CD, FONT_SIZE, FONT_COLOR, FILL_COLOR, IMG_NM, IN_FG, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{item.storeCd}, #{item.tukeyGrpCd}, #{item.tukeyClassCd}, #{item.tukeyClassNm}, #{item.pageNo}, #{item.pageRows}, #{item.x}, #{item.y}, #{item.width}, #{item.height}, #{item.styleCd}, #{item.fontSize, jdbcType=NUMERIC}, #{item.fontColor, jdbcType=VARCHAR}, #{item.fillColor, jdbcType=VARCHAR}, #{item.imgNm, jdbcType=VARCHAR}, #{item.inFg}, #{item.regDt}, #{item.regId}, #{item.regDt}, #{item.regId} )

mapper.delete:
/* USE : TouchkeyMapper.deleteTouchKeyClass */
DELETE FROM TB_HQ_TOUCH_KEY_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND TUKEY_GRP_CD = '01'
DELETE FROM TB_MS_TOUCH_KEY_CLASS WHERE STORE_CD = #{storeCd} AND TUKEY_GRP_CD = '01'

mapper.insert:
/* USE : TouchkeyMapper.insertTouchKey */
INTO TB_HQ_TOUCH_KEY ( HQ_OFFICE_CD, TUKEY_CLASS_CD, TUKEY_CD, TUKEY_FG, PROD_CD, PAGE_NO, X, Y, WIDTH, HEIGHT, STYLE_CD, FONT_SIZE, FONT_COLOR, FILL_COLOR, IMG_NM, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{item.storeCd}, #{item.tukeyClassCd}, #{item.tukeyCd}, #{item.tukeyFg}, #{item.prodCd}, #{item.pageNo}, #{item.x}, #{item.y}, #{item.width}, #{item.height}, #{item.styleCd}, #{item.fontSize, jdbcType=NUMERIC}, #{item.fontColor, jdbcType=VARCHAR}, #{item.fillColor, jdbcType=VARCHAR}, #{item.imgNm, jdbcType=VARCHAR}, #{item.regDt}, #{item.regId}, #{item.regDt}, #{item.regId} )
INTO TB_MS_TOUCH_KEY ( STORE_CD, TUKEY_CLASS_CD, TUKEY_CD, TUKEY_FG, PROD_CD, PAGE_NO, X, Y, WIDTH, HEIGHT, STYLE_CD, FONT_SIZE, FONT_COLOR, FILL_COLOR, IMG_NM, IN_FG, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{item.storeCd}, #{item.tukeyClassCd}, #{item.tukeyCd}, #{item.tukeyFg}, #{item.prodCd}, #{item.pageNo}, #{item.x}, #{item.y}, #{item.width}, #{item.height}, #{item.styleCd}, #{item.fontSize, jdbcType=NUMERIC}, #{item.fontColor, jdbcType=VARCHAR}, #{item.fillColor, jdbcType=VARCHAR}, #{item.imgNm, jdbcType=VARCHAR}, #{item.inFg}, #{item.regDt}, #{item.regId}, #{item.regDt}, #{item.regId} )

mapper.delete:
/* USE : TouchkeyMapper.deleteTouchKey */
DELETE FROM TB_HQ_TOUCH_KEY WHERE HQ_OFFICE_CD = #{hqOfficeCd}
DELETE FROM TB_MS_TOUCH_KEY WHERE STORE_CD = #{storeCd}
