mapper.select:
/* USE : TouchKeyMapper.getTouchKeyPageStyleCd */
SELECT tkc.STYLE_CD FROM ( SELECT ROWNUM AS rnum, thtkc.STYLE_CD FROM TB_HQ_TOUCH_KEY_CLASS thtkc WHERE thtkc.HQ_OFFICE_CD = #{hqOfficeCd} AND thtkc.TUKEY_GRP_CD = #{tukeyGrpCd} AND thtkc.PAGE_NO = #{pageNo} ORDER BY TUKEY_CLASS_CD ) tkc WHERE tkc.rnum = 1
SELECT tkc.STYLE_CD FROM ( SELECT ROWNUM AS rnum, tmtkc.STYLE_CD FROM TB_MS_TOUCH_KEY_CLASS tmtkc WHERE tmtkc.STORE_CD = #{storeCd} AND tmtkc.TUKEY_GRP_CD = #{tukeyGrpCd} AND tmtkc.PAGE_NO = #{pageNo} ORDER BY tmtkc.TUKEY_CLASS_CD ) tkc WHERE tkc.rnum = 1

/* USE : TouchkeyMapper.getTouchKeyStyleList */ SELECT tctksd.STYLE_CD, tctksd.BUTTON_FG, tctksd.BUTTON_TAG_FG, tctksd.BUTTON_ON_COLOR, tctksd.BUTTON_OFF_COLOR, tctksd.FONT_ON_COLOR, tctksd.FONT_OFF_COLOR, tctksd.FONT_SIZE FROM TB_CM_TOUCH_KEY_STYLE_DTL tctksd
tctksd.STYLE_CD = #{styleCd}
ORDER BY tctksd.STYLE_CD, tctksd.BUTTON_FG

/* USE : TouchkeyMapper.getTouchKeyStyleForCompare */
