            XML
        FROM
            TB_WB_STORE_CONFG_XML twscx
        WHERE
            twscx.STORE_CD = #{storeCd}
            AND twscx.CONFG_FG = #{confgFg}
            AND twscx.GRP_CD = #{tukeyGrpCd}
            AND twscx.USE_YN = 'Y'

/* USE : TouchKeyMapper.getTouchKeyProdInfoList */
SELECT thtk.PROD_CD, thp.PROD_NM, TO_CHAR(NVL(thpsp.SALE_UPRC, 0), 'FM9,999,999') AS SALE_UPRC FROM TB_HQ_TOUCH_KEY thtk LEFT OUTER JOIN TB_HQ_PRODUCT thp ON thtk.HQ_OFFICE_CD = thp.HQ_OFFICE_CD AND thtk.PROD_CD = thp.PROD_CD LEFT OUTER JOIN ( SELECT HQ_OFFICE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
GROUP BY HQ_OFFICE_CD, PROD_CD ) thpsp ON thtk.HQ_OFFICE_CD = thpsp.HQ_OFFICE_CD AND thtk.PROD_CD = thpsp.PROD_CD WHERE thtk.HQ_OFFICE_CD = #{hqOfficeCd} AND thtk.TUKEY_GRP_CD = #{tukeyGrpCd} GROUP BY thtk.PROD_CD, thp.PROD_NM, thpsp.SALE_UPRC ORDER BY thtk.PROD_CD
SELECT tmtk.PROD_CD, tmp.PROD_NM, NVL(tmpsp.SALE_UPRC, 0) AS SALE_UPRC FROM TB_MS_TOUCH_KEY tmtk LEFT OUTER JOIN TB_MS_PRODUCT tmp ON tmtk.STORE_CD = tmp.STORE_CD AND tmtk.PROD_CD = tmp.PROD_CD LEFT OUTER JOIN ( SELECT STORE_CD, PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
GROUP BY STORE_CD, PROD_CD ) tmpsp ON tmtk.STORE_CD = tmpsp.STORE_CD AND tmtk.PROD_CD = tmpsp.PROD_CD WHERE tmtk.STORE_CD = #{storeCd} AND tmtk.TUKEY_GRP_CD = #{tukeyGrpCd} GROUP BY tmtk.PROD_CD, tmp.PROD_NM, tmpsp.SALE_UPRC ORDER BY tmtk.PROD_CD

