mapper.select:
/* TouchKeyMapper.selectProdByStore */
SELECT thp.PROD_CD, thp.PROD_NM, thpc.PROD_CLASS_NM, NVL(thpsp.SALE_UPRC, 0) AS SALE_UPRC FROM TB_HQ_PRODUCT thp LEFT OUTER JOIN TB_HQ_PRODUCT_CLASS thpc ON thp.HQ_OFFICE_CD = thpc.HQ_OFFICE_CD AND thp.PROD_CLASS_CD = thpc.PROD_CLASS_CD LEFT OUTER JOIN ( SELECT PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_HQ_PRODUCT_SALE_PRICE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
GROUP BY PROD_CD ) thpsp ON thp.PROD_CD = thpsp.PROD_CD WHERE thp.HQ_OFFICE_CD = #{hqOfficeCd} ORDER BY thp.PROD_CD
SELECT tmp.PROD_CD, tmp.PROD_NM, tmpc.PROD_CLASS_NM, NVL(tmpsp.SALE_UPRC, 0) AS SALE_UPRC FROM TB_MS_PRODUCT tmp LEFT OUTER JOIN TB_MS_PRODUCT_CLASS tmpc ON tmp.STORE_CD = tmpc.STORE_CD AND tmp.PROD_CLASS_CD = tmpc.PROD_CLASS_CD LEFT OUTER JOIN ( SELECT PROD_CD, MIN(SALE_UPRC) KEEP( DENSE_RANK FIRST ORDER BY START_DATE DESC ) AS SALE_UPRC FROM TB_MS_PRODUCT_SALE_PRICE WHERE STORE_CD = #{storeCd}
AND START_DATE <= TO_CHAR(SYSDATE, 'yyyyMMdd') AND END_DATE >= TO_CHAR(SYSDATE, 'yyyyMMdd')
GROUP BY PROD_CD ) tmpsp ON tmp.PROD_CD = tmpsp.PROD_CD WHERE tmp.STORE_CD = #{storeCd} ORDER BY tmp.PROD_CD

mapper.delete:
/* TouchkeyMapper.deleteTouchClassByStore */
DELETE FROM TB_HQ_TOUCH_CLASS WHERE HQ_OFFICE_CD = #{hqOfficeCd}
DELETE FROM TB_MS_TOUCH_CLASS WHERE STORE_CD = #{storeCd}

mapper.insert:
/* TouchkeyMapper.mergeTableGroupByStore */
INSERT INTO TB_HQ_TOUCH_CLASS thtc ( thtc.HQ_OFFICE_CD, thtc.TUKEY_GRP_CD, thtc.TUKEY_CLASS_CD, thtc.TUKEY_CLASS_NM, thtc.STYLE_CD, thtc.PAGE_NO, thtc.X, thtc.Y, thtc.WIDTH, thtc.HEIGHT, thtc.IMG_NM, thtc.REG_DT, thtc.REG_ID, thtc.MOD_DT, thtc.MOD_ID ) VALUES ( #{hqOfficeCd}, #{tukeyGrpCd}, #{tukeyClassCd}, #{tukeyClassNm}, #{styleCd}, #{pageNo}, #{x}, #{y}, #{width}, #{height}, #{imgNm, jdbcType=VARCHAR}, #{regDt}, #{regId}, #{regDt}, #{regId} )
INSERT INTO TB_MS_TOUCH_CLASS tmtc ( tmtc.STORE_CD, tmtc.TUKEY_GRP_CD, tmtc.TUKEY_CLASS_CD, tmtc.TUKEY_CLASS_NM, tmtc.STYLE_CD, tmtc.PAGE_NO, tmtc.X, tmtc.Y, tmtc.WIDTH, tmtc.HEIGHT, tmtc.IMG_NM, tmtc.IN_FG, tmtc.REG_DT, tmtc.REG_ID, tmtc.MOD_DT, tmtc.MOD_ID ) VALUES ( #{storeCd}, #{tukeyGrpCd}, #{tukeyClassCd}, #{tukeyClassNm}, #{styleCd}, #{pageNo}, #{x}, #{y}, #{width}, #{height}, #{imgNm, jdbcType=VARCHAR}, #{inFg}, #{regDt}, #{regId}, #{regDt}, #{regId} )

mapper.update:
/* TouchkeyMapper.deleteTouchByStore */







        DELETE
        FROM
        TB_MS_TOUCH
        WHERE
        STORE_CD = #{storeCd}
