mapper.select:
/* USE : TouchKeyResveMapper.getTouchKeyResveList */
SELECT tms.BRANCH_CD , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM , tmtkgr.STORE_CD , tms.STORE_NM , tmtkgr.POS_NO , tms.HQ_BRAND_CD , tmsi.MOMS_TEAM , tmsi.MOMS_AC_SHOP , tmtkgr.START_DATE AS ORG_START_DATE , TO_CHAR(TO_DATE(tmtkgr.START_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS START_DATE , tmtkgr.TUKEY_GRP_CD AS ORG_TOUCH_KEY_GRP , tmtkgr.TUKEY_GRP_CD AS TOUCH_KEY_GRP , TO_CHAR(TO_DATE(tmpe.MOD_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS MOD_DT , tmpe.ENVST_VAL AS ENV_4038 , tmtkgr.RESVE_FG
FROM TB_MS_TOUCH_KEY_GROUP_RESVE tmtkgr , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi , TB_MS_POS_ENVST tmpe WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmtkgr.STORE_CD = tms.STORE_CD AND tmtkgr.STORE_CD = tmsi.STORE_CD(+) AND tmtkgr.STORE_CD = tmpe.STORE_CD(+) AND tmtkgr.POS_NO = tmpe.POS_NO(+) AND '4038' = tmpe.ENVST_CD(+)
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tmtkgr.START_DATE BETWEEN #{startDate} AND #{endDate}
AND tmtkgr.TUKEY_GRP_CD = #{touchKeyGrp}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}
ORDER BY tmtkgr.STORE_CD, tmtkgr.POS_NO, tmtkgr.START_DATE

/* USE : TouchKeyResveMapper.getTouchKeyResveAddList */
SELECT	tms.BRANCH_CD
,		(SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS thbm WHERE thbm.HQ_OFFICE_CD = #{hqOfficeCd} AND thbm.BRANCH_CD = tms.BRANCH_CD) AS BRANCH_NM
,		tms.STORE_CD
,		tms.STORE_NM
,		tmpe.POS_NO
,		tms.HQ_BRAND_CD
,		tmsi.MOMS_TEAM
,		tmsi.MOMS_AC_SHOP
,		tmpe.ENVST_VAL AS ORG_TOUCH_KEY_GRP
,       TO_CHAR(TO_DATE(tmpe.MOD_DT, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD HH24:MI:SS') AS MOD_DT
FROM 	TB_MS_STORE tms
,		TB_MS_STORE_INFO tmsi
,		TB_MS_POS_ENVST tmpe
,		TB_MS_POS_ENVST tmpe2
WHERE 	tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND 	tms.STORE_CD = tmsi.STORE_CD(+)
AND 	tms.STORE_CD = tmpe.STORE_CD(+)
AND 	'4038' = tmpe.ENVST_CD(+)
AND 	tms.STORE_CD = tmpe2.STORE_CD(+)
AND 	tmpe.POS_NO = tmpe2.POS_NO
AND 	'4019' = tmpe2.ENVST_CD(+)
AND 	'6' != tmpe2.ENVST_VAL(+)

    AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'


    AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'


    AND tmpe.ENVST_VAL = #{touchKeyGrp}


    AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때



        -- 매장브랜드 전체일때
        AND tms.HQ_BRAND_CD IN

            #{item}




    AND tmsi.MOMS_TEAM = #{momsTeam}


    AND tmsi.MOMS_AC_SHOP = #{momsAcShop}


    AND tmsi.MOMS_AREA_FG = #{momsAreaFg}


    AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}


    AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}


    AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}


    AND tms.BRANCH_CD = #{branchCd}


    AND tmsi.MOMS_STORE_FG_01 = #{momsStoreFg01}

ORDER
BY      tms.STORE_CD, tmpe.POS_NO

/* USE : KioskDisplayResveMapper.getTouchKeyResveCnt */
SELECT  COUNT(*)
FROM    TB_MS_TOUCH_KEY_GROUP_RESVE
WHERE   STORE_CD    = #{storeCd}
AND     POS_NO = #{posNo}
AND     START_DATE  = #{startDate}
AND     RESVE_FG   != '0'

mapper.insert:
/* USE : TouchKeyResveMapper.saveTouchKeyResve */
MERGE INTO TB_MS_TOUCH_KEY_GROUP_RESVE A
USING DUAL
ON
(
    A.STORE_CD = #{storeCd}
AND A.POS_NO = #{posNo}
AND A.START_DATE = #{startDate}
)
WHEN MATCHED THEN
UPDATE
SET
    A.TUKEY_GRP_CD = #{touchKeyGrp},
    A.MOD_DT = #{modDt},
    A.MOD_ID = #{modId}
WHEN NOT MATCHED THEN
INSERT  (
        STORE_CD
    ,   POS_NO
    ,   START_DATE
    ,   TUKEY_GRP_CD
    ,   RESVE_FG
    ,   REG_DT
    ,   REG_ID
    ,   MOD_DT
    ,   MOD_ID
) VALUES (
        #{storeCd}
    ,   #{posNo}
    ,   #{startDate}
    ,   #{touchKeyGrp}
    ,   '0'
    ,   #{regDt}
    ,   #{regId}
    ,   #{modDt}
    ,   #{modId}
)

mapper.delete:
/* USE : TouchKeyResveMapper.deleteTouchKeyResve */
DELETE
FROM    TB_MS_TOUCH_KEY_GROUP_RESVE
WHERE   STORE_CD = #{storeCd}
AND     POS_NO = #{posNo}
AND     START_DATE = #{orgStartDate}
AND     TUKEY_GRP_CD = #{orgTouchKeyGrp}
AND     RESVE_FG = '0'
