mapper.select:
/* VendrMapper.getHqVendrCd */
SELECT
LPAD((NVL(MAX(thv.VENDR_CD), 0) + 1), 10, 0) AS VENDR_CD
FROM TB_HQ_VENDOR thv
WHERE 1=1
AND thv.HQ_OFFICE_CD = #{hqOfficeCd}

/* VendrMapper.getMsVendrCd */
SELECT
LPAD((NVL(MAX(tmv.VENDR_CD), 0) + 1), 10, 0) AS VENDR_CD
FROM TB_MS_VENDOR tmv
WHERE 1=1
AND tmv.STORE_CD = #{storeCd}

/* USE : VendrMapper.getHqVendrProdList */ SELECT ROWNUM AS RNUM, thvp.PROD_CD, thp.PROD_NM, thp.SPLY_UPRC FROM TB_HQ_VENDOR_PROD thvp, TB_HQ_PRODUCT thp WHERE 1=1 AND thvp.HQ_OFFICE_CD (+)= thp.HQ_OFFICE_CD AND thvp.PROD_CD (+)= thp.PROD_CD AND thvp.HQ_OFFICE_CD = #{hqOfficeCd} AND thvp.VENDR_CD = #{vendrCd} AND thvp.USE_YN = 'Y'
AND thvp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND thp.PROD_NM LIKE '%'||#{prodNm}||'%'

/* USE : VendrMapper.getMsVendrProdList */ SELECT ROWNUM AS RNUM, tmvp.PROD_CD, tmp.PROD_NM, tmp.SPLY_UPRC FROM TB_MS_VENDOR_PROD tmvp, TB_MS_PRODUCT tmp WHERE 1=1 AND tmvp.STORE_CD (+)= tmp.STORE_CD AND tmvp.PROD_CD (+)= tmp.PROD_CD AND tmvp.STORE_CD = tmp.STORE_CD AND tmvp.STORE_CD = #{storeCd} AND tmvp.VENDR_CD = #{vendrCd} AND tmvp.USE_YN = 'Y'
AND tmvp.PROD_CD LIKE '%'||#{prodCd}||'%'
AND tmp.PROD_NM LIKE '%'||#{prodNm}||'%'
