mapper.select:
/* VendrMapper.getHqVendrCd */
SELECT
LPAD((NVL(MAX(thv.VENDR_CD), 0) + 1), 10, 0) AS VENDR_CD
FROM TB_HQ_VENDOR thv
WHERE 1=1
AND thv.HQ_OFFICE_CD = #{hqOfficeCd}

/* VendrMapper.getMsVendrCd */
SELECT
LPAD((NVL(MAX(tmv.VENDR_CD), 0) + 1), 10, 0) AS VENDR_CD
FROM TB_MS_VENDOR tmv
WHERE 1=1
AND tmv.STORE_CD = #{storeCd}

mapper.update:
/* USE : VendrMapper.getMembrNmSaveUpdate */
UPDATE TB_MB_MEMBER
SET
MEMBR_NM = SBPENC.E(#{membrNm}),
MOD_DT = #{modDt},
MOD_ID = #{modId}
WHERE 1=1
AND MEMBR_ORGN_CD = #{hqOfficeCd}
AND MEMBR_NO = #{membrNo}

mapper.select:
/* VendrMapper.getMinRegStoreCd */
SELECT MIN(STORE_CD) AS REG_STORE_CD
FROM TB_MS_STORE
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
