mapper.select:
/* ProdPrintYnMapper.getProdOptionPrintYnList */
SELECT A.OPTION_GRP_CD, A.OPTION_GRP_NM, A.USE_YN, B.OPTION_VAL_CD, B.OPTION_VAL_NM, B.OPT_PROD_CD, C.PROD_NM AS OPT_PROD_NM, NVL(B.PRINT_YN, 'Y') AS PRINT_YN FROM TB_HQ_PRODUCT_OPTION_GROUP A, TB_HQ_PRODUCT_OPTION_VAL B, TB_HQ_PRODUCT C WHERE 1=1 AND A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.OPTION_GRP_CD LIKE '%'||#{optionGrpCd}||'%'
AND A.OPTION_GRP_NM LIKE '%'||#{optionGrpNm}||'%'
AND B.OPTION_VAL_CD LIKE '%'||#{optionValCd}||'%'
AND B.OPTION_VAL_NM LIKE '%'||#{optionValNm}||'%'
AND NVL(B.PRINT_YN, 'Y') = #{printYn}
AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.OPTION_GRP_CD = A.OPTION_GRP_CD AND C.HQ_OFFICE_CD (+)= B.HQ_OFFICE_CD AND C.PROD_CD (+)= B.OPT_PROD_CD ORDER BY A.OPTION_GRP_CD, B.OPTION_VAL_CD
SELECT A.OPTION_GRP_CD, A.OPTION_GRP_NM, A.USE_YN, B.OPTION_VAL_CD, B.OPTION_VAL_NM, B.OPT_PROD_CD, C.PROD_NM AS OPT_PROD_NM, NVL(B.PRINT_YN, 'Y') AS PRINT_YN FROM TB_MS_PRODUCT_OPTION_GROUP A, TB_MS_PRODUCT_OPTION_VAL B, TB_MS_PRODUCT C WHERE 1=1 AND A.STORE_CD = #{storeCd}
AND A.OPTION_GRP_CD LIKE '%'||#{optionGrpCd}||'%'
AND A.OPTION_GRP_NM LIKE '%'||#{optionGrpNm}||'%'
AND B.OPTION_VAL_CD LIKE '%'||#{optionValCd}||'%'
AND B.OPTION_VAL_NM LIKE '%'||#{optionValNm}||'%'
AND NVL(B.PRINT_YN, 'Y') = #{printYn}
AND B.STORE_CD = A.STORE_CD AND B.OPTION_GRP_CD = A.OPTION_GRP_CD AND C.STORE_CD (+)= B.STORE_CD AND C.PROD_CD (+)= B.OPT_PROD_CD ORDER BY A.OPTION_GRP_CD, B.OPTION_VAL_CD

mapper.update:
/* ProdPrintYnMapper.getProdOptionPrintYnSaveUpdate */



        UPDATE
        TB_HQ_PRODUCT_OPTION_VAL
        SET
        PRINT_YN = #{printYn},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND HQ_OFFICE_CD = #{hqOfficeCd}
        AND OPTION_GRP_CD = #{optionGrpCd}
        AND OPTION_VAL_CD = #{optionValCd}



        UPDATE
        TB_MS_PRODUCT_OPTION_VAL
        SET
        PRINT_YN = #{printYn},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND OPTION_GRP_CD = #{optionGrpCd}
        AND OPTION_VAL_CD = #{optionValCd}

/* ProdPrintYnMapper.getSideMenuProdPrintYnSaveUpdate */



        UPDATE
        TB_HQ_PRODUCT_SDSEL_PROD
        SET
        PRINT_YN = #{printYn},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND HQ_OFFICE_CD = #{hqOfficeCd}
        AND SDSEL_CLASS_CD = #{sdselClassCd}
        AND PROD_CD = #{sdselProdCd}



        UPDATE
        TB_MS_PRODUCT_SDSEL_PROD
        SET
        PRINT_YN = #{printYn},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND SDSEL_CLASS_CD = #{sdselClassCd}
        AND PROD_CD = #{sdselProdCd}
