/* PromotionReportMapper.getPromotionReportList */
SELECT thph.PROMOTION_NM , tsdp.PROMOTION_CD , TO_CHAR(TO_DATE(thpc.START_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS START_YMD , TO_CHAR(TO_DATE(thpc.END_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS END_YMD , tsdp.STORE_CD , tms.STORE_NM , (SUBSTR(tms.BIZ_NO, 1,3) || '-' || SUBSTR(tms.BIZ_NO, 4,2) || '-' || SUBSTR(tms.BIZ_NO, 6)) AS BIZ_NO , TO_CHAR(TO_DATE(thpc.DEPOSIT_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS DEPOSIT_YMD , DECODE(tms.DIRECT_MANAGE_YN,'Y','직영점','N','가맹점') AS REMARK , SUM(tsdp.HD_REAL_SALE_CNT) AS HD_REAL_SALE_CNT , SUM(tsdp.DT_REAL_SALE_CNT) AS DT_REAL_SALE_CNT , SUM(tsdp.REAL_SALE_QTY) AS REAL_SALE_QTY , NVL2(thpc.CHARGE_DS, DECODE(thpc.CHARGE_DS, '1', '정율', '2', '정액'), '미사용') AS CHARGE_DS , NVL2(thpc.CHARGE_DS, thpc.HQ_CHARGE_UPRC, '') AS HQ_CHARGE_UPRC_M , NVL2(thpc.CHARGE_DS, thpc.MS_CHARGE_UPRC, '') AS MS_CHARGE_UPRC_M , NVL2(thpc.CHARGE_DS, thpc.PARTNER_CHARGE_UPRC, '') AS PARTNER_CHARGE_UPRC_M , NVL2(thpc.CHARGE_DS, DECODE(thpc.CHARGE_DS, '1', '', '2', (thpc.HQ_CHARGE_UPRC * SUM(tsdp.DT_REAL_SALE_CNT))), '') AS TOT_HQ_CHARGE_UPRC_M , NVL2(thpc.CHARGE_DS, DECODE(thpc.CHARGE_DS, '1', '', '2', (thpc.MS_CHARGE_UPRC * SUM(tsdp.DT_REAL_SALE_CNT))), '') AS TOT_MS_CHARGE_UPRC_M , NVL2(thpc.CHARGE_DS, DECODE(thpc.CHARGE_DS, '1', '', '2', (thpc.PARTNER_CHARGE_UPRC * SUM(tsdp.DT_REAL_SALE_CNT))), '') AS TOT_PARTNER_CHARGE_UPRC_M , DECODE(NVL(SUM(tsdp.DT_REAL_SALE_CNT), ''), 0, 0, SUM(tsdp.HQ_CHARGE_UPRC)/SUM(tsdp.DT_REAL_SALE_CNT)) AS HQ_CHARGE_UPRC , DECODE(NVL(SUM(tsdp.DT_REAL_SALE_CNT), ''), 0, 0, SUM(tsdp.MS_CHARGE_UPRC)/SUM(tsdp.DT_REAL_SALE_CNT)) AS MS_CHARGE_UPRC , DECODE(NVL(SUM(tsdp.DT_REAL_SALE_CNT), ''), 0, 0, SUM(tsdp.PARTNER_CHARGE_UPRC)/SUM(tsdp.DT_REAL_SALE_CNT)) AS PARTNER_CHARGE_UPRC , SUM(tsdp.HQ_CHARGE_UPRC) AS TOT_HQ_CHARGE_UPRC , SUM(tsdp.MS_CHARGE_UPRC) AS TOT_MS_CHARGE_UPRC , SUM(tsdp.PARTNER_CHARGE_UPRC) AS TOT_PARTNER_CHARGE_UPRC , SUM(tsdp.HD_REAL_SALE_CNT) AS HD_REAL_SALE_CNT_PROMO_CD , FN_DT_REAL_SALE_CNT_PROMO(tsdp.STORE_CD, tsdp.PROMOTION_CD, SUM(tsdp.REAL_SALE_QTY)) AS DT_REAL_SALE_CNT_PROMO_CD , SUM(tsdp.REAL_SALE_QTY) AS REAL_SALE_QTY_PROMO_CD , NVL2(thpc.CHARGE_DS, thpc.HQ_CHARGE_UPRC, '') AS HQ_CHARGE_UPRC_M_PROMO_CD , NVL2(thpc.CHARGE_DS, thpc.MS_CHARGE_UPRC, '') AS MS_CHARGE_UPRC_M_PROMO_CD , NVL2(thpc.CHARGE_DS, thpc.PARTNER_CHARGE_UPRC, '') AS PARTNER_CHARGE_UPRC_M_PROMO_CD , FN_PROMO_CHARGE_UPRC(tsdp.STORE_CD, tsdp.PROMOTION_CD, SUM(tsdp.REAL_SALE_QTY), 'HQ') AS HQ_CHARGE_UPRC_PROMO_CD , FN_PROMO_CHARGE_UPRC(tsdp.STORE_CD, tsdp.PROMOTION_CD, SUM(tsdp.REAL_SALE_QTY), 'MS') AS MS_CHARGE_UPRC_PROMO_CD , FN_PROMO_CHARGE_UPRC(tsdp.STORE_CD, tsdp.PROMOTION_CD, SUM(tsdp.REAL_SALE_QTY), 'PN') AS PARTNER_CHARGE_UPRC_PROMO_CD
FROM TB_SL_DAILY_PROMO tsdp , TB_HQ_PROMO_H thph , TB_HQ_PROMO_CONDI thpc , TB_MS_STORE tms , TB_MS_STORE_INFO tmsi WHERE tsdp.HQ_OFFICE_CD = #{hqOfficeCd} AND thpc.DEPOSIT_YMD BETWEEN #{startDate} AND #{endDate} AND tsdp.HQ_OFFICE_CD = thph.HQ_OFFICE_CD AND tsdp.PROMOTION_CD = thph.PROMOTION_CD AND tsdp.HQ_OFFICE_CD = thpc.HQ_OFFICE_CD AND tsdp.PROMOTION_CD = thpc.PROMOTION_CD AND tsdp.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tsdp.STORE_CD = tms.STORE_CD AND tmsi.STORE_CD (+)= tms.STORE_CD
AND tsdp.PROMOTION_CD IN
#{item}
AND tsdp.STORE_CD IN
#{item}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 매장브랜드 선택일때
-- 매장브랜드 전체일때 AND tms.HQ_BRAND_CD IN
#{item}
AND tmsi.MOMS_TEAM = #{momsTeam}
AND tmsi.MOMS_AC_SHOP = #{momsAcShop}
AND tmsi.MOMS_AREA_FG = #{momsAreaFg}
AND tmsi.MOMS_COMMERCIAL = #{momsCommercial}
AND tmsi.MOMS_SHOP_TYPE = #{momsShopType}
AND tmsi.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND tms.BRANCH_CD = #{branchCd}
GROUP BY thph.PROMOTION_NM , tsdp.PROMOTION_CD , thpc.START_YMD , thpc.END_YMD , tsdp.STORE_CD , tms.STORE_NM , tms.BIZ_NO , thpc.DEPOSIT_YMD , tms.DIRECT_MANAGE_YN , thpc.CHARGE_DS , thpc.HQ_CHARGE_UPRC , thpc.MS_CHARGE_UPRC , thpc.PARTNER_CHARGE_UPRC ORDER BY tsdp.PROMOTION_CD, tsdp.STORE_CD
