mapper.select:
/* USE : StoreSelfPromotionMapper.getStoreSelfPromotionList */ SELECT * FROM ( SELECT tmph.STORE_CD, tms.STORE_NM, tmph.PROMOTION_CD, tmph.PROMOTION_NM, TO_CHAR(TO_DATE(tmpc.START_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS START_YMD, TO_CHAR(TO_DATE(tmpc.END_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS END_YMD FROM TB_MS_PROMO_H tmph, TB_MS_PROMO_CONDI tmpc, TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmph.STORE_CD = tmpc.STORE_CD AND tmph.PROMOTION_CD = tmpc.PROMOTION_CD AND tmph.STORE_CD = tms.STORE_CD AND tmpc.DATE_YN = 'N'
AND tmph.STORE_CD IN
#{item}
AND tmph.REG_FG = 'S' UNION SELECT tmph.STORE_CD, tms.STORE_NM, tmph.PROMOTION_CD, tmph.PROMOTION_NM, TO_CHAR(TO_DATE(tmpc.START_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS START_YMD, TO_CHAR(TO_DATE(tmpc.END_YMD, 'yyyymmdd'), 'yyyy-mm-dd') AS END_YMD FROM TB_MS_PROMO_H tmph, TB_MS_PROMO_CONDI tmpc, TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmph.STORE_CD = tmpc.STORE_CD AND tmph.PROMOTION_CD = tmpc.PROMOTION_CD AND tmph.STORE_CD = tms.STORE_CD AND tmpc.DATE_YN = 'Y'
AND tmpc.START_YMD >= #{startDate}
AND tmpc.END_YMD <= #{endDate}
AND tmph.STORE_CD IN
#{item}
AND tmph.REG_FG = 'S' ) A ORDER BY A.STORE_CD ASC, A.PROMOTION_CD DESC
