mapper.select:
/* GuestManagMapper.getGuestManageList */
SELECT A.NMCODE_CD AS GUEST_CD, B.NMCODE_NM AS GUEST_NM FROM TB_CM_NMCODE A, TB_HQ_NMCODE B WHERE 1=1 AND A.NMCODE_GRP_CD = '119' AND B.HQ_OFFICE_CD (+)= #{hqOfficeCd} AND B.NMCODE_GRP_CD (+)= A.NMCODE_GRP_CD AND B.NMCODE_CD (+)= A.NMCODE_CD ORDER BY A.NMCODE_CD
SELECT A.NMCODE_CD AS GUEST_CD, B.NMCODE_NM AS GUEST_NM FROM TB_CM_NMCODE A, TB_MS_STORE_NMCODE B WHERE 1=1 AND A.NMCODE_GRP_CD = '119' AND B.STORE_CD (+)= #{storeCd} AND B.NMCODE_GRP_CD (+)= A.NMCODE_GRP_CD AND B.NMCODE_CD (+)= A.NMCODE_CD ORDER BY A.NMCODE_CD

mapper.insert:
/* GuestManagMapper.getGuestManageSaveInsert */
MERGE INTO TB_HQ_NMCODE USING DUAL ON ( HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '119' AND NMCODE_CD = #{guestCd} ) WHEN MATCHED THEN UPDATE SET NMCODE_NM = #{guestNm}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '119' AND NMCODE_CD = #{guestCd} WHEN NOT MATCHED THEN INSERT ( HQ_OFFICE_CD, NMCODE_GRP_CD, NMCODE_CD, PARENT_CD, NMCODE_NM, NMCODE_ITEM_1, NMCODE_ITEM_2, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, '119', #{guestCd}, '119', #{guestNm}, '', '', 'Y', #{regDt}, #{regId}, #{modDt}, #{modId} )
MERGE INTO TB_MS_STORE_NMCODE USING DUAL ON ( STORE_CD = #{storeCd} AND NMCODE_GRP_CD = '119' AND NMCODE_CD = #{guestCd} ) WHEN MATCHED THEN UPDATE SET NMCODE_NM = #{guestNm}, MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE STORE_CD = #{storeCd} AND NMCODE_GRP_CD = '119' AND NMCODE_CD = #{guestCd} WHEN NOT MATCHED THEN INSERT ( STORE_CD, NMCODE_GRP_CD, NMCODE_CD, PARENT_CD, NMCODE_NM, NMCODE_ITEM_1, NMCODE_ITEM_2, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{storeCd}, '119', #{guestCd}, '119', #{guestNm}, '', '', 'Y', #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.delete:
/* GuestManagMapper.getGuestManageSaveDelete */




            DELETE
            FROM TB_HQ_NMCODE
            WHERE 1=1
            AND HQ_OFFICE_CD = #{hqOfficeCd}
            AND NMCODE_GRP_CD = '119'
            AND NMCODE_CD = #{guestCd}



            DELETE
            FROM TB_MS_STORE_NMCODE
            WHERE 1=1
            AND STORE_CD = #{storeCd}
            AND NMCODE_GRP_CD = '119'
            AND NMCODE_CD = #{guestCd}

mapper.select:
/* GuestManagMapper.getGuestManageStoreRegistList */ SELECT tms.HQ_OFFICE_CD, tms.STORE_CD, tms.STORE_NM, tms.SYS_STAT_FG FROM TB_MS_STORE tms WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd}
AND tms.STORE_CD LIKE '%'||#{storeCd}||'%'
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.STORE_CD ASC
