mapper.select:
/* MyInfoMapper.findAllHqNmcodeByHqOfficeCdAndNmcodeGrpCd */
SELECT NMCODE_GRP_CD,
       NMCODE_CD,
       NMCODE_NM,
       NMCODE_ITEM_1,
       NMCODE_ITEM_2
  FROM TB_HQ_NMCODE
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND NMCODE_GRP_CD = #{nmcodeGrpCd}
   AND USE_YN = 'Y'

mapper.update:
/* MyInfoMapper.updateHqOffice */ UPDATE TB_HQ_OFFICE SET OWNER_NM = #{ownerNm}, BIZ_STORE_NM = #{bizStoreNm}, TEL_NO = #{telNo}, FAX_NO = #{faxNo}, AREA_CD = #{areaCd}, EMAIL_ADDR = #{emailAddr}, HMPG_ADDR = #{hmpgAddr},
ADDR = #{addr},
MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE HQ_OFFICE_CD = #{hqOfficeCd}

mapper.insert:
/* MyInfoMapper.insertHqNmcode */
SELECT DECODE( #{nmcodeCd}, '', TO_CHAR( NVL( MAX( NMCODE_CD ), 0 ) + 1 ), #{nmcodeCd} ) FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = #{nmcodeGrpCd}
INSERT INTO TB_HQ_NMCODE( HQ_OFFICE_CD, NMCODE_GRP_CD, NMCODE_CD, PARENT_CD, NMCODE_NM, NMCODE_ITEM_1, NMCODE_ITEM_2, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID ) VALUES ( #{hqOfficeCd}, #{nmcodeGrpCd}, #{nmcodeCd}, #{nmcodeCd}, #{nmcodeNm}, #{nmcodeItem1}, #{nmcodeItem2}, #{useYn}, #{regDt}, #{regId}, #{modDt}, #{modId} )

mapper.update:
/* MyInfoMapper.updateHqNmcode */
UPDATE TB_HQ_NMCODE

   NMCODE_NM = #{nmcodeNm},
   NMCODE_ITEM_1 = #{nmcodeItem1},
   NMCODE_ITEM_2 = #{nmcodeItem2},
   USE_YN = #{useYn},
   MOD_DT = #{modDt},
   MOD_ID = #{modId}

 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND NMCODE_GRP_CD = #{nmcodeGrpCd}
   AND NMCODE_CD = #{nmcodeCd}

/* MyInfoMapper.deleteHqNmcode */
UPDATE TB_HQ_NMCODE
   SET USE_YN = 'N',
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND NMCODE_GRP_CD = #{nmcodeGrpCd}
   AND NMCODE_CD = #{nmcodeCd}
   AND USE_YN = 'Y'
