mapper.update:
/* TableLayoutMapper.deleteTableGroupByStore */
UPDATE TB_MS_TABLE_GROUP
   SET USE_YN = 'N'
 WHERE STORE_CD = #{storeCd}

/* TableLayoutMapper.mergeTableGroupByStore */ MERGE INTO TB_MS_TABLE_GROUP A USING (SELECT #{storeCd} STORE_CD, LPAD(#{tblGrpCd}, 2, '0') TBL_GRP_CD FROM DUAL ) ORG ON (A.STORE_CD = ORG.STORE_CD AND A.TBL_GRP_CD = ORG.TBL_GRP_CD )
WHEN MATCHED THEN UPDATE SET A.TBL_GRP_NM = #{tblGrpNm}, A.TBL_GRP_FG = #{tblGrpFg}, A.BG_IMG_NM = #{bgImgNm}, A.DISP_SEQ = #{dispSeq}, A.USE_YN = #{useYn}, A.MOD_DT = #{regDt}, A.MOD_ID = #{regId}
WHEN NOT MATCHED THEN INSERT ( A.STORE_CD, A.TBL_GRP_CD, A.TBL_GRP_NM, A.TBL_GRP_FG, A.BG_IMG_NM, A.DISP_SEQ, A.USE_YN, A.REG_DT, A.REG_ID, A.MOD_DT, A.MOD_ID ) VALUES ( #{storeCd}, LPAD(#{tblGrpCd}, 2, '0'), #{tblGrpNm}, #{tblGrpFg}, #{bgImgNm}, #{dispSeq}, #{useYn}, #{regDt}, #{regId}, #{regDt}, #{regId} )

/* TableLayoutMapper.deleteTableByStore */
UPDATE TB_MS_TABLE
   SET USE_YN = 'N'
 WHERE STORE_CD = #{storeCd}
