mapper.select:
/* USE : ViewMapper.getViewList */
SELECT tms.STORE_CD, tms.STORE_NM, tms.CLS_FG, tms.BIZ_NO , tms.OWNER_NM, tms.TEL_NO, tms.POST_NO || ') ' || tms.ADDR || tms.ADDR_DTL AS ADDRESS, tmp.POS_VER_NO, tms.SYS_STAT_FG, TO_CHAR(TO_DATE(tms.SYS_OPEN_DATE, 'yyyyMMdd'), 'YYYY/MM/DD') AS SYS_OPEN_DATE, TO_CHAR(TO_DATE(tms.SYS_CLOSURE_DATE, 'yyyyMMdd'), 'YYYY/MM/DD') AS SYS_CLOSURE_DATE
FROM TB_MS_STORE tms, TB_MS_POS tmp WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tmp.STORE_CD (+)= tms.STORE_CD AND tmp.POS_NO (+)= '01' -- 기본포스(포스 최신버전 가져오기 위함) AND tmp.USE_YN (+)= 'Y'
AND tms.STORE_CD = #{storeCd}
AND tms.STORE_NM LIKE '%'||#{storeNm}||'%'
AND tms.CLS_FG = #{clsFg}
AND tms.SYS_STAT_FG = #{sysStatFg}
ORDER BY tms.STORE_CD ASC

/* ViewMapper.getTerminalEnv */
SELECT NVL(ENVST_VAL, '0') AS ENVST_VAL
  FROM TB_MS_STORE_ENVST
 WHERE STORE_CD = #{storeCd}
   AND ENVST_CD = #{envstCd}
   AND USE_YN = 'Y'

/* USE : ViewMapper.copyStoreNmcode */
{
    CALL PKG_MS_STORE_COPY.SP_MS_STORE_NNCODE
    (
        #{hqOfficeCd},
        #{originalStoreCd},
        #{targetStoreCd},
        #{regDt},
        #{regId},
        #{modDt},
        #{modId},
        #{result, mode=OUT, jdbcType=VARCHAR}
    )
}

