mapper.select:
/* EmpWebMenuMapper.getUseEmp */ SELECT the.EMP_NO , the.EMP_NM , the.USER_ID , the.MP_NO , twub.HQ_BRAND_NM AS USER_HQ_BRAND , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND BRANCH_CD = the.BRANCH_CD) AS BRANCH_CD , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '151' AND NMCODE_CD = the.MOMS_TEAM) AS MOMS_TEAM , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '152' AND NMCODE_CD = the.MOMS_AC_SHOP) AS MOMS_AC_SHOP , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '153' AND NMCODE_CD = the.MOMS_AREA_FG) AS MOMS_AREA_FG , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '154' AND NMCODE_CD = the.MOMS_COMMERCIAL) AS MOMS_COMMERCIAL , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '155' AND NMCODE_CD = the.MOMS_SHOP_TYPE) AS MOMS_SHOP_TYPE , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '156' AND NMCODE_CD = the.MOMS_STORE_MANAGE_TYPE) AS MOMS_STORE_MANAGE_TYPE , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '167' AND NMCODE_CD = the.MOMS_STORE_FG_01) AS MOMS_STORE_FG_01 FROM TB_HQ_EMPLOYEE the , ( SELECT twub.USER_ID , LISTAGG(twub.HQ_BRAND_CD, ',') WITHIN GROUP (ORDER BY twub.HQ_BRAND_CD) AS HQ_BRAND_CD , LISTAGG(thb.HQ_BRAND_NM, ',') WITHIN GROUP (ORDER BY thb.HQ_BRAND_NM) AS HQ_BRAND_NM FROM TB_WB_USER_BRAND twub , TB_HQ_BRAND thb WHERE 1=1 AND thb.HQ_OFFICE_CD = #{hqOfficeCd} AND thb.HQ_BRAND_CD = twub.HQ_BRAND_CD GROUP BY twub.USER_ID ) twub WHERE the.HQ_OFFICE_CD = #{hqOfficeCd} AND the.USER_ID = twub.USER_ID(+) AND the.EMP_NO != '0000' AND the.USER_ID IS NOT NULL AND the.EMP_NO NOT IN ( SELECT DISTINCT THE.EMP_NO FROM TB_HQ_EMPLOYEE the , TB_WB_USER_INFO twu , TB_WB_AUTHOR_EXCEPT_V01 twa WHERE the.HQ_OFFICE_CD = #{hqOfficeCd} AND twu.USER_ID = the.USER_ID AND twa.USER_ID = twu.USER_ID AND twa.INCLD_EXCLD_FG = 'E' AND twa.USE_YN = 'Y' AND twa.RESRCE_CD = #{sMenuCd} )
AND the.EMP_NO LIKE '%'||#{empNo}||'%'
AND the.EMP_NM LIKE '%'||#{empNm}||'%'
AND the.USER_ID LIKE '%'||#{userId}||'%'
AND the.MP_NO LIKE '%'||#{mpNo}||'%'
AND twub.HQ_BRAND_CD LIKE '%'||#{userHqBrand}||'%'
AND the.BRANCH_CD = #{branchCd}
AND the.MOMS_TEAM = #{momsTeam}
AND the.MOMS_AC_SHOP = #{momsAcShop}
AND the.MOMS_AREA_FG = #{momsAreaFg}
AND the.MOMS_COMMERCIAL = #{momsCommercial}
AND the.MOMS_SHOP_TYPE = #{momsShopType}
AND the.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND the.MOMS_STORE_FG_01 = #{momsStoreFg01}
ORDER BY the.EMP_NO

/* EmpWebMenuMapper.getUnuesdEmp */ SELECT the.EMP_NO , the.EMP_NM , the.USER_ID , the.MP_NO , twub.HQ_BRAND_NM AS USER_HQ_BRAND , (SELECT BRANCH_NM FROM TB_HQ_BRANCH_MOMS WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND BRANCH_CD = the.BRANCH_CD) AS BRANCH_CD , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '151' AND NMCODE_CD = the.MOMS_TEAM) AS MOMS_TEAM , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '152' AND NMCODE_CD = the.MOMS_AC_SHOP) AS MOMS_AC_SHOP , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '153' AND NMCODE_CD = the.MOMS_AREA_FG) AS MOMS_AREA_FG , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '154' AND NMCODE_CD = the.MOMS_COMMERCIAL) AS MOMS_COMMERCIAL , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '155' AND NMCODE_CD = the.MOMS_SHOP_TYPE) AS MOMS_SHOP_TYPE , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '156' AND NMCODE_CD = the.MOMS_STORE_MANAGE_TYPE) AS MOMS_STORE_MANAGE_TYPE , (SELECT NMCODE_NM FROM TB_HQ_NMCODE WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND NMCODE_GRP_CD = '167' AND NMCODE_CD = the.MOMS_STORE_FG_01) AS MOMS_STORE_FG_01 FROM ( SELECT DISTINCT the.EMP_NO , the.EMP_NM , the.USER_ID , the.MP_NO , the.BRANCH_CD , the.MOMS_TEAM , the.MOMS_AC_SHOP , the.MOMS_AREA_FG , the.MOMS_COMMERCIAL , the.MOMS_SHOP_TYPE , the.MOMS_STORE_MANAGE_TYPE , the.MOMS_STORE_FG_01 FROM TB_HQ_EMPLOYEE the , TB_WB_USER_INFO twu , TB_WB_AUTHOR_EXCEPT_V01 twa WHERE the.HQ_OFFICE_CD = #{hqOfficeCd} AND twu.USER_ID = the.USER_ID AND twa.USER_ID = twu.USER_ID AND twa.INCLD_EXCLD_FG = 'E' AND twa.USE_YN = 'Y' AND twa.RESRCE_CD = #{sMenuCd} ) the , ( SELECT twub.USER_ID , LISTAGG(twub.HQ_BRAND_CD, ',') WITHIN GROUP (ORDER BY twub.HQ_BRAND_CD) AS HQ_BRAND_CD , LISTAGG(thb.HQ_BRAND_NM, ',') WITHIN GROUP (ORDER BY thb.HQ_BRAND_NM) AS HQ_BRAND_NM FROM TB_WB_USER_BRAND twub , TB_HQ_BRAND thb WHERE 1=1 AND thb.HQ_OFFICE_CD = #{hqOfficeCd} AND thb.HQ_BRAND_CD = twub.HQ_BRAND_CD GROUP BY twub.USER_ID ) twub WHERE the.USER_ID = twub.USER_ID(+) AND the.EMP_NO != '0000'
AND the.EMP_NO LIKE '%'||#{empNo}||'%'
AND the.EMP_NM LIKE '%'||#{empNm}||'%'
AND the.USER_ID LIKE '%'||#{userId}||'%'
AND the.MP_NO LIKE '%'||#{mpNo}||'%'
AND twub.HQ_BRAND_CD LIKE '%'||#{userHqBrand}||'%'
AND the.BRANCH_CD = #{branchCd}
AND the.MOMS_TEAM = #{momsTeam}
AND the.MOMS_AC_SHOP = #{momsAcShop}
AND the.MOMS_AREA_FG = #{momsAreaFg}
AND the.MOMS_COMMERCIAL = #{momsCommercial}
AND the.MOMS_SHOP_TYPE = #{momsShopType}
AND the.MOMS_STORE_MANAGE_TYPE = #{momsStoreManageType}
AND the.MOMS_STORE_FG_01 = #{momsStoreFg01}
ORDER BY the.EMP_NO

mapper.delete:
/* EmpWebMenuMapper.getEmpWebMenuSaveDelete */
DELETE  TB_WB_AUTHOR_EXCEPT
WHERE   USER_ID = #{userId}
AND     RESRCE_CD = #{sMenuCd}
