mapper.select:
/* USE : Media.getList */
SELECT A.ADVER_FILE_NO AS VER_SER_NO, TO_CHAR(TO_DATE(A.ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(A.ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, A.FILE_DESC AS VER_SER_NM, A.FILE_USE_TYPE, A.FILE_SIZE, A.USE_YN, A.FILE_ORG_NM || '.' || A.FILE_EXT AS FILE_ORG_NM, A.DISP_TIME, A.FILE_NM AS FILE_NM_EXT, A.FILE_EXT, A.FILE_ORG_NM AS REAL_FILE_ORG_NM, NVL(B.STORE_CNT, 0) AS STORE_CNT, A.LANG_FG
FROM TB_HQ_POS_ADVER_FILE A, ( SELECT ADVER_FILE_NO, COUNT(STORE_CD) AS STORE_CNT FROM TB_HQ_POS_ADVER_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd} GROUP BY ADVER_FILE_NO ) B WHERE A.HQ_OFFICE_CD = #{hqOfficeCd}
AND A.ADVER_FILE_NO LIKE '%' || #{verSerNo} || '%'
AND A.FILE_DESC LIKE '%' || #{verSerNm} || '%'
AND A.FILE_ORG_NM LIKE '%' || #{fileOrgNm} || '%'
AND A.USE_YN = #{useYn}
AND A.FILE_USE_TYPE = #{fileType}
AND #{startDate} BETWEEN A.ADVER_S_DATE AND A.ADVER_E_DATE
AND A.LANG_FG = #{langFg}
AND A.FILE_USE_TYPE != '008' AND B.ADVER_FILE_NO (+)= A.ADVER_FILE_NO ORDER BY A.ADVER_FILE_NO DESC
SELECT A.ADVER_FILE_NO AS VER_SER_NO, TO_CHAR(TO_DATE(A.ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(A.ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, A.FILE_DESC AS VER_SER_NM, A.FILE_USE_TYPE, A.FILE_SIZE, A.USE_YN, A.FILE_ORG_NM || '.' || A.FILE_EXT AS FILE_ORG_NM, A.DISP_SEQ, A.DISP_TIME, A.FILE_NM AS FILE_NM_EXT, A.FILE_EXT, A.FILE_ORG_NM AS REAL_FILE_ORG_NM, A.LANG_FG
FROM TB_MS_POS_ADVER_FILE A WHERE A.STORE_CD = #{storeCd}
AND A.ADVER_FILE_NO LIKE '%' || #{verSerNo} || '%'
AND A.FILE_DESC LIKE '%' || #{verSerNm} || '%'
AND A.FILE_ORG_NM LIKE '%' || #{fileOrgNm} || '%'
AND A.USE_YN = #{useYn}
AND A.FILE_USE_TYPE = #{fileType}
AND #{startDate} BETWEEN A.ADVER_S_DATE AND A.ADVER_E_DATE
AND A.LANG_FG = #{langFg}
AND A.FILE_USE_TYPE != '008' ORDER BY A.ADVER_FILE_NO DESC

/* USE : Media.dtlInfo */
SELECT ADVER_FILE_NO AS VER_SER_NO, FILE_USE_TYPE AS FILE_TYPE, TO_CHAR(TO_DATE(ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, ADVER_S_DATE AS START_DATE, ADVER_E_DATE AS END_DATE, FILE_DIR, FILE_NM, FILE_EXT, FILE_ORG_NM || '.' || FILE_EXT AS FILE_ORG_NM, FILE_SIZE, FILE_MIME_TYPE, FILE_ORG_NM, FILE_DESC AS VER_SER_NM, USE_YN, REG_DT, DISP_TIME, PROMOTION_CD, NVL2(PROMOTION_CD,(SELECT PROMOTION_NM FROM TB_HQ_PROMO_H thph WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND thph.PROMOTION_CD = thpaf.PROMOTION_CD), '') AS PROMOTION_NM, LANG_FG, ADVER_URL FROM TB_HQ_POS_ADVER_FILE thpaf WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ADVER_FILE_NO = #{verSerNo}
SELECT ADVER_FILE_NO AS VER_SER_NO, FILE_USE_TYPE AS FILE_TYPE, TO_CHAR(TO_DATE(ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, ADVER_S_DATE AS START_DATE, ADVER_E_DATE AS END_DATE, FILE_DIR, FILE_NM, FILE_EXT, FILE_ORG_NM || '.' || FILE_EXT AS FILE_ORG_NM, FILE_SIZE, FILE_MIME_TYPE, FILE_ORG_NM, FILE_DESC AS VER_SER_NM, USE_YN, REG_DT, DISP_TIME, PROMOTION_CD, NVL2(PROMOTION_CD,(SELECT PROMOTION_NM FROM TB_MS_PROMO_H thph WHERE STORE_CD = #{storeCd} AND thph.PROMOTION_CD = tmpaf.PROMOTION_CD), '') AS PROMOTION_NM, LANG_FG, ADVER_URL FROM TB_MS_POS_ADVER_FILE tmpaf WHERE STORE_CD = #{storeCd} AND ADVER_FILE_NO = #{verSerNo}

/* USE : Media.dtlInfo2 */


    SELECT FILE_NM
    FROM TB_HQ_POS_ADVER_FILE
    WHERE HQ_OFFICE_CD = #{hqOfficeCd}
    AND ADVER_FILE_NO = #{verSerNo}


    SELECT FILE_NM
    FROM TB_MS_POS_ADVER_FILE
    WHERE STORE_CD = #{storeCd}
    AND ADVER_FILE_NO = #{verSerNo}

/* USE : Media.getFileCd */

        SELECT LPAD(NVL(MAX(ADVER_FILE_NO), 0) +1, 4, '0')
        FROM TB_HQ_POS_ADVER_FILE
        WHERE HQ_OFFICE_CD = #{hqOfficeCd}


        SELECT LPAD(NVL(MAX(ADVER_FILE_NO), 7999) +1, 4, '0')
        FROM TB_MS_POS_ADVER_FILE
        WHERE STORE_CD = #{storeCd}
        AND ADVER_FILE_NO > 7999

mapper.insert:
/* USE : Media.verRegist */
INSERT INTO TB_HQ_POS_ADVER_FILE ( HQ_OFFICE_CD, ADVER_FILE_NO, FILE_USE_TYPE, ADVER_S_DATE, ADVER_E_DATE, FILE_DIR, FILE_NM, FILE_EXT, FILE_SIZE, FILE_MIME_TYPE, FILE_ORG_NM, FILE_DESC, FILE_CHG_DT, USE_YN, REG_DT, REG_ID, MOD_DT, MOD_ID, DISP_SEQ, DISP_TIME, LANG_FG, ADVER_URL ) VALUES ( #{hqOfficeCd}, #{verSerNo}, #{fileType}, #{startDate}, #{endDate}, #{fileDir}, #{fileNm}, #{fileExt}, #{fileSize}, #{fileMimeType}, #{fileOrgNm}, #{verSerNm}, #{regDt}, #{useYn}, #{regDt}, #{regId}, #{modDt}, #{modId}, #{dispSeq}, #{dispTime}, #{langFg}, #{adverUrl} )
INSERT INTO TB_MS_POS_ADVER_FILE ( STORE_CD, ADVER_FILE_NO, FILE_USE_TYPE, ADVER_S_DATE, ADVER_E_DATE, FILE_DIR, FILE_NM, FILE_EXT, FILE_SIZE, FILE_MIME_TYPE, FILE_ORG_NM, FILE_DESC, FILE_CHG_DT, USE_YN, REG_FG, REG_DT, REG_ID, MOD_DT, MOD_ID, DISP_SEQ, DISP_TIME, LANG_FG, ADVER_URL ) VALUES ( #{storeCd}, #{verSerNo}, #{fileType}, #{startDate}, #{endDate}, #{fileDir}, #{fileNm}, #{fileExt}, #{fileSize}, #{fileMimeType}, #{fileOrgNm}, #{verSerNm}, #{regDt}, #{useYn}, 'S', #{regDt}, #{regId}, #{modDt}, #{modId}, #{dispSeq}, #{dispTime}, #{langFg}, #{adverUrl} )

mapper.update:
/* USE : Media.verModify */
UPDATE TB_HQ_POS_ADVER_FILE SET FILE_USE_TYPE = #{fileType}, ADVER_S_DATE = #{startDate}, ADVER_E_DATE = #{endDate},
FILE_DIR = #{fileDir}, FILE_NM = #{fileNm}, FILE_EXT = #{fileExt}, FILE_SIZE = #{fileSize}, FILE_MIME_TYPE = #{fileMimeType}, FILE_ORG_NM = #{fileOrgNm}, FILE_CHG_DT = #{modDt},
FILE_DESC = #{verSerNm}, USE_YN = #{useYn}, MOD_DT = #{modDt}, MOD_ID = #{modId}, DISP_TIME = #{dispTime}, LANG_FG = #{langFg}, ADVER_URL = #{adverUrl} WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND ADVER_FILE_NO = #{verSerNo}
UPDATE TB_MS_POS_ADVER_FILE SET FILE_USE_TYPE = #{fileType}, ADVER_S_DATE = #{startDate}, ADVER_E_DATE = #{endDate},
FILE_DIR = #{fileDir}, FILE_NM = #{fileNm}, FILE_EXT = #{fileExt}, FILE_SIZE = #{fileSize}, FILE_MIME_TYPE = #{fileMimeType}, FILE_ORG_NM = #{fileOrgNm}, FILE_CHG_DT = #{modDt},
FILE_DESC = #{verSerNm}, USE_YN = #{useYn}, MOD_DT = #{modDt}, MOD_ID = #{modId}, DISP_TIME = #{dispTime}, LANG_FG = #{langFg}, ADVER_URL = #{adverUrl} WHERE STORE_CD = #{storeCd} AND ADVER_FILE_NO = #{verSerNo}

mapper.select:
/* USE : Media.getFileType */
SELECT TRIM(NMCODE_ITEM_2)
  FROM TB_CM_NMCODE
 WHERE NMCODE_GRP_CD ='303'
   AND NMCODE_CD = #{fileType}

/* USE : Media.getFileTypeNm */
SELECT NMCODE_NM
FROM TB_CM_NMCODE
WHERE NMCODE_GRP_CD ='303'
  AND NMCODE_CD = #{fileType}

/* USE : Media.chkDate */


    SELECT COUNT(*)
      FROM TB_HQ_POS_ADVER_FILE
     WHERE HQ_OFFICE_CD = #{hqOfficeCd}
       AND ADVER_S_DATE <= #{endDate}
       AND ADVER_E_DATE >= #{startDate}
       AND FILE_USE_TYPE = #{fileType}
       AND USE_YN = #{useYn}


       AND ADVER_FILE_NO != #{verSerNo}




    SELECT COUNT(*)
      FROM TB_MS_POS_ADVER_FILE
     WHERE STORE_CD = #{storeCd}
       AND ADVER_S_DATE <= #{endDate}
       AND ADVER_E_DATE >= #{startDate}
       AND FILE_USE_TYPE = #{fileType}
       AND USE_YN = #{useYn}


       AND ADVER_FILE_NO != #{verSerNo}

mapper.update:
/* USE : Media.deleteVersion */
UPDATE TB_CM_POS_ADDR_VERSN
   SET DEL_YN = 'Y',
       USE_YN = 'N'
 WHERE VER_SER_NO = #{verSerNo}

mapper.select:
/* USE : Media.srchStoreList */ SELECT tms.HQ_OFFICE_CD, tho.HQ_OFFICE_NM, tms.STORE_CD, tms.STORE_NM, tms.SYS_STAT_FG, tmp.POS_CNT FROM TB_MS_STORE tms, TB_HQ_OFFICE tho, ( SELECT STORE_CD, COUNT(POS_NO) AS POS_CNT FROM TB_MS_POS WHERE USE_YN ='Y' GROUP by STORE_CD ) tmp WHERE tms.HQ_OFFICE_CD = #{hqOfficeCd} AND tho.HQ_OFFICE_CD = tms.HQ_OFFICE_CD AND tmp.STORE_CD = tms.STORE_CD
AND tms.STORE_CD IN (SELECT STORE_CD FROM TB_HQ_POS_ADVER_STORE WHERE ADVER_FILE_NO = #{verSerNo} )
AND tms.STORE_CD NOT IN (SELECT STORE_CD FROM TB_HQ_POS_ADVER_STORE WHERE ADVER_FILE_NO = #{verSerNo} )
AND tms.STORE_CD IN (${storeCdQuery})
AND tms.SYS_STAT_FG = #{sysStatFg}
AND tms.HQ_BRAND_CD = #{storeHqBrandCd} -- 선택한 매장브랜드가 있을 때
-- 선택한 매장브랜드가 없을 때('전체' 일때), 사용자 관리브랜드에 속한 정보만 조회 AND tms.HQ_BRAND_CD IN
#{item}
AND tms.STORE_CD IN ( SELECT A.STORE_CD FROM ( SELECT DISTINCT STORE_CD, POS_NO FROM TB_MS_POS_ENVST WHERE STORE_CD IN (SELECT store_cd FROM tb_ms_store WHERE hQ_office_cd = #{hqOfficeCd}) AND (ENVST_CD = '4019' AND ENVST_VAL = '6') -- 키오스크 ) A , ( SELECT DISTINCT STORE_CD, POS_NO FROM TB_MS_POS_ENVST WHERE STORE_CD IN (SELECT store_cd FROM tb_ms_store WHERE hQ_office_cd = #{hqOfficeCd}) AND (ENVST_CD IN ('4068', '4069') AND ENVST_VAL = #{tuClsType}) ) B WHERE A.STORE_CD = B.STORE_CD AND A.POS_NO = B.POS_NO )
ORDER BY HQ_OFFICE_CD, STORE_CD

mapper.delete:
/* USE : Media.removeStore */

DELETE TB_HQ_POS_ADVER_STORE
 WHERE ADVER_FILE_NO = #{verSerNo}
   AND STORE_CD = #{storeCd}

mapper.select:
/* USE : Media.getDispSeq */

    SELECT NVL(MAX(DISP_SEQ), 0) +1 AS DISP_SEQ
    FROM TB_HQ_POS_ADVER_FILE
    WHERE HQ_OFFICE_CD = #{hqOfficeCd}
    AND FILE_USE_TYPE = #{fileType}


    SELECT NVL(MAX(DISP_SEQ), 0) +1 AS DISP_SEQ
    FROM TB_MS_POS_ADVER_FILE
    WHERE STORE_CD = #{storeCd}
    AND FILE_USE_TYPE = #{fileType}

mapper.delete:
/* MediaMapper.getMediaDelete */



        DELETE
        TB_HQ_POS_ADVER_FILE
        WHERE 1=1
        AND HQ_OFFICE_CD = #{hqOfficeCd}
        AND ADVER_FILE_NO = #{verSerNo}



        DELETE
        TB_MS_POS_ADVER_FILE
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND ADVER_FILE_NO = #{verSerNo}

/* MediaMapper.getMediaHqStoreDelete */
DELETE
TB_HQ_POS_ADVER_STORE
WHERE 1=1
AND HQ_OFFICE_CD = #{hqOfficeCd}
AND ADVER_FILE_NO = #{verSerNo}

mapper.select:
/* MediaMapper.getMediaPlaySeqList */
SELECT ADVER_FILE_NO AS VER_SER_NO, TO_CHAR(TO_DATE(ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, FILE_DESC AS VER_SER_NM, FILE_USE_TYPE, FILE_SIZE, USE_YN, FILE_ORG_NM || '.' || FILE_EXT AS FILE_ORG_NM, DISP_TIME, DISP_SEQ, LANG_FG FROM TB_HQ_POS_ADVER_FILE WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd} AND FILE_USE_TYPE = #{fileType} ORDER BY DISP_SEQ
SELECT ADVER_FILE_NO AS VER_SER_NO, TO_CHAR(TO_DATE(ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, FILE_DESC AS VER_SER_NM, FILE_USE_TYPE, FILE_SIZE, USE_YN, FILE_ORG_NM || '.' || FILE_EXT AS FILE_ORG_NM, DISP_TIME, DISP_SEQ, LANG_FG FROM TB_MS_POS_ADVER_FILE WHERE 1=1 AND STORE_CD = #{storeCd} AND FILE_USE_TYPE = #{fileType} ORDER BY DISP_SEQ

mapper.update:
/* MediaMapper.getMediaPlaySeqSaveUpdate */



        UPDATE
        TB_HQ_POS_ADVER_FILE
        SET
        DISP_SEQ = #{dispSeq},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND HQ_OFFICE_CD = #{hqOfficeCd}
        AND ADVER_FILE_NO = #{verSerNo}



        UPDATE
        TB_MS_POS_ADVER_FILE
        SET
        DISP_SEQ = #{dispSeq},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
        WHERE 1=1
        AND STORE_CD = #{storeCd}
        AND ADVER_FILE_NO = #{verSerNo}

mapper.select:
/* USE : Media.getMediaStoreApplyList */
SELECT A.STORE_CD , (SELECT C.STORE_NM FROM TB_MS_STORE C WHERE C.STORE_CD = A.STORE_CD) AS STORE_NM , A.ADVER_FILE_NO AS VER_SER_NO , B.FILE_DESC AS VER_SER_NM , TO_CHAR(TO_DATE(B.ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(B.ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE , B.FILE_ORG_NM || '.' || B.FILE_EXT AS FILE_ORG_NM , B.FILE_USE_TYPE , B.LANG_FG , B.FILE_SIZE , B.USE_YN , B.DISP_TIME , B.FILE_NM AS FILE_NM_EXT , B.FILE_EXT , B.FILE_ORG_NM AS REAL_FILE_ORG_NM
FROM TB_HQ_POS_ADVER_STORE A , TB_HQ_POS_ADVER_FILE B WHERE A.HQ_OFFICE_CD = #{hqOfficeCd} AND B.HQ_OFFICE_CD = A.HQ_OFFICE_CD AND B.ADVER_FILE_NO = A.ADVER_FILE_NO
AND A.STORE_CD IN (${storeCdQuery})
AND B.ADVER_FILE_NO LIKE '%' || #{verSerNo} || '%'
AND B.FILE_DESC LIKE '%' || #{verSerNm} || '%'
AND B.FILE_ORG_NM LIKE '%' || #{fileOrgNm} || '%'
AND B.USE_YN = #{useYn}
AND B.FILE_USE_TYPE = #{fileType}
AND #{startDate} BETWEEN B.ADVER_S_DATE AND B.ADVER_E_DATE
AND B.LANG_FG = #{langFg}
ORDER BY A.STORE_CD , B.FILE_USE_TYPE

/* USE : Media.chkFileTypeCnt */

    SELECT  COUNT(*)
    FROM    TB_HQ_POS_ADVER_FILE
    WHERE   HQ_OFFICE_CD    = #{hqOfficeCd}
    AND     FILE_USE_TYPE   = #{fileType}
    AND     USE_YN          = 'Y'
    AND     ADVER_FILE_NO   NOT IN (#{verSerNo})


    SELECT  COUNT(*)
    FROM    TB_MS_POS_ADVER_FILE
    WHERE   STORE_CD        = #{storeCd}
    AND     FILE_USE_TYPE   = #{fileType}
    AND     USE_YN          = 'Y'
    AND     ADVER_FILE_NO   NOT IN (#{verSerNo})
