mapper.select:
/* MemberTermsMapper.getMemberTermsList */
SELECT ADVER_FILE_NO AS VER_SER_NO, TO_CHAR(TO_DATE(ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, FILE_DESC AS VER_SER_NM, FILE_USE_TYPE, FILE_SIZE, USE_YN, FILE_ORG_NM || '.' || FILE_EXT AS FILE_ORG_NM
FROM TB_HQ_POS_ADVER_FILE WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd}
AND ADVER_FILE_NO LIKE '%' || #{verSerNo} || '%'
AND FILE_DESC LIKE '%' || #{verSerNm} || '%'
AND FILE_ORG_NM LIKE '%' || #{fileOrgNm} || '%'
AND USE_YN = #{useYn}
AND FILE_USE_TYPE = #{fileType}
AND #{startDate} BETWEEN ADVER_S_DATE AND ADVER_E_DATE
ORDER BY REG_DT DESC
SELECT ADVER_FILE_NO AS VER_SER_NO, TO_CHAR(TO_DATE(ADVER_S_DATE, 'yyyymmdd'), 'yyyy-mm-dd') || ' ~ ' || TO_CHAR(TO_DATE(ADVER_E_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS USE_DATE, FILE_DESC AS VER_SER_NM, FILE_USE_TYPE, FILE_SIZE, USE_YN, FILE_ORG_NM || '.' || FILE_EXT AS FILE_ORG_NM
FROM TB_MS_POS_ADVER_FILE WHERE 1=1 AND STORE_CD = #{storeCd}
AND ADVER_FILE_NO LIKE '%' || #{verSerNo} || '%'
AND FILE_DESC LIKE '%' || #{verSerNm} || '%'
AND FILE_ORG_NM LIKE '%' || #{fileOrgNm} || '%'
AND USE_YN = #{useYn}
AND FILE_USE_TYPE = #{fileType}
AND #{startDate} BETWEEN ADVER_S_DATE AND ADVER_E_DATE
ORDER BY REG_DT DESC

mapper.update:
/* MemberTermsMapper.getMemberTermsRegistPreUpdateSave */
UPDATE TB_HQ_POS_ADVER_FILE SET USE_YN = 'N', MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE 1=1 AND HQ_OFFICE_CD = #{hqOfficeCd} AND FILE_USE_TYPE = '008'
UPDATE TB_MS_POS_ADVER_FILE SET USE_YN = 'N', MOD_DT = #{modDt}, MOD_ID = #{modId} WHERE 1=1 AND STORE_CD = #{storeCd} AND FILE_USE_TYPE = '008'
