mapper.select:
/* USE : StoreEmpMapper.getStoreEmpList */
SELECT tme.STORE_CD, tme.EMP_NO, tme.EMP_NM, tme.EMP_PWD, tme.WEB_USE_YN, tcn_web_use_yn.NMCODE_NM AS WEB_USE_YN_NM, tme.USER_ID, tme.MP_NO, tme.SERVICE_FG, tcn_service_fg.NMCODE_NM AS SERVICE_FG_NM, tme.SMS_RECV_YN
FROM TB_MS_EMPLOYEE tme LEFT OUTER JOIN TB_CM_NMCODE tcn_service_fg ON tme.SERVICE_FG = tcn_service_fg.NMCODE_CD AND tcn_service_fg.NMCODE_GRP_CD = '007' LEFT OUTER JOIN TB_CM_NMCODE tcn_web_use_yn ON tme.WEB_USE_YN = tcn_web_use_yn.NMCODE_CD AND tcn_web_use_yn.NMCODE_GRP_CD = '067'
AND tme.STORE_CD = #{storeCd}
AND tme.EMP_NO = #{empNo}
AND tme.EMP_NM LIKE '%'||#{empNm}||'%'
AND tme.USER_ID LIKE '%'||#{userId}||'%'
AND tme.SERVICE_FG = #{serviceFg}
AND tme.MP_NO LIKE '%'||#{mpNo}||'%'
AND tme.WEB_USE_YN = #{webUseYn}
AND tme.SMS_RECV_YN = #{smsRecvYn}
AND tme.REG_DT BETWEEN #{startDate}||'000000' AND #{endDate}||'235959' --조회(등록)일자
ORDER BY tme.REG_DT DESC, tme.EMP_NO

/* USE : StoreEmpMapper.getStoreUserIdCnt */
SELECT COUNT(1) AS CNT
  FROM TB_WB_USER_INFO
 WHERE USER_ID = #{userId}

/* USE : StoreEmpMapper.getStoreEmpPassword */
SELECT twui.USER_PWD
  FROM TB_WB_USER_INFO twui
 WHERE twui.USER_ID = #{userId}

mapper.update:
/* USE : StoreEmpMapper.updateStoreEmpInfo */
UPDATE TB_MS_EMPLOYEE tme
   SET tme.EMP_NM = #{empNm},
       tme.WEB_USE_YN = #{webUseYn},
       tme.USER_ID = #{userId},
       tme.MP_NO = #{mpNo},
       tme.SERVICE_FG = #{serviceFg},
       tme.SMS_RECV_YN = #{smsRecvYn},
       tme.MOD_ID = #{regId},
       tme.MOD_DT = #{regDt}
 WHERE tme.STORE_CD = #{storeCd}
   AND tme.EMP_NO = #{empNo}

/* USE : StoreEmpMapper.saveWbUserInfo */ MERGE INTO TB_WB_USER_INFO twui USING (SELECT the.USER_ID FROM TB_MS_EMPLOYEE the WHERE the.STORE_CD = #{storeCd} AND the.EMP_NO = #{empNo} ) org ON (twui.USER_ID = org.USER_ID) WHEN MATCHED THEN UPDATE SET twui.USE_YN = #{webUseYn},
twui.USER_PWD = #{userPwd},
twui.MOD_DT = #{modDt}, twui.MOD_ID = #{modId} WHEN NOT MATCHED THEN INSERT ( twui.USER_ID, twui.USER_PWD, twui.AUTH_GRP_CD, twui.LOGIN_FAIL_CNT, twui.USE_YN, twui.REG_DT, twui.REG_ID, twui.MOD_DT, twui.MOD_ID ) VALUES ( #{userId}, #{userPwd}, #{authGrpCd}, '0', #{webUseYn}, #{regDt}, #{regId}, #{modDt}, #{modId} )

/* USE : StoreEmpMapper.updateUserPassword */
UPDATE TB_WB_USER_INFO
   SET USER_PWD = #{userPwd},
       LAST_PWD_CHG_DT = #{modDt},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE USER_ID = #{userId}
