mapper.select:
/* USE : StoreTypeMapper.getStoreType */ SELECT HQ_OFFICE_CD, STORE_TYPE_CD, STORE_TYPE_NM, REMARK, USE_YN FROM TB_HQ_STORE_TYPE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_TYPE_NM LIKE '%' || #{storeTypeNm} || '%'
AND USE_YN = #{useYn}
ORDER BY STORE_TYPE_CD ASC

/* USE : StoreTypeMapper.getStoreTypeCode */
SELECT LPAD(NVL(MAX(STORE_TYPE_CD), '0') + 1, 3, '0')
  FROM TB_HQ_STORE_TYPE
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}

mapper.update:
/* USE : StoreTypeMapper.updateStoreType */
UPDATE TB_HQ_STORE_TYPE
   SET STORE_TYPE_NM = #{storeTypeNm},
        REMARK = #{remark},
        USE_YN = #{useYn},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_TYPE_CD = #{storeTypeCd}

mapper.delete:
/* StoreTypeMapper.deleteStoreMapping */
DELETE TB_HQ_STORE_TYPE_STORE
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_TYPE_CD = #{storeTypeCd}
   AND STORE_CD = #{storeCd}

mapper.select:
/* StoreTypeMapper.getStoreList */ SELECT HQ_OFFICE_CD, STORE_CD, STORE_NM, SYS_STAT_FG FROM TB_MS_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_CD LIKE '%'||#{storeCd}||'%' --매장코드
AND STORE_NM LIKE '%'||#{storeNm}||'%' --매장명
AND SYS_STAT_FG = #{sysStatFg} --매장상태구분
AND STORE_CD NOT IN (SELECT STORE_CD FROM TB_HQ_STORE_TYPE_STORE WHERE HQ_OFFICE_CD = #{hqOfficeCd}) ORDER BY STORE_CD ASC

/* StoreTypeMapper.getStoreMappingYn */
SELECT COUNT(1)
  FROM TB_HQ_STORE_TYPE_STORE
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_CD = #{storeCd}

mapper.delete:
/* StoreTypeMapper.deleteMenuGroupMapping */
DELETE TB_HQ_STORE_TYPE_PROD_GROUP
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_TYPE_CD = #{storeTypeCd}
   AND STORE_GROUP_CD = #{storeGroupCd}

mapper.select:
/* StoreTypeMapper.getMenuGroupList */ SELECT HQ_OFFICE_CD, STORE_GROUP_CD, STORE_GROUP_NM, USE_YN FROM TB_HQ_STORE_PROD_GROUP WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_GROUP_CD LIKE '%'||#{storeGroupCd}||'%' --메뉴그룹코드
AND STORE_GROUP_NM LIKE '%'||#{storeGroupNm}||'%' --메뉴그룹명
AND USE_YN = #{useYn}
AND STORE_GROUP_CD NOT IN (SELECT STORE_GROUP_CD FROM TB_HQ_STORE_TYPE_PROD_GROUP WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND STORE_TYPE_CD = #{storeTypeCd}) ORDER BY STORE_GROUP_CD ASC

/* USE : StoreTypeMapper.getMenuGroup */ SELECT HQ_OFFICE_CD, STORE_GROUP_CD, STORE_GROUP_NM, REMARK, USE_YN FROM TB_HQ_STORE_PROD_GROUP WHERE HQ_OFFICE_CD = #{hqOfficeCd}
AND STORE_GROUP_NM LIKE '%' || #{storeGroupNm} || '%'
AND USE_YN = #{useYn}
ORDER BY STORE_GROUP_CD ASC

/* USE : StoreTypeMapper.getMenuGroupCode */
SELECT LPAD(NVL(MAX(STORE_GROUP_CD), '0') + 1, 3, '0')
  FROM TB_HQ_STORE_PROD_GROUP
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}

mapper.update:
/* USE : StoreTypeMapper.updateMenuGroup */
UPDATE TB_HQ_STORE_PROD_GROUP
   SET STORE_GROUP_NM = #{storeGroupNm},
        REMARK = #{remark},
        USE_YN = #{useYn},
        MOD_DT = #{modDt},
        MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_GROUP_CD = #{storeGroupCd}

mapper.delete:
/* StoreTypeMapper.deleteProdMapping */
DELETE TB_HQ_STORE_PROD_GROUP_DTL
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_GROUP_CD = #{storeGroupCd}
   AND PROD_CD = #{prodCd}

mapper.update:
/* USE : StoreTypeMapper.updateProdMapping */
UPDATE TB_HQ_STORE_PROD_GROUP_DTL
    SET SALE_UPRC = #{saleUprc},
         STIN_SALE_UPRC = NVL(#{stinSaleUprc}, #{saleUprc}),
         DLVR_SALE_UPRC = NVL(#{dlvrSaleUprc}, #{saleUprc}),
         PACK_SALE_UPRC = NVL(#{packSaleUprc}, #{saleUprc}),
         MOD_DT = #{modDt},
         MOD_ID = #{modId}
  WHERE HQ_OFFICE_CD = #{hqOfficeCd}
    AND STORE_GROUP_CD = #{storeGroupCd}
    AND PROD_CD = #{prodCd}

mapper.select:
/* StoreTypeMapper.getBrandList */
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT HQ_BRAND_CD AS VALUE, HQ_BRAND_NM AS NAME FROM TB_HQ_BRAND WHERE HQ_OFFICE_CD = #{hqOfficeCd} AND USE_YN = 'Y' ORDER BY VALUE NULLS FIRST
SELECT '' AS VALUE, '전체' AS NAME FROM DUAL UNION SELECT MS_BRAND_CD AS VALUE, MS_BRAND_NM AS NAME FROM TB_MS_BRAND WHERE STORE_CD = #{storeCd} AND USE_YN = 'Y' ORDER BY VALUE NULLS FIRST

/* StoreTypeMapper.getStoreTypeChgHist */ SELECT TO_CHAR(TO_DATE(A.PROC_DT ,'YYYYMMDDhh24miss'), 'YYYY/MM/DD hh24:mi:ss') AS PROC_DT, A.PROC_FG, A.STORE_TYPE_CD, A.STORE_TYPE_NM, A.B_STORE_TYPE_NM, A.REMARK, A.B_REMARK, A.OPTION01, A.B_OPTION01, A.USE_YN, A.B_USE_YN, A.REG_DT, A.B_REG_DT, A.REG_ID, A.B_REG_ID, A.MOD_DT, A.B_MOD_DT, A.MOD_ID, A.B_MOD_ID FROM TB_LG_HQ_STORE_TYPE_LOG A WHERE A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.PROC_DT BETWEEN #{startDate} || '000000' AND #{endDate} || '999999'
AND A.STORE_TYPE_CD LIKE '%' || #{storeTypeCd} || '%'
AND A.STORE_TYPE_NM LIKE '%' || #{storeTypeNm} || '%'
AND A.PROC_FG = #{procFg}
ORDER BY A.PROC_DT DESC

/* StoreTypeMapper.getMenuGroupChgHist */ SELECT TO_CHAR(TO_DATE(A.PROC_DT ,'YYYYMMDDhh24miss'), 'YYYY/MM/DD hh24:mi:ss') AS PROC_DT, A.PROC_FG, A.STORE_GROUP_CD, B.STORE_GROUP_NM, A.REMARK, A.B_REMARK, A.OPTION01, A.B_OPTION01, A.USE_YN, A.B_USE_YN, A.REG_DT, A.B_REG_DT, A.REG_ID, A.B_REG_ID, A.MOD_DT, A.B_MOD_DT, A.MOD_ID, A.B_MOD_ID FROM TB_LG_HQ_STORE_PROD_GROUP_LOG A, TB_HQ_STORE_PROD_GROUP B WHERE A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.PROC_DT BETWEEN #{startDate} || '000000' AND #{endDate} || '999999' AND A.HQ_OFFICE_CD = B.HQ_OFFICE_CD AND A.STORE_GROUP_CD = B.STORE_GROUP_CD
AND A.STORE_GROUP_CD LIKE '%' || #{storeGroupCd} || '%'
AND B.STORE_GROUP_NM LIKE '%' || #{storeGroupNm} || '%'
AND A.PROC_FG = #{procFg}
ORDER BY A.PROC_DT DESC
