mapper.select:
/* USE : SystemEmpPwdManageMapper.getPwdManageList */
SELECT emp.*
FROM ( SELECT tce.ADMIN_FG, tce.EMP_NO, tce.EMP_NM AS USER_NM, tce.USER_ID, tce.SERVICE_FG, tce.WEB_USE_YN, tce.MP_NO, tce.SMS_RECV_YN FROM TB_CM_EMPLOYEE tce LEFT OUTER JOIN TB_CM_NMCODE tcn ON tce.SERVICE_FG = tcn.NMCODE_CD AND tcn.NMCODE_GRP_CD = '007' WHERE 1=1
AND tce.USER_ID LIKE #{userId}||'%'
AND tce.EMP_NM LIKE #{userNm}||'%'
) emp
WHERE emp.ADMIN_FG = #{adminFg}
ORDER BY emp.ADMIN_FG ASC, emp.EMP_NO, emp.USER_NM

mapper.update:
/* USE : SystemEmpPwdManageMapper.updatePassword */
UPDATE
    TB_CM_EMPLOYEE
SET
    EMP_PWD = #{newPassword},
    MOD_DT = #{modDt},
    MOD_ID = #{modId}
WHERE
    EMP_NO = #{empNo}
