mapper.select:
/* DlvrRegistMapper.getNewDlvrLzoneCd */
SELECT LPAD( NVL(MAX(DLVR_LZONE_CD), 0) + 1, 3, '0') AS DLVR_LZONE_CD
  FROM TB_MS_DELIVERY_LZONE
 WHERE STORE_CD = #{storeCd}

mapper.insert:
/* DlvrRegistMapper.insertDlvrRegistInfo */
INSERT INTO TB_MS_DELIVERY_LZONE (
    STORE_CD
    , DLVR_LZONE_CD
    , DLVR_LZONE_NM
    , PAGE_NO
    , EMP_NO
    , IN_FG
    , USE_YN
    , REG_DT
    , REG_ID
    , MOD_DT
    , MOD_ID
) VALUES (
    #{storeCd}
    , #{dlvrLzoneCd}
    , #{dlvrLzoneNm}
    , #{pageNo}
    , #{empNo}
    , #{inFg}
    , #{useYn}
    , #{regDt}
    , #{regId}
    , #{modDt}
    , #{modId}
)

mapper.update:
/* DlvrRegistMapper.updateDlvrRegistInfo */
UPDATE TB_MS_DELIVERY_LZONE tmm
   SET
     DLVR_LZONE_NM = #{dlvrLzoneNm}
    , PAGE_NO = #{pageNo}
    , EMP_NO = #{empNo}
    , IN_FG = #{inFg}
    , USE_YN = #{useYn}
    , REG_DT = #{regDt}
    , REG_ID = #{regId}
    , MOD_DT = #{modDt}
    , MOD_ID = #{modId} 
 WHERE STORE_CD = #{storeCd}
 AND DLVR_LZONE_CD = #{dlvrLzoneCd}

mapper.delete:
/* DlvrRegistMapper.deleteDlvrRegistInfo */
UPDATE TB_MS_DELIVERY_LZONE
   SET USE_YN = #{useYn}
 WHERE STORE_CD = #{storeCd}
   AND DLVR_LZONE_CD = #{dlvrLzoneCd}

mapper.select:
/* DlvrRegistMapper.getNewDlvrMzoneCd */
SELECT LPAD( NVL(MAX(DLVR_MZONE_CD), 0) + 1, 3, '0') AS DLVR_MZONE_CD
  FROM TB_MS_DELIVERY_MZONE
 WHERE STORE_CD = #{storeCd}

mapper.insert:
/* DlvrRegistMapper.insertDlvrDetailRegistInfo */
INSERT INTO TB_MS_DELIVERY_MZONE (
    STORE_CD
    , DLVR_MZONE_CD
    , DLVR_MZONE_NM
    , DLVR_LZONE_CD
    , PAGE_NO
    , EMP_NO
    , SBPENC.D(DLVR_ADDR) AS DLVR_ADDR
    , IN_FG
    , USE_YN
    , REG_DT
    , REG_ID
    , MOD_DT
    , MOD_ID
) VALUES (
    #{storeCd}
    , #{dlvrMzoneCd}
    , #{dlvrMzoneNm}
    , #{dlvrLzoneCd}
    , #{pageNo}
    , #{empNo}
    , SBPENC.E(#{dlvrAddr})
    , #{inFg}
    , #{useYn}
    , #{regDt}
    , #{regId}
    , #{modDt}
    , #{modId}
)

mapper.update:
/* DlvrRegistMapper.updateDlvrDetailRegistInfo */
UPDATE TB_MS_DELIVERY_MZONE
   SET DLVR_MZONE_NM = #{dlvrMzoneNm}
    , DLVR_LZONE_CD = #{dlvrLzoneCd}
    , PAGE_NO       = #{pageNo}
    , EMP_NO        = #{empNo}
    , DLVR_ADDR     = SBPENC.E(#{dlvrAddr})
    , IN_FG         = #{inFg}
    , USE_YN        = #{useYn}
    , MOD_DT        = #{modDt}
    , MOD_ID        = #{modId}
 WHERE STORE_CD = #{storeCd}
 AND DLVR_MZONE_CD = #{dlvrMzoneCd}

mapper.delete:
/* DlvrRegistMapper.deleteDlvrDetailRegistInfo */
UPDATE TB_MS_DELIVERY_MZONE
   SET USE_YN = #{useYn}
 WHERE STORE_CD = #{storeCd}
   AND DLVR_MZONE_CD = #{dlvrMzoneCd}
