mapper.select:
/* DayCloseMapper.getDayCloseList */ SELECT A.STORE_CD , A.STORE_NM , TO_CHAR(TO_DATE(A.CLOSE_DATE, 'yyyymmdd'), 'yyyy-mm-dd') AS CLOSE_DATE , C.OPEN_DATE , C.CLOSE_FG , C.INTEREST_AMT , C.IN_AMT , C.OUT_AMT , C.GROUP_AMT , C.HOCKEY_AMT , C.ETC_AMT , C.IN_DAY_AMT , C.IN_SUM , C.IN_MONTH_SUM , C.IN_B_MONTH_SUM , C.IN_TOTAL_SUM , C.OUT_SUM , C.OUT_MONTH_SUM , C.OUT_B_MONTH_SUM , C.OUT_TOTAL_SUM , C.REMARK1 , C.REMARK2 , C.REMARK3 , C.REMARK4 , C.REMARK5 , C.REMARK6 FROM ( SELECT B.STORE_CD , B.STORE_NM , A.YYYYMMDD AS CLOSE_DATE FROM TB_CM_DATEM A , TB_MS_STORE B WHERE A.YYYYMMDD BETWEEN #{closeDate}||'01' AND #{closeDate}||'31' AND B.HQ_OFFICE_CD = #{hqOfficeCd}
AND B.STORE_CD IN (${storeCdQuery})
) A , TB_PO_CLOSE_KWU C WHERE C.STORE_CD (+) = A.STORE_CD AND C.CLOSE_DATE (+) = A.CLOSE_DATE
AND C.CLOSE_FG = '1'
AND C.CLOSE_FG (+) <> '1'
ORDER BY A.STORE_CD , A.CLOSE_DATE

mapper.update:
UPDATE  TB_PO_CLOSE_KWU
SET     CLOSE_FG    = #{closeFg},
        MOD_DT      = #{modDt},
        MOD_ID      = #{modId}
WHERE   STORE_CD    = #{storeCd}
AND     CLOSE_DATE  = #{closeDate}
