mapper.select:
/* DepositDdcMapper.getStoreTotalList */ SELECT A.STORE_CD, B.STORE_NM, SUM(CASE WHEN A.MONEY_FG = '00' THEN A.MONEY_AMT ELSE 0 END) AS DEPOSIT_MONEY_AMT, SUM(CASE WHEN A.MONEY_FG != '00' THEN A.MONEY_AMT ELSE 0 END) AS OTHER_MONEY_AMT, SUM(A.MONEY_AMT) AS TOT_MONEY_AMT FROM TB_PO_HQ_STORE_MONEY A, TB_MS_STORE B WHERE A.HQ_OFFICE_CD = #{hqOfficeCd} AND A.HQ_OFFICE_CD = B.HQ_OFFICE_CD AND A.STORE_CD = B.STORE_CD AND A.MONEY_DATE BETWEEN #{startDate} AND #{endDate}
AND A.STORE_CD IN (${storeCdQuery})
GROUP BY A.STORE_CD, B.STORE_NM

/* DepositDdcMapper.getDepositDdc */
SELECT A.MONEY_FG,
       A.MONEY_AMT,
       A.REMARK,
       (SELECT STORE_NM FROM TB_MS_STORE TMS WHERE TMS.STORE_CD = #{storeCd}) AS STORE_NM
  FROM TB_PO_HQ_STORE_MONEY A
 WHERE A.HQ_OFFICE_CD = #{hqOfficeCd}
   AND A.STORE_CD = #{storeCd}
   AND A.MONEY_DATE = #{moneyDate}
   AND A.SEQ_NO = #{seqNo}

mapper.update:
/* USE : DepositDdcMapper.updateDepositDdc */
UPDATE TB_PO_HQ_STORE_MONEY
   SET MONEY_FG = #{moneyFg},
       MONEY_AMT = #{moneyAmt},
       REMARK = #{remark},
       MOD_DT = #{modDt},
       MOD_ID = #{modId}
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_CD = #{storeCd}
   AND MONEY_DATE = #{moneyDate}
   AND SEQ_NO = #{seqNo}

mapper.delete:
/* DepositDdcMapper.deleteDepositDdc */
DELETE
  FROM TB_PO_HQ_STORE_MONEY
 WHERE HQ_OFFICE_CD = #{hqOfficeCd}
   AND STORE_CD = #{storeCd}
   AND MONEY_DATE = #{moneyDate}
   AND SEQ_NO = #{seqNo}
