mapper.select:
/* SaleRegistMapper.getBillNo */
SELECT  LPAD(NVL(MAX(BILL_NO), 9000) + 1, 4, '0')
FROM    TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = '01'
AND     BILL_NO > '9000'

/* SaleRegistMapper.getCashAmt */
SELECT 	NVL(MAX(PAY_AMT), 0)
FROM 	TB_SL_SALE_HDR_PAY
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}
AND     PAY_CD = '01'
UNION  ALL
SELECT 	NVL(MAX(PAY_AMT), 0)
FROM 	TB_SL_SALE_HDR_PAY
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}
AND     PAY_CD = '02'

/* SaleRegistMapper.getSaleFg */
SELECT 	SALE_FG
FROM 	TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}

mapper.delete:
/* SaleRegistMapper.delSaleHdr */
DELETE  TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = '01'
AND     BILL_NO = #{billNo}
AND     REG_ID like 'WEB_REG_%'

mapper.insert:
/* SaleRegistMapper.getSalePay */ INSERT INTO TB_SL_SALE_PAY ( HQ_OFFICE_CD , HQ_BRAND_CD , STORE_CD , SALE_DATE , POS_NO , BILL_NO , PAY_CD , PAY_AMT , TAX_AMT , VAT_AMT , TIP_AMT , NO_TAX_AMT , REG_DT , REG_ID , MOD_DT , MOD_ID , REG_SEQ , SALE_YN , RECV_AMT , RTN_AMT ) SELECT HQ_OFFICE_CD , HQ_BRAND_CD , STORE_CD , SALE_DATE , POS_NO , BILL_NO , #{payCd} , #{payAmt} * #{saleFg} , TAX_AMT , VAT_AMT , TIP_AMT , NO_TAX_AMT , #{regDt} , #{regId} , #{modDt} , #{modId} , REG_SEQ , SALE_YN
, '0' , '0' FROM TB_SL_SALE_PAY_CARD
, RECV_AMT , RTN_AMT FROM TB_SL_SALE_PAY_CASH
WHERE STORE_CD = #{storeCd} AND SALE_DATE = #{saleDate} AND POS_NO = '01' AND BILL_NO = #{billNo}
