mapper.select:
/* SaleRegistKmuMapper.getBillNo */
SELECT  LPAD(NVL(MAX(BILL_NO), 9000) + 1, 4, '0')
FROM    TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = '99'
AND     BILL_NO > '9000'

/* SaleRegistKmuMapper.getCashAmt */
SELECT 	NVL(MAX(PAY_AMT), 0)
FROM 	TB_SL_SALE_HDR_PAY
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}
AND     PAY_CD = '01'
UNION  ALL
SELECT 	NVL(MAX(PAY_AMT), 0)
FROM 	TB_SL_SALE_HDR_PAY
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}
AND     PAY_CD = '02'

/* SaleRegistKmuMapper.getSaleFg */
SELECT 	SALE_FG
FROM 	TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = #{posNo}
AND     BILL_NO = #{billNo}

mapper.delete:
/* SaleRegistKmuMapper.delSaleHdr */
DELETE  TB_SL_SALE_HDR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = '99'
AND     BILL_NO = #{billNo}
AND     REG_ID like 'WEB_REG_%'

mapper.insert:
/* SaleRegistKmuMapper.getSalePay */ INSERT INTO TB_SL_SALE_PAY ( HQ_OFFICE_CD , HQ_BRAND_CD , STORE_CD , SALE_DATE , POS_NO , BILL_NO , PAY_CD , PAY_AMT , TAX_AMT , VAT_AMT , TIP_AMT , NO_TAX_AMT , REG_DT , REG_ID , MOD_DT , MOD_ID , REG_SEQ , SALE_YN
, RECV_AMT , RTN_AMT
, RECV_AMT , RTN_AMT
) SELECT HQ_OFFICE_CD , HQ_BRAND_CD , STORE_CD , SALE_DATE , POS_NO , BILL_NO , #{payCd} , #{payAmt} * #{saleFg} , TAX_AMT , VAT_AMT , TIP_AMT , NO_TAX_AMT , #{regDt} , #{regId} , #{modDt} , #{modId} , REG_SEQ , SALE_YN
, '0' , '0' FROM TB_SL_SALE_PAY_CARD
, RECV_AMT , RTN_AMT FROM TB_SL_SALE_PAY_CASH
FROM TB_SL_SALE_PAY_POSTPAID
WHERE STORE_CD = #{storeCd} AND SALE_DATE = #{saleDate} AND POS_NO = '99' AND BILL_NO = #{billNo}

mapper.select:
/* SaleRegistKmuMapper.getSaleRegistKmuMemberList */ SELECT tmm.MEMBR_NO, SBPENC.D(tmm.MEMBR_NM) AS MEMBR_NM FROM TB_MB_MEMBER tmm WHERE 1=1 AND tmm.MEMBR_ORGN_CD = #{membrOrgnCd}
AND tmm.MEMBR_NO LIKE '%'||#{membrNo}||'%'
AND SBPENC.D(tmm.MEMBR_NM) LIKE '%'||#{membrNm}||'%'
ORDER BY tmm.MEMBR_NO

mapper.delete:
/* SaleRegistKmuMapper.delSaleHdrMembr */
DELETE  TB_SL_SALE_HDR_MEMBR
WHERE   STORE_CD = #{storeCd}
AND     SALE_DATE = #{saleDate}
AND     POS_NO = '99'
AND     BILL_NO = #{billNo}
AND     REG_ID like 'WEB_REG_%'
